}","keccak256":"0x28e6db727fb510b0e155ac8873ebdd44b6051412e8f6742b128d6c00d739bed9"},"contracts/stablecoin/controller.sol":{"content":"pragma solidity ^0.8.4;
import "./IController.sol";
import "../turbo/TrustedMarketFactoryV3.sol";
import {MarketManager} from "./marketmanager.sol";
import {ReputationNFT} from "./reputationtoken.sol";
import {OwnedERC20} from "../turbo/OwnedShareToken.sol";
import {LinearBondingCurve} from "../bonds/LinearBondingCurve.sol";
import {BondingCurve} from "../bonds/bondingcurve.sol";
import {Vault} from "../vaults/vault.sol";
import {Instrument} from "../vaults/instrument.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import {FixedPointMathLib} from "solmate/src/utils/FixedPointMathLib.sol";

import "hardhat/console.sol";
import "@interep/contracts/IInterep.sol";

// Controller contract responsible for providing initial liquidity to the
// borrower cds market, collect winnings when default, and burn the corresponding DS
contract Controller {
    using SafeMath for uint256;
    using FixedPointMathLib for uint256;

    struct MarketData {
        address instrument_address;
        address recipient;
    }

    event MarketInitiated(uint256 marketId, address recipient);

    mapping(address => bool) public  validators; 
    mapping(address => bool) public  verified;
    mapping(uint256 => MarketData) public market_data; // id => recipient
    mapping(address=> uint256) public ad_to_id; //utilizer address to marketId, only one market ID per address at given moment, can generalize later

    address[] validators_array;

    address creator_address;

    IInterep interep;
    TrustedMarketFactoryV3 marketFactory;
    MarketManager marketManager;
    Vault public vault;
    ReputationNFT repNFT; 

    uint256 constant TWITTER_UNRATED_GROUP_ID = 16106950158033643226105886729341667676405340206102109927577753383156646348711;
    bytes32 constant private signal = bytes32("twitter-unrated");
    uint256 insurance_constant = 5e5; //1 is 1e6, also needs to be able to be changed 
    uint256 constant PRICE_PRECISION = 1e18; 
    
    // Bond Curve Name
    string constant baseName = "Bond";
    string constant baseSymbol = "B";
    uint256 nonce = 0;

    /* ========== MODIFIERS ========== */
    modifier onlyValidator() {
        require(validators[msg.sender] == true || msg.sender == creator_address, "Only Validators can call this function");
        _;
    }

    modifier onlyOwner() {
        require(msg.sender == creator_address, "Only Owner can call this function");
        _;
    }
    modifier onlyManager() {
        require(msg.sender == address(marketManager) || msg.sender == creator_address, "Only Manager can call this function");
        _;
    }

    constructor (
        address _creator_address,
        address _interep_address
    ) {
        creator_address = _creator_address;
        interep = IInterep(_interep_address);
    }

    /*----Setup Functions----*/

    function setMarketManager(address _marketManager) public onlyOwner {
        require(_marketManager != address(0));
        marketManager = MarketManager(_marketManager);
    }

    function setVault(address _vault) public onlyOwner {
        require(_vault != address(0));
        vault = Vault(_vault);
    }

    function setMarketFactory(address _marketFactory) public onlyOwner {
        require(_marketFactory != address(0));
        marketFactory = TrustedMarketFactoryV3(_marketFactory);
    }

    function setReputationNFT(address NFT_address) public onlyOwner{
        repNFT = ReputationNFT(NFT_address); 
    }


    /// @notice curveparams for linear bonds 
    /// b is a initial price parameter we choose i.e 0.9, a is a function of b
    /// a = (1-b) **2 / 2* interest 
    /// @dev both principal/interest should be in price precision
    /// @param interest is amount of interest in dollars, not percentage,
    /// returns a,b is both in 18 price_precision
    function getCurveParams(uint256 principal, uint256 interest) internal view returns (uint256, uint256){

      uint256 price_precision = 1e18; 
      uint256 interest_ = interest * (10**12); 
      uint256 principal_ = principal * (10**12); 

      uint256 b = (2*principal_).divWadDown(principal_+interest_) - price_precision; 
      uint256 a = (price_precision -b).divWadDown(principal_+interest_); 

      return (a,b);


  }


    function verifyAddress(
        uint256 nullifier_hash, 
        uint256 external_nullifier,
        uint256[8] calldata proof
    ) external  {
        require(!verified[msg.sender], "address already verified");
        interep.verifyProof(TWITTER_UNRATED_GROUP_ID, signal, nullifier_hash, external_nullifier, proof);
        verified[msg.sender] = true;
    }

    function testVerifyAddress() external {
      verified[msg.sender] = true;
    }


    function mintRepNFT(
      address NFT_address,
      address trader
      ) external  {
      ReputationNFT(NFT_address).mint(msg.sender);
  }



    //Validator should be added for each borrower
    function addValidator(address validator_address) external  {
      require(validator_address != address(0), "Zero address detected");
      require(validators[validator_address] == false, "Address already exists");

      validators[validator_address] = true; 
      validators_array.push(validator_address);
  }


    /**
     @dev initiates market, called by frontend loan proposal or instrument form submit button.
     @param recipient is the 
     @dev a and b must be 60.18 format
     */
    function initiateMarket(
        address recipient,
        Vault.InstrumentData memory instrumentData // marketId should be set to zero, no way of knowing.
    ) external  {
 
      (uint256 a, uint256 b) = getCurveParams(instrumentData.principal, instrumentData.expectedYield);
      console.log('a,b', a,b); 
      string memory name = string(abi.encodePacked(baseName, "-", Strings.toString(nonce)));
      string memory symbol = string(abi.encodePacked(baseSymbol, Strings.toString(nonce)));
      nonce++;

      OwnedERC20 zcb = new LinearBondingCurve(
          name,
          symbol,
          address(marketManager), // owner
          address(vault), 
          a,
          b
      );

      console.log("deployed ZCB address: ", address(zcb));


      uint256[] memory odds = new uint256[](2); //TODO get rid of this 
      odds[0] = 0;
      odds[1] = 0; 

      uint256 marketId = marketFactory.createZCBMarket(
          address(this), // controller is the settlement address
          instrumentData.description,
          odds,
          zcb
      );


      ad_to_id[recipient] = marketId; 
      instrumentData.marketId = marketId;


      vault.addProposal(
          instrumentData
      );

      market_data[marketId] = MarketData(address(instrumentData.Instrument_address), recipient);
      marketManager.setMarketPhase(marketId, true, true, 0, insurance_constant * instrumentData.principal / (10**6));  // need to set min rep score here as well.e

      emit MarketInitiated(marketId, recipient);
  }
 
    
   
    /**
    @notice main function called at maturity OR premature resolve of instrument(from early default)
    @dev triggered by resolve Loan 
    @param atLoss: when actual returns lower than expected 
    @param principal_loss: if total returned less than principal, principal-total returned, this is total loss

    When market finishes at maturity, need to 
    1. burn all vault tokens in bc 
    2. mint all incoming redeeming vault tokens 
    */
  function resolveMarket(
      uint256 marketId,
      bool atLoss,
      uint256 extra_gain,
      uint256 principal_loss
  ) external  {
      marketManager.update_redemption_price(marketId, atLoss, extra_gain, principal_loss); 
      marketManager.handle_maturity(marketId, atLoss, principal_loss); 
      marketManager.deactivateMarket(marketId, atLoss);
      
      //Burn all vault tokens in BC
      address bc_ad = getZCB_ad(marketId); 
      uint256 bc_vault_balance = vault.balanceOf(bc_ad); 
      vault.controller_burn(bc_vault_balance,bc_ad); 

      uint256 winning_outcome = 0; //TODO  
      marketFactory.trustedResolveMarket(marketId, winning_outcome);
    }

    /// @notice called by the validator when market conditions are met
    function approveMarket(
        uint256 marketId
    ) external onlyValidator {
      if (!marketManager.marketCondition(marketId)) revert("Market Condition Not met"); 
      
      require(!marketManager.onlyReputable(marketId), "Market Phase err"); 
      
      marketManager.approveMarket(marketId);
      marketManager.setUpperBound(marketId, 10**6); // need to get upper bound 
      
      trustInstrument(marketId); 

      // Deposit to the instrument contract
      uint256 principal = vault.fetchInstrumentData(marketId).principal; 
      //maybe this should be separated to prevent attacks 
      
      vault.depositIntoInstrument(Instrument(market_data[marketId].instrument_address), principal );
      
      vault.setMaturityDate(Instrument(market_data[marketId].instrument_address));
      
      vault.onMarketApproval(marketId);
    }

    /*
    Market is denied by validator or automatically if conditions are not met 
    */
    function denyMarket(
        uint256 marketId
    ) external  onlyValidator {
      marketManager.denyMarket(marketId);
        //TrustedMarketFactoryV3 marketFactory = TrustedMarketFactoryV3(marketInfo.marketFactoryAddress);
      
      uint256 winning_outcome = 0; //TODO  
      
      marketFactory.trustedResolveMarket(marketId, winning_outcome);
      
      vault.denyInstrument(marketId);
    }


    function trustInstrument(uint256 marketId) private  {
      vault.trustInstrument(Instrument(market_data[marketId].instrument_address));
    }

    function redeem_mint(uint256 amount, address to) external onlyManager{
      vault.controller_mint(amount,to); 
    }

    /* --------VIEW FUNCTIONS---------  */
    function getMarketId(address recipient) public view returns(uint256){
      return ad_to_id[recipient];
    }

    function getZCB(uint256 marketId) public view returns (OwnedERC20){
      AbstractMarketFactoryV3.Market memory market = marketFactory.getZCBMarket(marketId);
      return OwnedERC20(market.shareTokens[0]);
    }
    function getZCB_ad(uint256 marketId) public view returns (address){
      AbstractMarketFactoryV3.Market memory market = marketFactory.getZCBMarket(marketId);
      return address(OwnedERC20(market.shareTokens[0]));
    }
    function canBeApproved(uint256 marketId) public view returns (bool) {
        //TODO
      return true;
    }

    function isVerified(address addr) view public returns (bool) {
      return verified[addr];
    }
}

","keccak256":"0x8f1eca43b1390126394b329cd736b65104fcc8373be7c6183670e4cb0761750a"},"contracts/stablecoin/marketmanager.sol":{"content":"pragma solidity ^0.8.4;

import "./owned.sol";
import "../turbo/AMMFactory.sol"; 
import "./reputationtoken.sol"; 
import {BondingCurve} from "../bonds/bondingcurve.sol";
import {Controller} from "./controller.sol";
import "./IMarketManager.sol";
import "hardhat/console.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../prb/PRBMathUD60x18.sol";



contract MarketManager is Owned {
	/*Wrapper contract for bondingcurve markets, trades are restricted/funneled through here
		Types of restrictions are 
		1) Being verified 
		2) reputation to buy early 

		4) Restriction to quantity 

	Misc. 
		a) To avoid securitization, enforce selling Fee  
	*/
	using PRBMathUD60x18 for uint256;

    uint256 private constant PRICE_PRECISION = 1e6; 

	ReputationNFT rep;
  Controller controller;

  	mapping(uint256=>uint256) private redemption_prices; //redemption price for each market, set when market resolves 
  	mapping(uint256=>mapping(address=>uint256)) private assessment_collaterals;  //marketId-> trader->collateralIn
  	mapping(uint256=>mapping(address=>uint256)) private assessment_prices; 
  	mapping(uint256=>mapping(address=>bool)) private assessment_trader; 
	mapping(uint256=> MarketPhaseData) public restriction_data; // market ID => restriction data
	mapping(uint256=> uint256) collateral_pot; // marketID => total collateral recieved (? isn't this redundant bc bonding curves fundsperBonds)
	mapping(uint256=> CDP) private debt_pools; // marketID => debt info

	struct CDP{
		mapping(address=>address) collateral_address; 
		mapping(address=>uint256) collateral_amount;
		mapping(address=>uint256) borrowed_amount; 
		uint256 total_debt; 
		uint256 total_collateral; //only usdc 
	}

	struct MarketPhaseData {
		bool duringAssessment;
		bool onlyReputable;
		bool resolved;
		uint256 min_rep_score;
		bool alive;
		bool atLoss;
		uint256 base_budget;
	}

	uint256 private INSURANCE_CONSTANT = 5 * 10**5; // 0.5 for DS decimal format.
	uint256 private REPUTATION_CONSTANT = 3 * 10**5;
	
    modifier onlyController(){
        require(address(controller) == msg.sender || msg.sender == owner || msg.sender == address(this), "is not controller"); 
        _;
    }

	constructor(
		address _creator_address,
		address reputationNFTaddress,  
		address _controllerAddress
	) Owned(_creator_address){
		rep = ReputationNFT(reputationNFTaddress);
		controller = Controller(_controllerAddress); 
	}

	/*----Phase Functions----*/

	/*
	called on market initialization by controller
	*/
	function setMarketPhase(
		uint256 marketId, 
		bool duringAssessment,
		bool _onlyReputable,
		uint256 min_rep_score,
		uint256 base_budget
	) external  onlyController {
		MarketPhaseData storage data = restriction_data[marketId]; 
		data.onlyReputable = _onlyReputable; 
		data.duringAssessment = duringAssessment;
		data.min_rep_score = min_rep_score;
		data.base_budget = base_budget;
		data.alive = true;
	}

	/**
	 called by this when onlyRep => false.
	 */
	function setReputationPhase(
		uint256 marketId,
		bool _onlyReputable
	) internal {
		require(restriction_data[marketId].alive, "market must be alive");
		restriction_data[marketId].onlyReputable = _onlyReputable;
	}

	/**
	 called by controller
	 */
	function approveMarket(uint256 marketId) external onlyController {
		require(restriction_data[marketId].alive);
		require(restriction_data[marketId].duringAssessment);

		restriction_data[marketId].duringAssessment = false;		
	}

	/* 
	Called when market should end, a) when denied b) when maturity 
	*/
	function deactivateMarket(uint256 marketId, bool atLoss) external  onlyController{
		restriction_data[marketId].resolved = true; 
		restriction_data[marketId].atLoss = atLoss; 

	}

	/*---View Functions---*/

	/* 
	Returns Minimal reputation score to participate in the onlyReputation phase
	TODO
	*/
	function getMinRepScore(uint256 marketId) internal view returns(uint256){
		return restriction_data[marketId].min_rep_score;
	}

	/* Conditions */
 	
	/**
	 @dev verification of trader initializes reputation score at 0, to gain reputation need to participate in markets.
	 */
	function isVerified(address trader) public view returns(bool){
		return (controller.isVerified(trader) || trader == owner);
		//return (rep.balanceOf(trader) >= 1 || trader == owner); 
	}


	function isReputable(address trader, uint256 marketId) public view returns(bool){
		return (restriction_data[marketId].min_rep_score <= rep.getReputationScore(trader) || trader == owner); 
	}

	/*
	Returns true if during risk assessment phase
	*/
	function duringMarketAssessment(
		uint256 marketId) public view returns(bool){
		return restriction_data[marketId].duringAssessment; 
	}

	/*
	Returns true when market only allows reputable traders
	*/
	function onlyReputable(
		uint256 marketId
		) public view returns(bool){
		return restriction_data[marketId].onlyReputable; 

	}

	function isMarketApproved(uint256 marketId) public view returns(bool){
		return(!restriction_data[marketId].duringAssessment && restriction_data[marketId].alive); 
		
	}

	//TODO Need to find out if the given market has enough (liquidity-required liq)
	function exposureSet(address trader, address ammFactoryAddress, address marketId) internal view returns(bool){
		return true; 
	}

	function marketActive(uint256 marketId) public view returns(bool){
		return restriction_data[marketId].alive; 
	}

	/// @notice returns true if amount bought is greater than the insurance threshold
	function marketCondition(uint256 marketId) public view returns(bool){
		uint256 principal = controller.vault().fetchInstrumentData(marketId).principal;
		uint256 total_bought = BondingCurve(address(controller.getZCB(marketId))).getTotalCollateral();
		return (total_bought >= (principal * INSURANCE_CONSTANT)/PRICE_PRECISION); 
	}


	function getDebtPosition(address trader, uint256 marketId) public view returns(uint256, uint256){
		CDP storage cdp = debt_pools[marketId];
		return (cdp.collateral_amount[trader], cdp.borrowed_amount[trader]);
	}

	/// @notice get trade budget = f(reputation)
	/// sqrt for now 
	function getTraderBudget(uint256 marketId, address trader) public view returns(uint256){
		uint256 repscore = rep.getReputationScore(trader);	
		return restriction_data[marketId].base_budget; // sqrt(repscore * 10**6), since w/ decimals.
	}
 	
 	/// @notice computes the price for ZCB one needs to short at to completely
 	/// hedge for the case of maximal loss, function of principal and interest
	function getHedgePrice(uint256 marketId) public view returns(uint256){
		uint256 principal = controller.vault().fetchInstrumentData(marketId).principal; 
		uint256 yield = controller.vault().fetchInstrumentData(marketId).expectedYield; 

		uint256 den = (principal * (PRICE_PRECISION - INSURANCE_CONSTANT))/PRICE_PRECISION; 
		return PRICE_PRECISION -  (yield*PRICE_PRECISION)/den;

	}

	/// @notice computes maximum amount of quantity that trader can short while being hedged
	/// such that when he loses his loss will be offset by his gains  
	function getHedgeQuantity(address trader, uint256 marketId) public view returns(uint256){
		uint256 principal = controller.vault().fetchInstrumentData(marketId).principal; 
		uint256 holdings =  controller.vault().balanceOf(trader);
		uint256 marketCap = controller.vault().totalSupply(); 
		uint num = (principal * (PRICE_PRECISION - INSURANCE_CONSTANT)/PRICE_PRECISION) * holdings; 
		return num/marketCap; 
	}	

	
	function canBuy(
		address trader,
		uint256 amount, //this is in DS with decimals.
		uint256 marketId
	) public returns(bool, uint) {
		require(marketActive(marketId), "Market Not Active"); // this is not correct since marketDenied set to false by default.
		bool _duringMarketAssessment = duringMarketAssessment(marketId);
		bool _onlyReputable =  onlyReputable(marketId);

		if (_duringMarketAssessment){
			if (!isVerified(trader) || !(getTraderBudget(marketId, trader)>= amount)) return (false, 0); 
	
		}

  		//During the early risk assessment phase only reputable can buy 
		if (_onlyReputable){
			require(_duringMarketAssessment, "Market needs to be in assessment phase"); 
			if (!isReputable(trader, marketId)) return (false, 1); 
		}

		//If after assessment there is a set buy threshold, people can't buy above this threshold
		if (!_duringMarketAssessment){
		
			BondingCurve zcb = BondingCurve(address(controller.getZCB(marketId)));
			uint256 tokens_bought = zcb.calculatePurchaseReturn(amount);
			uint256 price_after_trade = zcb.calculateExpectedPrice(tokens_bought);
			uint256 price_upper_bound = zcb.getUpperBound();
			require(price_upper_bound > 0, "Restrictions need to be set"); 
			if (price_upper_bound > price_after_trade) return (false, 2); 
		}

		return (true, 0); 
		// require(_duringMarketAssessment, "Sells not allowed during assessments");
		// require(exposureset(trader, ammFactoryAddress, marketId), "Not enough liquidity");

	}

	function canSell(
		address trader,
		uint256 amount, 
		uint256 marketId
	) internal view returns(bool) {
		require(marketActive(marketId), "Market Not Active"); 
		bool _duringMarketAssessment = duringMarketAssessment( marketId);
		if (_duringMarketAssessment){
			require(isVerified(trader), "User Not Verified");
		}
		return true; 
	}

	/**
	 called by controller on approveMarket, preset upper bound for assessment phase is 1.
	 */
	function setUpperBound(uint256 marketId, uint256 upper_bound) external onlyController {
		BondingCurve zcb = BondingCurve(address(controller.getZCB(marketId)));
		zcb.setUpperBound(upper_bound);
	}
	

	/// @notice During assessment phase, need to log the trader's 
	/// total collateral when he bought zcb. Trader can only redeem collateral in 
	/// when market is not approved 
	/// @param priceOut is the price of the zcb after the trader made his trade
	function log_assessment_trade(
		uint256 marketId, 
		address trader, 
		uint256 amountOut, 
		uint256 collateralIn,
		uint256 priceOut)
		internal 
	{	
		assessment_trader[marketId][trader] = true; 
		assessment_collaterals[marketId][trader] = collateralIn;
		assessment_prices[marketId][trader] = priceOut; 

	}

	/* 
	After market is denied, redeem every zcb trader has for his collateral 
	*/
	function redeemPostAssessment(
		uint256 marketId, 
		address trader 
	) public {
		require(restriction_data[marketId].resolved, "Market Still During Assessment");
		uint256 collateral_amount = assessment_collaterals[marketId][trader]; 
		BondingCurve zcb = BondingCurve(address(controller.getZCB(marketId)));
		zcb.redeemPostAssessment(trader, collateral_amount); 
	}

	/// @notice denies market from validator 
	function denyMarket(
		uint256 marketId
	) external  onlyController {
		require(marketActive(marketId), "Market Not Active"); 
		require(restriction_data[marketId].duringAssessment, "Not in assessment"); 
		MarketPhaseData storage data = restriction_data[marketId]; 
		data.resolved = true; 
		data.duringAssessment = false;
	}



	function buy(
        uint256 _marketId,
        uint256 _collateralIn
    ) external  returns (uint256){
		require(!restriction_data[_marketId].resolved, "must not be resolved");
		(bool canbuy, uint256 error) = canBuy(msg.sender, _collateralIn, _marketId); 
		require(canbuy,"Trade Restricted");


		BondingCurve zcb = BondingCurve(address(controller.getZCB(_marketId))); // SOMEHOW GET ZCB
		uint256 amountOut = zcb.trustedBuy(msg.sender, _collateralIn);
 
 		//Need to log assessment trades for updating reputation scores or returning collateral
 		//when market denied 
		if (duringMarketAssessment(_marketId)){
			uint256 priceOut = zcb.calculateExpectedPrice(0); // 60.18 for some reason
			log_assessment_trade(_marketId, msg.sender, amountOut, _collateralIn, priceOut);

			//  keeps track of amount bought during reputation phase
			// and make transitions from onlyReputation true->false
			uint256 principal = controller.vault().fetchInstrumentData(_marketId).principal;
			uint256 total_bought = zcb.getTotalCollateral();
			console.log("total_bought", total_bought);

			if (onlyReputable(_marketId)){

				if (total_bought > (REPUTATION_CONSTANT * principal)/PRICE_PRECISION){
					setReputationPhase(_marketId, false); 
				}

			}

		}

		return amountOut; 
	}


	function sell(
        uint256 _marketId,
        uint256 _zcb_amount_in
    ) external  returns (uint256){
		require(!restriction_data[_marketId].resolved, "must not be resolved");
		require(canSell(msg.sender, 
		 	_zcb_amount_in, 
		 	_marketId),"Trade Restricted");

		BondingCurve zcb = BondingCurve(address(controller.getZCB(_marketId))); // SOMEHOW GET ZCB
		uint256 amountOut = zcb.trustedSell(msg.sender, _zcb_amount_in);
		return amountOut;
	}


	/* 
	For now only allow collateral to be ds
	*/
	function borrow_with_collateral(
		uint256 _marketId, 
		uint256 requested_zcb, 
		address trader
		) external {
		//1.Use 100 ds as collateral to borrow 100zcb 1:1, only this for now 
		//2.use 0.1eth(170) as collateral to borrow 100zcb =>100/170 collateral ratio
		BondingCurve zcb = BondingCurve(address(controller.getZCB(_marketId))); // SOMEHOW GET ZCB
		address collateral_address = zcb.getCollateral();
		SafeERC20.safeTransferFrom(IERC20(collateral_address), trader, address(zcb), requested_zcb);

		// SafeERC20.safeTransferFrom(IERC20(collateral_address), trader, address(this), requested_zcb); 
		uint256 _collateralIn = requested_zcb; 

		CDP storage cdp = debt_pools[_marketId];
		cdp.collateral_amount[trader] += _collateralIn; 
		cdp.borrowed_amount[trader] += requested_zcb;  
		cdp.total_debt += requested_zcb; 
		cdp.total_collateral += _collateralIn; //only ds 
		collateral_pot[_marketId] += _collateralIn; //Total ds collateral 

		zcb.trustedMint(trader, requested_zcb);
	}

	/*
	Trader provides zcb and receives back collateral 
	 */
	function repay_for_collateral(
		uint256 _marketId, 
		uint256 repaying_zcb, 
		address trader
	) external {
		BondingCurve zcb = BondingCurve(address(controller.getZCB(_marketId)));
		address collateral_address = zcb.getCollateral();
		zcb.trustedBurn(trader, repaying_zcb);

		uint256 _collateralOut = repaying_zcb; 

		CDP storage cdp = debt_pools[_marketId];
		cdp.collateral_amount[trader] -= _collateralOut;
		cdp.borrowed_amount[trader] -= repaying_zcb;
		cdp.total_debt -= repaying_zcb;
		cdp.total_collateral -= _collateralOut;
		collateral_pot[_marketId] -= _collateralOut;

		// SafeERC20.safeTransfer(IERC20(collateral_address), trader, _collateralOut);
		// zcb needs to approve transfer from itself to trader.
		zcb.trustedApproveCollateralTransfer(trader, _collateralOut);
		SafeERC20.safeTransferFrom(IERC20(collateral_address), address(zcb), trader, _collateralOut);
	}



	/*Maturity Functions */


	function get_redemption_price(uint256 marketId) public view returns(uint256){
		return redemption_prices[marketId]; 
	}

	/**
	@dev Redemption price, as calculated at maturity,
	depends on total_repayed/(principal + predetermined yield)
	If total_repayed = 0, redemption price is 0
	@param atLoss: defines circumstances where expected returns are higher than actual
	@param principal_loss: principal - returned amount => non-negative always?
	@param extra_gain: any extra yield not factored during assessment. Is 0 yield is as expected
	 */
	function update_redemption_price(
		uint256 marketId,
		bool atLoss, 
		uint256 extra_gain, 
		uint256 principal_loss
	) external  onlyController {	

		BondingCurve zcb = BondingCurve(address(controller.getZCB(marketId))); // SOMEHOW GET ZCB
		uint256 total_bought_collateral = zcb.getTotalCollateral();
		uint256 total_bought_bonds = zcb.getTotalZCB();
		console.log("total_bought_bonds", total_bought_bonds);
		console.log("principal_loss", principal_loss);

		if (atLoss){
			if (total_bought_collateral - principal_loss > 0){
				redemption_prices[marketId] = PRICE_PRECISION - (principal_loss*PRICE_PRECISION/total_bought_bonds);
			}
			else{
				redemption_prices[marketId] = 0; 
			}
		}
		else{
			require(extra_gain >= 0 && principal_loss ==0,  "loss err"); 
			uint256 num_shorts = debt_pools[marketId].total_debt; //For now assume that every zcb borrowed is used to short
			redemption_prices[marketId] = PRICE_PRECISION + (extra_gain*PRICE_PRECISION/(total_bought_bonds+num_shorts)); 
		}	

			console.log('redemption_price', redemption_prices[marketId]); 
	}


	/* 
	Handles maturity. Includes default/no defaults
	Called by controller after redemption price has been set 
	
	if default: need to burn the underlying tokens used as collateral to buy the zcb as first loss
	*/
	function handle_maturity(
		uint256 marketId, 
		bool atLoss, 
		uint256 principal_loss
	) external  onlyController {

		BondingCurve zcb =  BondingCurve(address(controller.getZCB(marketId)));

		uint256 redemption_price = get_redemption_price(marketId); 
		require(redemption_price > 0, "Need to set redemption price"); // what if redemption price is set to zero?
		uint256 total_bought_bonds = zcb.getTotalZCB();
		uint256 total_bought_collateral = zcb.getTotalCollateral();

		if (atLoss){

			uint256 burnamount = total_bought_collateral - ((redemption_price * total_bought_bonds)/PRICE_PRECISION);
			console.log('burnamounts', burnamount);

			if(principal_loss >0){
				require(burnamount>0,"burn amount err"); 
				zcb.burnFirstLoss(burnamount);

			}
		
		}

	}

	/* 
	@notice trader will redeem entire balance of ZCB
	Needs to be called at maturity, market needs to be resolved first(from controller)
	*/
	function redeem(
		uint256 marketId,
	 	address receiver 
	) public returns(uint256){
		require(!marketActive(marketId), "Market Active"); 
		BondingCurve zcb = BondingCurve(address(controller.getZCB(marketId)));
		uint256 zcb_redeem_amount = zcb.balanceOf(msg.sender); 
		zcb.trustedBurn(msg.sender, zcb_redeem_amount); 

		uint256 redemption_price = get_redemption_price(marketId); 
		require(redemption_price > 0, "Redeem price is 0");
		uint256 zcb_redeem_amount_prec = zcb_redeem_amount/(10**12); 
		uint256 collateral_redeem_amount = (redemption_price * zcb_redeem_amount_prec)/PRICE_PRECISION; 

		controller.redeem_mint(collateral_redeem_amount, msg.sender); 

		return collateral_redeem_amount; 

	}


	/// @notice when market is resolved(maturity/early default), calculates score
	/// and update each assessment phase trader's reputation, called by individual traders
	function updateReputation(uint256 marketId) external  {
		require(restriction_data[marketId].resolved, "Market not resolved"); 	
		require(assessment_trader[marketId][msg.sender], "Not manager"); 

		bool atLoss = restriction_data[marketId].atLoss; 
		uint256 priceOut = assessment_prices[marketId][msg.sender]/(10**12); 
		uint256 collateralIn = assessment_collaterals[marketId][msg.sender]; 
		uint256 traderBudget = getTraderBudget(marketId, msg.sender);
		uint256 num_bonds_bought = (collateralIn * priceOut)/PRICE_PRECISION;
		console.log("collateralIn: ", collateralIn);
		console.log("traderBudget: ", traderBudget);
		console.log("num_bonds bought: ", num_bonds_bought); 
		console.log("Price Out: ", priceOut);

		uint256 scoreToAdd; 
		// if (!atLoss) scoreToAdd = (((num_bonds_bought*PRICE_PRECISION/traderBudget) * (PRICE_PRECISION - priceOut))/PRICE_PRECISION)*(num_bonds_bought)/PRICE_PRECISION;
		// console.log('num_bonds_bought'); 
		if(!atLoss) scoreToAdd = num_bonds_bought; 
		else scoreToAdd = (num_bonds_bought/traderBudget) * priceOut * num_bonds_bought; 

		rep.addScore(msg.sender, scoreToAdd, !atLoss); 
	}


	function max(uint256 a, uint256 b) internal pure returns (uint256) {
	    return a >= b ? a : b;
	}

	function sqrt(uint y) internal pure returns (uint z) {
	    if (y > 3) {
	        z = y;
	        uint x = y / 2 + 1;
	        while (x < z) {
	            z = x;
	            x = (y / x + x) / 2;
	        }
	    } else if (y != 0) {
	        z = 1;
	    }
	}
}

","keccak256":"0xfcd337759ea64ecbd1e1c526fb2dc0f0384464d727ad309fce8fe6f658dfc9ac"},"contracts/stablecoin/owned.sol":{"content":"// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity ^0.8.4;

// https://docs.synthetix.io/contracts/Owned
contract Owned {
    address public owner;
    address public nominatedOwner;

    constructor (address _owner) public {
        require(_owner != address(0), "Owner address cannot be 0");
        owner = _owner;
        emit OwnerChanged(address(0), _owner);
    }

    function nominateNewOwner(address _owner) external onlyOwner {
        nominatedOwner = _owner;
        emit OwnerNominated(_owner);
    }

    function acceptOwnership() external {
        require(msg.sender == nominatedOwner, "You must be nominated before you can accept ownership");
        emit OwnerChanged(owner, nominatedOwner);
        owner = nominatedOwner;
        nominatedOwner = address(0);
    }

    modifier onlyOwner {
        require(msg.sender == owner, "Only the contract owner may perform this action");
        _;
    }

    event OwnerNominated(address newOwner);
    event OwnerChanged(address oldOwner, address newOwner);
}","keccak256":"0x1214743d77165d1ce7f88cf4f0383173ea9d465859c9b557e8da00523059bd9d","license":"GPL-2.0-or-later"},"contracts/stablecoin/reputationtoken.sol":{"content":"pragma solidity ^0.8.4; 
//https://github.com/poap-xyz/poap-contracts/tree/master/contracts
import {ERC721} from "solmate/src/tokens/ERC721.sol";
import {Controller} from "./controller.sol";
import {IReputationNFT} from "./IReputationNFT.sol";
import {BondingCurve} from "../bonds/bondingcurve.sol";
import "hardhat/console.sol";



contract ReputationNFT is IReputationNFT, ERC721 {
  mapping(uint256 => ReputationData) internal _reputation; // id to reputation
  mapping(address => uint256) internal _ownerToId;
  mapping(uint256 => TraderData[]) internal _marketData; // **MarketId to Market's data needed for calculating brier score.

  uint256 private nonce = 1;
  Controller controller;
  uint256 SCALE = 1e18;


  struct ReputationData {
    uint256 n; // number of markets participated in => regular uint256
    uint256 score; // averaged reputation score => 60.18
  }

  struct TraderData { // for each market
    address trader;
    uint256 tokensBought;
  }

  modifier onlyController() {
    require(msg.sender == address(controller));
    _;
  }

  constructor (
    address _controller
  ) ERC721("Debita Reputation Token", "DRT") {
    console.log("here");
    controller = Controller(_controller);
  }

  function _baseURI() internal pure returns (string memory baseURI) {
    baseURI = "";
  }

  function tokenURI(uint256 id) public view override returns (string memory) {
    require(_ownerOf[id] != address(0), "Invalid Identifier");

    string memory baseURI = _baseURI();
    return bytes(baseURI).length > 0 ? string(abi.encodePacked(baseURI, id)) : "";
  }

  function mint(address to) external {
    require(_ownerToId[to] == uint256(0), "can only mint one reputation token");
    super._mint(to, nonce);
    _ownerToId[to] = nonce;
    nonce++;
  }

  function getReputationScore(address owner) view external returns (uint256){
    require(_ownerToId[owner] != uint256(0), "No Id found");
    return _reputation[_ownerToId[owner]].score;
  }



  /**
   @notice calculates average of scores added.
   @param score: 60.18 format
   */
  function addScore(address to, uint256 score, bool atLoss) external  {
    require(_ownerToId[to] != uint256(0), "No Id found");

    ReputationData storage data = _reputation[_ownerToId[to]];
    
    if (data.n == 0) {
      data.score = score;
    } else {
      //data.score = ((data.score / data.n) + score) / (data.n + 1);
      data.score = (data.score / data.n + score) / (data.n + 1);
    }

    data.n++;
  }

  /**
   @notice reset scores
   */
  function resetScore(address to) external {
    require(_ownerToId[to] != uint256(0), "No Id found");
    delete _reputation[_ownerToId[to]];

  }
}","keccak256":"0x686e4b0d67c459c98897a6a835ce0e9004e5f75e00cf945d8176d16f54008a2d"},"contracts/turbo/AMMFactory.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "../balancer/BFactory.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "./AbstractMarketFactoryV3.sol";
import "../balancer/BNum.sol";
import "../bonds/Ibondingcurve.sol"; 

contract AMMFactory is BNum {
    using SafeMath for uint256;

    uint256 private constant MAX_UINT = 2**256 - 1;
    uint256 private constant MIN_INITIAL_LIQUIDITY = BONE * 100;

    BFactory public bFactory;
    // MarketFactory => Market => BPool
    mapping(address => mapping(uint256 => BPool)) public pools;
    uint256 fee;

    event PoolCreated(
        address pool,
        address indexed marketFactory,
        uint256 indexed marketId,
        address indexed creator,
        address lpTokenRecipient
    );
    event LiquidityChanged(
        address indexed marketFactory,
        uint256 indexed marketId,
        address indexed user,
        address recipient,
        // from the perspective of the user. e.g. collateral is negative when adding liquidity
        int256 collateral,
        int256 lpTokens,
        uint256[] sharesReturned
    );
    event SharesSwapped(
        address indexed marketFactory,
        uint256 indexed marketId,
        address indexed user,
        uint256 outcome,
        // from the perspective of the user. e.g. collateral is negative when buying
        int256 collateral,
        int256 shares,
        uint256 price
    );

    constructor(BFactory _bFactory, uint256 _fee) {
        bFactory = _bFactory;
        fee = _fee;
    }

    function createPool(
        AbstractMarketFactoryV3 _marketFactory,
        uint256 _marketId,
        uint256 _initialLiquidity,
        address _lpTokenRecipient
    ) public returns (uint256) {
        require(pools[address(_marketFactory)][_marketId] == BPool(address(0)), "Pool already created");

        AbstractMarketFactoryV3.Market memory _market = _marketFactory.getMarket(_marketId);

        uint256 _sets = _marketFactory.calcShares(_initialLiquidity);

        // Comparing to sets because sets are normalized to 10e18.
        require(_sets >= MIN_INITIAL_LIQUIDITY, "Initial liquidity must be at least 100 collateral.");

        //  Turn collateral into shares
        IERC20Full _collateral = _marketFactory.collateral();
        require(
            _collateral.allowance(msg.sender, address(this)) >= _initialLiquidity,
            "insufficient collateral allowance for initial liquidity"
        );

        _collateral.transferFrom(msg.sender, address(this), _initialLiquidity);
        _collateral.approve(address(_marketFactory), MAX_UINT);

        _marketFactory.mintShares(_marketId, _sets, address(this));

        // Create pool
        BPool _pool = bFactory.newBPool();

        // Add each outcome to the pool. Collateral is NOT added.
        for (uint256 i = 0; i < _market.shareTokens.length; i++) {
            OwnedERC20 _token = _market.shareTokens[i];
            _token.approve(address(_pool), MAX_UINT);
            _pool.bind(address(_token), _sets, _market.initialOdds[i]);
        }

        // Set the swap fee.
        _pool.setSwapFee(fee);

        // Finalize pool setup
        _pool.finalize();

        pools[address(_marketFactory)][_marketId] = _pool;

        // Pass along LP tokens for initial liquidity
        uint256 _lpTokenBalance = _pool.balanceOf(address(this)) - (BONE / 1000);

        // Burn (BONE / 1000) lp tokens to prevent the bpool from locking up. When all liquidity is removed.
        _pool.transfer(address(0x0), (BONE / 1000));
        _pool.transfer(_lpTokenRecipient, _lpTokenBalance);

        uint256[] memory _balances = new uint256[](_market.shareTokens.length);
        for (uint256 i = 0; i < _market.shareTokens.length; i++) {
            _balances[i] = 0;
        }

        emit PoolCreated(address(_pool), address(_marketFactory), _marketId, msg.sender, _lpTokenRecipient);
        emit LiquidityChanged(
            address(_marketFactory),
            _marketId,
            msg.sender,
            _lpTokenRecipient,
            -int256(_initialLiquidity),
            int256(_lpTokenBalance),
            _balances
        );

        return _lpTokenBalance;
    }

    function addLiquidity(
        AbstractMarketFactoryV3 _marketFactory,
        uint256 _marketId,
        uint256 _collateralIn,
        uint256 _minLPTokensOut,
        address _lpTokenRecipient
    ) public returns (uint256 _poolAmountOut, uint256[] memory _balances) {
        BPool _pool = pools[address(_marketFactory)][_marketId];
        require(_pool != BPool(address(0)), "Pool needs to be created");

        AbstractMarketFactoryV3.Market memory _market = _marketFactory.getMarket(_marketId);

        //  Turn collateral into shares
        IERC20Full _collateral = _marketFactory.collateral();
        _collateral.transferFrom(msg.sender, address(this), _collateralIn);
        _collateral.approve(address(_marketFactory), MAX_UINT);
        uint256 _sets = _marketFactory.calcShares(_collateralIn);
        _marketFactory.mintShares(_marketId, _sets, address(this));

        // Find poolAmountOut
        _poolAmountOut = MAX_UINT;

        {
            uint256 _totalSupply = _pool.totalSupply();
            uint256[] memory _maxAmountsIn = new uint256[](_market.shareTokens.length);
            for (uint256 i = 0; i < _market.shareTokens.length; i++) {
                _maxAmountsIn[i] = _sets;

                OwnedERC20 _token = _market.shareTokens[i];
                uint256 _bPoolTokenBalance = _pool.getBalance(address(_token));

                // This is the result the following when solving for poolAmountOut:
                // uint256 ratio = bdiv(poolAmountOut, poolTotal);
                // uint256 tokenAmountIn = bmul(ratio, bal);
                uint256 _tokenPoolAmountOut =
                    (((((_sets * BONE) - (BONE / 2)) * _totalSupply) / _bPoolTokenBalance) - (_totalSupply / 2)) / BONE;

                if (_tokenPoolAmountOut < _poolAmountOut) {
                    _poolAmountOut = _tokenPoolAmountOut;
                }
            }
            _pool.joinPool(_poolAmountOut, _maxAmountsIn);
        }

        require(_poolAmountOut >= _minLPTokensOut, "Would not have received enough LP tokens");

        _pool.transfer(_lpTokenRecipient, _poolAmountOut);

        // Transfer the remaining shares back to _lpTokenRecipient.
        _balances = new uint256[](_market.shareTokens.length);
        for (uint256 i = 0; i < _market.shareTokens.length; i++) {
            OwnedERC20 _token = _market.shareTokens[i];
            _balances[i] = _token.balanceOf(address(this));
            if (_balances[i] > 0) {
                _token.transfer(_lpTokenRecipient, _balances[i]);
            }
        }

        emit LiquidityChanged(
            address(_marketFactory),
            _marketId,
            msg.sender,
            _lpTokenRecipient,
            -int256(_collateralIn),
            int256(_poolAmountOut),
            _balances
        );
    }

    function removeLiquidity(
        AbstractMarketFactoryV3 _marketFactory,
        uint256 _marketId,
        uint256 _lpTokensIn,
        uint256 _minCollateralOut,
        address _collateralRecipient
    ) public returns (uint256 _collateralOut, uint256[] memory _balances) {
        BPool _pool = pools[address(_marketFactory)][_marketId];
        require(_pool != BPool(address(0)), "Pool needs to be created");

        AbstractMarketFactoryV3.Market memory _market = _marketFactory.getMarket(_marketId);

        _pool.transferFrom(msg.sender, address(this), _lpTokensIn);

        uint256[] memory exitPoolEstimate;
        {
            uint256[] memory minAmountsOut = new uint256[](_market.shareTokens.length);
            exitPoolEstimate = _pool.calcExitPool(_lpTokensIn, minAmountsOut);
            _pool.exitPool(_lpTokensIn, minAmountsOut);
        }

        // Find the number of sets to sell.
        uint256 _setsToSell = MAX_UINT;
        for (uint256 i = 0; i < _market.shareTokens.length; i++) {
            uint256 _acquiredTokenBalance = exitPoolEstimate[i];
            if (_acquiredTokenBalance < _setsToSell) _setsToSell = _acquiredTokenBalance;
        }

        // Must be a multiple of share factor.
        _setsToSell = (_setsToSell / _marketFactory.shareFactor()) * _marketFactory.shareFactor();

        bool _resolved = _marketFactory.isMarketResolved(_marketId);
        if (_resolved) {
            _collateralOut = _marketFactory.claimWinnings(_marketId, _collateralRecipient);
        } else {
            _collateralOut = _marketFactory.burnShares(_marketId, _setsToSell, _collateralRecipient);
        }
        require(_collateralOut > _minCollateralOut, "Amount of collateral returned too low.");

        // Transfer the remaining shares back to _collateralRecipient.
        _balances = new uint256[](_market.shareTokens.length);
        for (uint256 i = 0; i < _market.shareTokens.length; i++) {
            OwnedERC20 _token = _market.shareTokens[i];
            if (_resolved && _token == _market.winner) continue; // all winning shares claimed when market is resolved
            _balances[i] = exitPoolEstimate[i] - _setsToSell;
            if (_balances[i] > 0) {
                _token.transfer(_collateralRecipient, _balances[i]);
            }
        }

        emit LiquidityChanged(
            address(_marketFactory),
            _marketId,
            msg.sender,
            _collateralRecipient,
            int256(_collateralOut),
            -int256(_lpTokensIn),
            _balances
        );
    }


    function buyZCB(
        AbstractMarketFactoryV3 _marketFactory, 
        address from, 
        address bondingcurve, 
        uint256 _marketId, 
        uint256 _collateralIn
        ) external returns(uint256){


        IERC20Full _collateral = _marketFactory.collateral();
        _collateral.transferFrom(from, address(this), _collateralIn);
        _collateral.approve(bondingcurve, _collateralIn); 

        return IBondingCurve(bondingcurve).buy(address(_marketFactory), from, _collateralIn, _marketId);
    }

    function sellZCB(
        AbstractMarketFactoryV3 _marketFactory, 
        address from, 
        address bondingcurve, 
        uint256 _marketId, 
        uint256 _zcb_amountIn
        ) external returns(uint256){

        uint256 fee_deducted_collateral_out = IBondingCurve(bondingcurve).sell(
            address(_marketFactory),
            from, 
            _zcb_amountIn, 
             _marketId); 

        IERC20Full _collateral = _marketFactory.collateral();
        _collateral.transfer(from, fee_deducted_collateral_out); 

        return fee_deducted_collateral_out; 
    }


    function buy(
        AbstractMarketFactoryV3 _marketFactory,
        uint256 _marketId,
        uint256 _outcome,
        uint256 _collateralIn,
        uint256 _minTokensOut
    ) external returns (uint256) {
        BPool _pool = pools[address(_marketFactory)][_marketId];
        require(_pool != BPool(address(0)), "Pool needs to be created");

        AbstractMarketFactoryV3.Market memory _market = _marketFactory.getMarket(_marketId);
        _marketFactory.logTrade(_marketId, _outcome, _collateralIn); 

        IERC20Full _collateral = _marketFactory.collateral();
        _collateral.transferFrom(msg.sender, address(this), _collateralIn);
        uint256 _sets = _marketFactory.calcShares(_collateralIn);
        _marketFactory.mintShares(_marketId, _sets, address(this));

        uint256 _totalDesiredOutcome = _sets;
        {
            OwnedERC20 _desiredToken = _market.shareTokens[_outcome];

            for (uint256 i = 0; i < _market.shareTokens.length; i++) {
                if (i == _outcome) continue;
                OwnedERC20 _token = _market.shareTokens[i];
                (uint256 _acquiredToken, ) =
                    _pool.swapExactAmountIn(address(_token), _sets, address(_desiredToken), 0, MAX_UINT);
                _totalDesiredOutcome += _acquiredToken;
            }
            require(_totalDesiredOutcome >= _minTokensOut, "Slippage exceeded");

            _desiredToken.transfer(msg.sender, _totalDesiredOutcome);
        }

        emit SharesSwapped(
            address(_marketFactory),
            _marketId,
            msg.sender,
            _outcome,
            -int256(_collateralIn),
            int256(_totalDesiredOutcome),
            bdiv(_sets, _totalDesiredOutcome)
        );

        return _totalDesiredOutcome;
    }

    function sellForCollateral(
        AbstractMarketFactoryV3 _marketFactory,
        uint256 _marketId,
        uint256 _outcome,
        uint256[] memory _shareTokensIn,
        uint256 _minSetsOut
    ) external returns (uint256) {
        BPool _pool = pools[address(_marketFactory)][_marketId];
        require(_pool != BPool(address(0)), "Pool needs to be created");
        AbstractMarketFactoryV3.Market memory _market = _marketFactory.getMarket(_marketId);

        uint256 _setsOut = MAX_UINT;
        uint256 _totalUndesiredTokensIn = 0;
        for (uint256 i = 0; i < _market.shareTokens.length; i++) {
            _totalUndesiredTokensIn += _shareTokensIn[i];
        }

        {
            _market.shareTokens[_outcome].transferFrom(msg.sender, address(this), _totalUndesiredTokensIn);
            _market.shareTokens[_outcome].approve(address(_pool), MAX_UINT);

            for (uint256 i = 0; i < _market.shareTokens.length; i++) {
                if (i == _outcome) continue;
                OwnedERC20 _token = _market.shareTokens[i];
                (uint256 tokenAmountOut, ) =
                    _pool.swapExactAmountIn(
                        address(_market.shareTokens[_outcome]),
                        _shareTokensIn[i],
                        address(_token),
                        0,
                        MAX_UINT
                    );

                //Ensure tokenAmountOut is a multiple of shareFactor.
                tokenAmountOut = (tokenAmountOut / _marketFactory.shareFactor()) * _marketFactory.shareFactor();
                if (tokenAmountOut < _setsOut) _setsOut = tokenAmountOut;
            }

            require(_setsOut >= _minSetsOut, "Minimum sets not available.");
            _marketFactory.burnShares(_marketId, _setsOut, msg.sender);
        }

        // Transfer undesired token balance back.
        for (uint256 i = 0; i < _market.shareTokens.length; i++) {
            OwnedERC20 _token = _market.shareTokens[i];
            uint256 _balance = _token.balanceOf(address(this));
            if (_balance > 0) {
                _token.transfer(msg.sender, _balance);
            }
        }

        uint256 _collateralOut = _marketFactory.calcCost(_setsOut);
        emit SharesSwapped(
            address(_marketFactory),
            _marketId,
            msg.sender,
            _outcome,
            int256(_collateralOut),
            -int256(_totalUndesiredTokensIn),
            bdiv(_setsOut, _totalUndesiredTokensIn)
        );

        return _collateralOut;
    }

    // Returns an array of token values for the outcomes of the market, relative to the first outcome.
    // So the first outcome is 10**18 and all others are higher or lower.
    // Prices can be derived due to the fact that the total of all outcome shares equals one collateral, possibly with a scaling factor,
    function tokenRatios(AbstractMarketFactoryV3 _marketFactory, uint256 _marketId)
        external
        view
        returns (uint256[] memory)
    {
        BPool _pool = pools[address(_marketFactory)][_marketId];
        // Pool does not exist. Do not want to revert because multicall.
        if (_pool == BPool(address(0))) {
            return new uint256[](0);
        }

        AbstractMarketFactoryV3.Market memory _market = _marketFactory.getMarket(_marketId);
        address _basisToken = address(_market.shareTokens[0]);
        uint256[] memory _ratios = new uint256[](_market.shareTokens.length);
        _ratios[0] = 10**18;
        for (uint256 i = 1; i < _market.shareTokens.length; i++) {
            uint256 _price = _pool.getSpotPrice(_basisToken, address(_market.shareTokens[i]));
            _ratios[i] = _price;
        }
        return _ratios;
    }

    function getPoolBalances(AbstractMarketFactoryV3 _marketFactory, uint256 _marketId)
        external
        view
        returns (uint256[] memory)
    {
        BPool _pool = pools[address(_marketFactory)][_marketId];
        // Pool does not exist. Do not want to revert because multicall.
        if (_pool == BPool(address(0))) {
            return new uint256[](0);
        }

        address[] memory _tokens = _pool.getCurrentTokens();
        uint256[] memory _balances = new uint256[](_tokens.length);
        for (uint256 i = 0; i < _tokens.length; i++) {
            _balances[i] = _pool.getBalance(_tokens[i]);
        }
        return _balances;
    }

    function getPoolWeights(AbstractMarketFactoryV3 _marketFactory, uint256 _marketId)
        external
        view
        returns (uint256[] memory)
    {
        BPool _pool = pools[address(_marketFactory)][_marketId];
        // Pool does not exist. Do not want to revert because multicall.
        if (_pool == BPool(address(0))) {
            return new uint256[](0);
        }

        address[] memory _tokens = _pool.getCurrentTokens();
        uint256[] memory _weights = new uint256[](_tokens.length);
        for (uint256 i = 0; i < _tokens.length; i++) {
            _weights[i] = _pool.getDenormalizedWeight(_tokens[i]);
        }
        return _weights;
    }

    function getSwapFee(AbstractMarketFactoryV3 _marketFactory, uint256 _marketId) external view returns (uint256) {
        BPool _pool = pools[address(_marketFactory)][_marketId];
        return _pool.getSwapFee();
    }

    function getPoolTokenBalance(
        AbstractMarketFactoryV3 _marketFactory,
        uint256 _marketId,
        address _user
    ) external view returns (uint256) {
        BPool _pool = pools[address(_marketFactory)][_marketId];
        return _pool.balanceOf(_user);
    }

    function getPool(AbstractMarketFactoryV3 _marketFactory, uint256 _marketId) external view returns (BPool) {
        return pools[address(_marketFactory)][_marketId];
    }
}
","keccak256":"0xc07d87d08f41ce5e5feae47a5ee1948e424e1860c2270a8bf33dab0590725568","license":"MIT"},"contracts/turbo/AbstractMarketFactoryV3.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "../libraries/IERC20Full.sol";
import "../balancer/BPool.sol";
import "./TurboShareTokenFactory.sol";
import "./FeePot.sol";
import "../libraries/Rewardable.sol";
import { LinearBondingCurve } from "../bonds/LinearBondingCurve.sol";

abstract contract AbstractMarketFactoryV3 is ZCBFactory, TurboShareTokenFactory, Ownable, Rewardable {
    using SafeMath for uint256;

    event MarketCreated(uint256 id, string[] names, uint256[] initialOdds);
    event MarketResolved(uint256 id, address winner, uint256 winnerIndex, string winnerName);
    event MarketActivated(uint256 id);

    event SharesMinted(uint256 id, uint256 amount, address receiver);
    event SharesBurned(uint256 id, uint256 amount, address receiver);
    event WinningsClaimed(
        uint256 id,
        address winningOutcome,
        uint256 winningIndex,
        string winningName,
        uint256 amount,
        uint256 settlementFee,
        uint256 payout,
        address indexed receiver
    );

    IERC20Full public collateral;
    FeePot public feePot;

    // fees are out of 1e18 and only apply to new markets
    uint256 public stakerFee;
    uint256 public settlementFee;
    uint256 public protocolFee;

    address public protocol; // collects protocol fees

    uint256 public accumulatedProtocolFee = 0;
    // settlement address => amount of collateral
    mapping(address => uint256) public accumulatedSettlementFees;

    // How many shares equals one collateral.
    // Necessary to account for math errors from small numbers in balancer.
    // shares = collateral / shareFactor
    // collateral = shares * shareFactor
    uint256 public shareFactor;

    struct Market {
        address settlementAddress;
        OwnedERC20[] shareTokens;
        OwnedERC20 winner;
        uint256 winnerIndex;
        uint256 settlementFee;
        uint256 protocolFee;
        uint256 stakerFee;
        uint256 creationTimestamp;
        uint256 resolutionTimestamp; // when winner is declared
        uint256[] initialOdds;
        bool active; // false if not ready to use or if resolved
    }

    Market[] internal markets;
    // ZCBMarket[] internal zcbmarkets; 

    uint256 private constant MAX_UINT = 2**256 - 1;

    mapping(uint256=> mapping(uint256=>uint256)) TradeDetails; //marketid -> (outcome->amount)
    mapping(uint256 => bool ) inAssessment; 
    mapping(uint256=> uint256) buy_thresholds;


    constructor(
        address _owner,
        IERC20Full _collateral,
        uint256 _shareFactor,
        FeePot _feePot,
        uint256[3] memory _fees, // staker, settlement, protocol
        address _protocol
    ) {
        owner = _owner; // controls fees for new markets
        collateral = _collateral;
        shareFactor = _shareFactor;
        feePot = _feePot;
        stakerFee = _fees[0];
        settlementFee = _fees[1];
        protocolFee = _fees[2];
        protocol = _protocol;

        _collateral.approve(address(_feePot), MAX_UINT);

        // First market is always empty so that marketid zero means "no market"
        markets.push(makeEmptyMarket());

        //FOR TESTING ONLY
        buy_thresholds[0] = MAX_UINT;
        buy_thresholds[1] = MAX_UINT; 
        buy_thresholds[2] = MAX_UINT; 
    }
        

   // function quantityAvailable(uint256 marketId)

    //Called by lendingpool when market is created i.e , and when loan is approved 
    function handleAssessment(uint256 _marketId, bool startAssessing) 
    external
    //onlyController
     {
        bool market_inAssessment = startAssessing? true : false; 
        inAssessment[_marketId] = market_inAssessment; 
    }
    function handleOnlyReputable(uint256 _marketId)
    external 
    //onlyController
    {

    }
    //Called by controller after assessment phase 
    function set_buy_threshold(uint256 _marketId, uint256 threshold)
    external 
    //onlyController
    {
        buy_thresholds[_marketId] = threshold;
    }

    function get_buy_threshold(uint256 _marketId) external view returns(uint256){
        return buy_thresholds[_marketId]; 
    }
    function isInAssessment(uint256 _marketId) external view returns(bool){
        return inAssessment[_marketId]; 
    }

    function onlyReputable(uint256 _marketId) external view returns(bool){
        return false;
    }
    function logTrade(uint256 _marketId, uint256 _outcome, uint256 _collateralIn) external {
        TradeDetails[_marketId][_outcome] = TradeDetails[_marketId][_outcome] + _collateralIn; 

    }

    function getTradeDetails(uint256 _marketId, uint256 _outcome) external view returns(uint256){
        return TradeDetails[_marketId][_outcome]; 
    }




    // Returns an empty struct if the market doesn't exist.
    // Can check market existence before calling this by comparing _id against markets.length.
    // Can check market existence of the return struct by checking that shareTokens[0] isn't the null address
    function getMarket(uint256 _id) public view returns (Market memory) {
        if (_id >= markets.length) {
            return makeEmptyMarket();
        } else {
            return markets[_id];
        }
    }

    function marketCount() public view returns (uint256) {
        return markets.length; //+ zcbmarkets.length;
    }

    // Returns factory-specific details about a market.
    // function getMarketDetails(uint256 _id) public view returns (MarketDetails memory);

    function mintShares(
        uint256 _id,
        uint256 _shareToMint,
        address _receiver
    ) public {
        require(markets.length > _id);
        require(markets[_id].active);

        uint256 _cost = calcCost(_shareToMint);
        collateral.transferFrom(msg.sender, address(this), _cost);

        Market memory _market = markets[_id];
        for (uint256 _i = 0; _i < _market.shareTokens.length; _i++) {
            _market.shareTokens[_i].trustedMint(_receiver, _shareToMint);
        }

        emit SharesMinted(_id, _shareToMint, _receiver);
    }

    function burnShares(
        uint256 _id,
        uint256 _sharesToBurn,
        address _receiver
    ) public returns (uint256) {
        require(markets.length > _id);
        require(markets[_id].active);

        Market memory _market = markets[_id];
        for (uint256 _i = 0; _i < _market.shareTokens.length; _i++) {
            // errors if sender doesn't have enough shares
            _market.shareTokens[_i].trustedBurn(msg.sender, _sharesToBurn);
        }

        uint256 _payout = calcCost(_sharesToBurn);
        uint256 _protocolFee = _payout.mul(_market.protocolFee).div(10**18);
        uint256 _stakerFee = _payout.mul(_market.stakerFee).div(10**18);
        _payout = _payout.sub(_protocolFee).sub(_stakerFee);

        accumulatedProtocolFee += _protocolFee;
        collateral.transfer(_receiver, _payout);
        feePot.depositFees(_stakerFee);

        emit SharesBurned(_id, _sharesToBurn, msg.sender);
        return _payout;
    }

    function claimWinnings(uint256 _id, address _receiver) public returns (uint256) {
        require(isMarketResolved(_id), "market unresolved");

        Market memory _market = markets[_id];
        uint256 _winningShares = _market.winner.trustedBurnAll(msg.sender);
        _winningShares = (_winningShares / shareFactor) * shareFactor; // remove unusable dust

        uint256 _payout = calcCost(_winningShares); // will fail if there are no winnings to claim
        uint256 _settlementFee = _payout.mul(_market.settlementFee).div(10**18);
        _payout = _payout.sub(_settlementFee);

        accumulatedSettlementFees[_market.settlementAddress] += _settlementFee;
        collateral.transfer(_receiver, _payout);

        uint256 _winningIndex = _market.winnerIndex;
        string memory _winningName = _market.winner.name();

        emit WinningsClaimed(
            _id,
            address(_market.winner),
            _winningIndex,
            _winningName,
            _winningShares,
            _settlementFee,
            _payout,
            _receiver
        );
        return _payout;
    }

    function claimManyWinnings(uint256[] memory _ids, address _receiver) public returns (uint256) {
        uint256 _totalWinnings = 0;
        for (uint256 i = 0; i < _ids.length; i++) {
            _totalWinnings = _totalWinnings.add(claimWinnings(_ids[i], _receiver));
        }
        return _totalWinnings;
    }

    function claimSettlementFees(address _receiver) public returns (uint256) {
        uint256 _fees = accumulatedSettlementFees[msg.sender];
        if (_fees > 0) {
            accumulatedSettlementFees[msg.sender] = 0;
            collateral.transfer(_receiver, _fees);
        }
        return _fees;
    }

    function claimProtocolFees() public returns (uint256) {
        require(msg.sender == protocol || msg.sender == address(this));
        uint256 _fees = accumulatedProtocolFee;
        if (_fees > 0) {
            accumulatedProtocolFee = 0;
            collateral.transfer(protocol, _fees);
        }
        return _fees;
    }

    function setSettlementFee(uint256 _newFee) external onlyOwner {
        settlementFee = _newFee;
    }

    function setStakerFee(uint256 _newFee) external onlyOwner {
        stakerFee = _newFee;
    }

    function setProtocolFee(uint256 _newFee) external onlyOwner {
        protocolFee = _newFee;
    }

    function setProtocol(address _newProtocol, bool _claimFirst) external onlyOwner {
        if (_claimFirst) {
            claimProtocolFees();
        }
        protocol = _newProtocol;
    }


    function startMarket(
        address _settlementAddress,
        string[] memory _names,
        uint256[] memory _initialOdds,
        bool _active
    ) internal returns (uint256 _marketId) {
        _marketId = markets.length;
        markets.push(
            Market(
                _settlementAddress,
                createShareTokens(_names, address(this)),
                OwnedERC20(address(0)),
                0,
                settlementFee,
                protocolFee,
                stakerFee,
                block.timestamp,
                0,
                _initialOdds,
                _active
            )
        );
        emit MarketCreated(_marketId, _names, _initialOdds);
        if (_active) {
            emit MarketActivated(_marketId);
        }
    }
       
    
    function activateMarket(uint256 _marketId) internal {
        markets[_marketId].active = true;
        emit MarketActivated(_marketId);
    }

    function makeEmptyMarket() private pure returns (Market memory) {
        OwnedERC20[] memory _tokens = new OwnedERC20[](0);
        uint256[] memory _initialOdds = new uint256[](0);
        return Market(address(0), _tokens, OwnedERC20(address(0)), 0, 0, 0, 0, 0, 0, _initialOdds, false);
    }


    function endMarket(uint256 _marketId, uint256 _winningOutcome) internal {
        Market storage _market = markets[_marketId];
        OwnedERC20 _winner = _market.shareTokens[_winningOutcome];

        _market.winner = _winner;
        _market.active = false;
        _market.winnerIndex = _winningOutcome;
        _market.resolutionTimestamp = block.timestamp;
        string memory _outcomeName = _winner.name();
        emit MarketResolved(_marketId, address(_winner), _winningOutcome, _outcomeName);
    }

    function isMarketResolved(uint256 _id) public view returns (bool) {
        Market memory _market = markets[_id];
        return _market.winner != OwnedERC20(address(0));
    }

    // shares => collateral
    // Shares must be both greater than (or equal to) and divisible by shareFactor.
    function calcCost(uint256 _shares) public view returns (uint256) {
        require(_shares >= shareFactor && _shares % shareFactor == 0);
        return _shares / shareFactor;
    }

    // collateral => shares
    function calcShares(uint256 _collateralIn) public view returns (uint256) {
        return _collateralIn * shareFactor;
    }

    function onTransferOwnership(address, address) internal override {}

    /**
     @notice 
     */
    function startZCBMarket(
        address _settlementAddress,
        uint256[] memory _initialOdds,
        bool _active,
        OwnedERC20 _zcb
    ) internal returns (uint256 _marketId){

        _marketId = markets.length;
        OwnedERC20[] memory zcb = new OwnedERC20[](2);
        zcb[0] = _zcb;
        markets.push(
            Market(
                _settlementAddress,
                zcb,
                OwnedERC20(address(0)),
                0,
                settlementFee,
                protocolFee,
                stakerFee,
                block.timestamp,
                0,
                _initialOdds,
                _active
                )
            );

        if (_active) {
            emit MarketActivated(_marketId);
        }

    }

    function getZCBMarket(uint256 _id) public view returns (Market memory) {
        if (_id >= markets.length) {
           revert("Market Not Activated");
        } else {
            return markets[_id];
        }
    }
}
