import {ERC20} from "../tokens/ERC20.sol";

/// @notice Safe ETH and ERC20 transfer library that gracefully handles missing return values.
/// @author Solmate (https://github.com/transmissions11/solmate/blob/main/src/utils/SafeTransferLib.sol)
/// @dev Use with caution! Some functions in this library knowingly create dirty bits at the destination of the free memory pointer.
/// @dev Note that none of the functions in this library check that a token has code at all! That responsibility is delegated to the caller.
library SafeTransferLib {
    /*//////////////////////////////////////////////////////////////
                             ETH OPERATIONS
    //////////////////////////////////////////////////////////////*/

    function safeTransferETH(address to, uint256 amount) internal {
        bool success;

        assembly {
            // Transfer the ETH and store if it succeeded or not.
            success := call(gas(), to, amount, 0, 0, 0, 0)
        }

        require(success, "ETH_TRANSFER_FAILED");
    }

    /*//////////////////////////////////////////////////////////////
                            ERC20 OPERATIONS
    //////////////////////////////////////////////////////////////*/

    function safeTransferFrom(
        ERC20 token,
        address from,
        address to,
        uint256 amount
    ) internal {
        bool success;

        assembly {
            // Get a pointer to some free memory.
            let freeMemoryPointer := mload(0x40)

            // Write the abi-encoded calldata into memory, beginning with the function selector.
            mstore(freeMemoryPointer, 0x23b872dd00000000000000000000000000000000000000000000000000000000)
            mstore(add(freeMemoryPointer, 4), from) // Append the "from" argument.
            mstore(add(freeMemoryPointer, 36), to) // Append the "to" argument.
            mstore(add(freeMemoryPointer, 68), amount) // Append the "amount" argument.

            success := and(
                // Set success to whether the call reverted, if not we check it either
                // returned exactly 1 (can't just be non-zero data), or had no return data.
                or(and(eq(mload(0), 1), gt(returndatasize(), 31)), iszero(returndatasize())),
                // We use 100 because the length of our calldata totals up like so: 4 + 32 * 3.
                // We use 0 and 32 to copy up to 32 bytes of return data into the scratch space.
                // Counterintuitively, this call must be positioned second to the or() call in the
                // surrounding and() call or else returndatasize() will be zero during the computation.
                call(gas(), token, 0, freeMemoryPointer, 100, 0, 32)
            )
        }

        require(success, "TRANSFER_FROM_FAILED");
    }

    function safeTransfer(
        ERC20 token,
        address to,
        uint256 amount
    ) internal {
        bool success;

        assembly {
            // Get a pointer to some free memory.
            let freeMemoryPointer := mload(0x40)

            // Write the abi-encoded calldata into memory, beginning with the function selector.
            mstore(freeMemoryPointer, 0xa9059cbb00000000000000000000000000000000000000000000000000000000)
            mstore(add(freeMemoryPointer, 4), to) // Append the "to" argument.
            mstore(add(freeMemoryPointer, 36), amount) // Append the "amount" argument.

            success := and(
                // Set success to whether the call reverted, if not we check it either
                // returned exactly 1 (can't just be non-zero data), or had no return data.
                or(and(eq(mload(0), 1), gt(returndatasize(), 31)), iszero(returndatasize())),
                // We use 68 because the length of our calldata totals up like so: 4 + 32 * 2.
                // We use 0 and 32 to copy up to 32 bytes of return data into the scratch space.
                // Counterintuitively, this call must be positioned second to the or() call in the
                // surrounding and() call or else returndatasize() will be zero during the computation.
                call(gas(), token, 0, freeMemoryPointer, 68, 0, 32)
            )
        }

        require(success, "TRANSFER_FAILED");
    }

    function safeApprove(
        ERC20 token,
        address to,
        uint256 amount
    ) internal {
        bool success;

        assembly {
            // Get a pointer to some free memory.
            let freeMemoryPointer := mload(0x40)

            // Write the abi-encoded calldata into memory, beginning with the function selector.
            mstore(freeMemoryPointer, 0x095ea7b300000000000000000000000000000000000000000000000000000000)
            mstore(add(freeMemoryPointer, 4), to) // Append the "to" argument.
            mstore(add(freeMemoryPointer, 36), amount) // Append the "amount" argument.

            success := and(
                // Set success to whether the call reverted, if not we check it either
                // returned exactly 1 (can't just be non-zero data), or had no return data.
                or(and(eq(mload(0), 1), gt(returndatasize(), 31)), iszero(returndatasize())),
                // We use 68 because the length of our calldata totals up like so: 4 + 32 * 2.
                // We use 0 and 32 to copy up to 32 bytes of return data into the scratch space.
                // Counterintuitively, this call must be positioned second to the or() call in the
                // surrounding and() call or else returndatasize() will be zero during the computation.
                call(gas(), token, 0, freeMemoryPointer, 68, 0, 32)
            )
        }

        require(success, "APPROVE_FAILED");
    }
}
","keccak256":"0x7c0075dca9374db749ed3fc781fd901694cf987812e3ef7c464d7df63de3e03a","license":"AGPL-3.0-only"},"contracts/vaults/vault.sol":{"content":"pragma solidity ^0.8.4;

import {Auth} from "./auth/Auth.sol";
import {ERC4626} from "./mixins/ERC4626.sol";

import {SafeCastLib} from "./utils/SafeCastLib.sol";
import {SafeTransferLib} from "./utils/SafeTransferLib.sol";
import {FixedPointMathLib} from "./utils/FixedPointMathLib.sol";

import {ERC20} from "./tokens/ERC20.sol";
import {Instrument} from "./instrument.sol";
import {Controller} from "../stablecoin/controller.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";


contract Vault is ERC4626, Auth{
	using SafeCastLib for uint256; 
    using SafeTransferLib for ERC20;
    using FixedPointMathLib for uint256;


    event InstrumentDeposit(address indexed user, Instrument indexed instrument, uint256 underlyingAmount);
    event InstrumentWithdrawal(address indexed user, Instrument indexed instrument, uint256 underlyingAmount);
    event InstrumentTrusted(address indexed user, Instrument indexed instrument);
    event InstrumentDistrusted(address indexed user, Instrument indexed instrument);
    event InstrumentHarvest(address indexed instrument, uint256 instrument_balance, uint256 mag, bool sign); //sign is direction of mag, + or -.

    /*///////////////////////////////////////////////////////////////
                                 CONSTANTS
    //////////////////////////////////////////////////////////////*/

    uint256 internal BASE_UNIT;
    uint256 totalInstrumentHoldings; //total holdings deposited into all Instruments collateral
    ERC20 public immutable UNDERLYING;
    Controller private controller;

    mapping(Instrument => InstrumentData) public getInstrumentData;
    mapping(address => uint256) public  num_proposals;
    mapping(uint256=> Instrument) Instruments; //marketID-> Instrument

    enum InstrumentType {
        CreditLine,
        Other
    }

    /// @param trusted Whether the Instrument is trusted.
    /// @param balance The amount of underlying tokens held in the Instrument.
    struct InstrumentData {
        // Used to determine if the Vault will operate on a Instrument.
        bool trusted;
        // Balance of the contract denominated in Underlying, 
        // used to determine profit and loss during harvests of the Instrument.  
        // represents the amount of debt the Instrument has incurred from this vault   
        uint248 balance; // in underlying
        uint256 faceValue; // in underlying
        uint256 marketId;
    	uint256 principal; //this is total available allowance in underlying
        uint256 expectedYield; // total interest paid over duration in underlying
        uint256 duration;
        string description;
        address Instrument_address;
        InstrumentType instrument_type;
        uint256 maturityDate;
    }

    constructor(
        address _UNDERLYING,
        address _controller
    )
        ERC4626(
            ERC20(_UNDERLYING),
            string(abi.encodePacked("debita ", ERC20(_UNDERLYING).name(), " Vault")),
            string(abi.encodePacked("db", ERC20(_UNDERLYING).symbol()))
        )  Auth(msg.sender)

    {
        UNDERLYING = ERC20(_UNDERLYING);
        BASE_UNIT = 10**ERC20(_UNDERLYING).decimals();
        controller = Controller(_controller);

        //totalSupply = type(uint256).max;
    }
    
    modifier onlyController(){
        require(address(controller) == msg.sender || msg.sender == owner ,  "is not controller"); 
        _;
    }

    /// @notice called by controller at maturity 
    function controller_burn(uint256 amount, address bc_address) external onlyController {
        _burn(bc_address,amount); 
    }
    /// @notice called by controller at maturity, since redeem amount > balance in bc
    function controller_mint(uint256 amount, address to) external onlyController {
        _mint(to , amount); 
    }



    /// @notice Harvest a trusted Instrument, records profit/loss 
    /**
     public bc anyone can call => records
     */
    function harvest(address instrument) public {
        require(getInstrumentData[Instrument(instrument)].trusted, "UNTRUSTED_Instrument");
    	
        uint256 oldTotalInstrumentHoldings = totalInstrumentHoldings; 
        
        uint256 balanceLastHarvest = getInstrumentData[Instrument(instrument)].balance;
        
        uint256 balanceThisHarvest = Instrument(instrument).balanceOfUnderlying(address(instrument));
        
        if (balanceLastHarvest == balanceThisHarvest) {
            return;
        }
        
        getInstrumentData[Instrument(instrument)].balance = balanceThisHarvest.safeCastTo248();

        uint256 delta;
       
        bool net_positive = balanceThisHarvest >= balanceLastHarvest;
        
        delta = net_positive ? balanceThisHarvest - balanceLastHarvest : balanceLastHarvest - balanceThisHarvest;

        totalInstrumentHoldings = net_positive ? oldTotalInstrumentHoldings + delta : oldTotalInstrumentHoldings - delta;

        emit InstrumentHarvest(instrument, balanceThisHarvest, delta, net_positive);
    }

    /// @notice Deposit a specific amount of float into a trusted Instrument.
   	/// Called when market is approved. 
   	/// Also has the role of granting a credit line to a credit-based Instrument like uncol.loans 
    function depositIntoInstrument(Instrument instrument, uint256 underlyingAmount) external onlyController{
    	require(getInstrumentData[instrument].trusted, "UNTRUSTED Instrument");
    	totalInstrumentHoldings += underlyingAmount; 

        getInstrumentData[instrument].balance += underlyingAmount.safeCastTo248();

        UNDERLYING.transfer(address(instrument), underlyingAmount);

        emit InstrumentDeposit(msg.sender, instrument, underlyingAmount);
    }

    /// @notice Withdraw a specific amount of underlying tokens from a Instrument.
    function withdrawFromInstrument(Instrument instrument, uint256 underlyingAmount) external requiresAuth{
    	require(getInstrumentData[instrument].trusted, "UNTRUSTED Instrument");
        
        getInstrumentData[instrument].balance -= underlyingAmount.safeCastTo248();
        
        totalInstrumentHoldings -= underlyingAmount;
        
        require(instrument.redeemUnderlying(underlyingAmount), "REDEEM_FAILED");
        
        emit InstrumentWithdrawal(msg.sender, instrument, underlyingAmount);

    }


    /// @notice Withdraws all underyling balance from the Instrument to the vault 
    function withdrawAllFromInstrument(Instrument instrument) internal {
    	uint248 total_Instrument_balance = instrument.balanceOfUnderlying(address(instrument)).safeCastTo248();
    	
        uint248 current_balance =  getInstrumentData[instrument].balance;
    	
        getInstrumentData[instrument].balance -= Math.min(total_Instrument_balance, current_balance).safeCastTo248();
    	
        instrument.redeemUnderlying(total_Instrument_balance);
    }

    /// @notice Stores a Instrument as trusted when its approved
    function trustInstrument(Instrument instrument) external onlyController{
    	getInstrumentData[instrument].trusted = true;

    }

    /// @notice Stores a Instrument as untrusted
    function distrustInstrument(Instrument instrument) external onlyController {
    	getInstrumentData[instrument].trusted = false; 
    }


    /// @notice returns true if Instrument is approved
    function isTrusted(Instrument instrument) public view returns(bool){
    	return getInstrumentData[instrument].trusted; 
    }

    /// @notice Calculates the total amount of underlying tokens the Vault holds, excluding profit 
    function totalAssets() public view override returns(uint256){
    	return totalInstrumentHoldings + totalFloat();
    }

   	
    function totalFloat() public view returns (uint256) {
        return UNDERLYING.balanceOf(address(this));
    }

    function fetchInstrumentData(uint256 marketId) public view returns(InstrumentData memory){
        return getInstrumentData[Instruments[marketId]];
    }

    /**
     called on market denial + removal, maybe no chekcs?
     */
    function removeInstrument(uint256 marketId) internal {
        InstrumentData storage data = getInstrumentData[Instruments[marketId]];
        require(data.marketId > 0, "instrument doesn't exist");
        delete getInstrumentData[Instruments[marketId]];
        delete Instruments[marketId];
        // emit event here;
    }

    function onMarketApproval(uint256 marketId) external onlyController {
        Instruments[marketId].onMarketApproval();
    }

    /// @notice add instrument proposal created by the Utilizer 
    /// @dev Instrument instance should be created before this is called
    /// need to add authorization
    function addProposal(
        InstrumentData memory data
    ) external {
        require(data.principal > 0, "principal must be greater than 0");
        require(data.duration > 0, "duration must be greater than 0");
        require(data.faceValue > 0, "faceValue must be greater than 0");
        require(data.principal >= BASE_UNIT, "Needs to be in decimal format"); // should be collateral address, not DS. Can't be less than 1.0 X?
        require(data.marketId > 0, "must be valid instrument");

        num_proposals[msg.sender] ++; 
        getInstrumentData[Instrument(data.Instrument_address)] = (
        	InstrumentData(
        		false, 
                0, 
                data.faceValue, 
                data.marketId, 
                data.principal, 
                data.expectedYield, 
                data.duration, 
                data.description, 
                data.Instrument_address,
                data.instrument_type,
                0
            )
        	); 

        Instruments[data.marketId] = Instrument(data.Instrument_address);
    }

    /**
     @notice checks status of instrument
     returns true if resolution, false if not.
     */
    function checkInstrument(
        uint256 marketId
    ) external returns (bool) {
        InstrumentData storage data = getInstrumentData[Instruments[marketId]];
        
        require(data.marketId > 0 && data.trusted, "instrument must be active");
        require(data.maturityDate > 0, "instrument hasn't been approved yet" );

        if (block.timestamp >= data.maturityDate) {
            resolveInstrument(Instruments[marketId]);
            return true;
        }
        return false;
    }

    /**
     @notice called by controller on approveMarket.
     */
    function setMaturityDate(Instrument _instrument) external onlyController {
        getInstrumentData[_instrument].maturityDate = getInstrumentData[_instrument].duration + block.timestamp;
    }

    /**
     @notice called on resolution => checkInstrument => called by anyone, withdraws all funds to vault, triggers resolveMarket for controller.
     @dev no checks, checks performed by checkInstrument()
     */
    function resolveInstrument(
        Instrument _instrument
    ) internal {
        harvest(address(_instrument));

        InstrumentData storage data = getInstrumentData[_instrument];

        bool atLoss = data.balance < data.faceValue;

        uint256 total_loss = atLoss ? data.faceValue - data.balance : 0;
        uint256 extra_gain = !atLoss ? data.balance - data.faceValue : 0;

        withdrawAllFromInstrument(_instrument);
        controller.resolveMarket(data.marketId, atLoss, extra_gain, total_loss);
        removeInstrument(data.marketId);
    }

    /**
     called on market denial by controller.
     */
    function denyInstrument(uint256 marketId) external requiresAuth {
        InstrumentData storage data = getInstrumentData[Instruments[marketId]];
        
        require(marketId > 0 && data.Instrument_address != address(0), "invalid instrument");

        require(!data.trusted, "can't deny approved instrument");
        
        removeInstrument(marketId);
    }
}","keccak256":"0x2fed62189f239454568e486e9387847f0e6cf268af437c3b9e71a170fcf53512"},"contracts/vaults/vault_tranche/splitter.sol":{"content":"pragma solidity ^0.8.4;


import {Auth} from "../auth/Auth.sol";
import {ERC4626} from "../mixins/ERC4626.sol";

import {SafeCastLib} from "../utils/SafeCastLib.sol";
import {SafeTransferLib} from "../utils/SafeTransferLib.sol";
import {FixedPointMathLib} from "../utils/FixedPointMathLib.sol";

import {ERC20} from "../tokens/ERC20.sol";
import {Instrument} from "../instrument.sol";
import {tVault} from "./tVault.sol"; 
import "@openzeppelin/contracts/utils/math/Math.sol";




/// @notice tokens for junior/senior tranches 
contract tToken is ERC20{

	modifier onlySplitter() {
    require(msg.sender == splitter, "!Splitter");
     _;
  }

  address splitter; 
  ERC20 asset; 

  /// @notice asset is the tVault  
  constructor(
      ERC20 _asset, 
      string memory _name,
      string memory _symbol, 
      address _splitter
  ) ERC20(_name, _symbol, _asset.decimals()) {
      asset = _asset;
      splitter = _splitter; 
  }

  function mint(address to, uint256 amount) external onlySplitter{
  	_mint(to, amount); 
  }

  function burn(address from, uint256 amount) external onlySplitter{
  	_burn(from, amount);
  }


}

/// @notice Accepts ERC20 and splits them into senior/junior tokens
/// Will hold the ERC20 token in this contract
/// Before maturity, redemption only allowed for a pair, 
/// After maturity, redemption allowed for individual tranche tokens, with the determined conversion rate
/// @dev new instance is generated for each vault
contract Splitter{
	using SafeCastLib for uint256; 
  using SafeTransferLib for ERC20;
  using FixedPointMathLib for uint256;

  tVault underlying; 
  tToken senior;
  tToken junior;  

  //weight is in PRICE_PRECISION 6, i.e 5e5 = 0.5
  uint256 junior_weight; 
  uint256 PRICE_PRECISION; 
  uint promised_return; 


  //Redemption Prices 
  uint256 s_r; 
  uint256 j_r; 

  constructor(
  	tVault _underlying //underlying vault token to split 
  	){
  	underlying = _underlying; 
  	senior = new tToken(_underlying, "senior", string(abi.encodePacked("se_", _underlying.symbol())), address(this));
  	junior = new tToken(_underlying, "junior", string(abi.encodePacked("ju_", _underlying.symbol())), address(this));

  	junior_weight = underlying.getJuniorWeight(); 
  	promised_return = underlying.getPromisedReturn(); 
  	PRICE_PRECISION = underlying.PRICE_PRECISION(); 
  }

	/// @notice accepts token_to_split and mints s,j tokens
	/// ex. 1 vault token-> 0.3 junior and 0.7 senior for weight of 0.3, 0.7
	function split(ERC20 token_to_split, uint256 amount) external returns(uint, uint) {
		require(token_to_split == underlying, "Wrong Splitter");

		token_to_split.safeTransferFrom(msg.sender, address(this), amount); 

		uint junior_token_mint_amount = (amount * junior_weight)/PRICE_PRECISION;  
		uint senior_token_mint_amount = amount - junior_token_mint_amount; 

		junior.mint(msg.sender, junior_token_mint_amount); 
		senior.mint(msg.sender, senior_token_mint_amount);

		return (junior_token_mint_amount, senior_token_mint_amount); 

	}

	/// @notice aceepts junior and senior token and gives back token_to_merge(tVault tokens)
	/// Function to call when redeeming before maturity
	/// @param token_to_merge is the valut token
	/// @param junior_amount is amount of junior tokens user want to redeem
	/// @dev senior amount is automiatically computed when given junior amount 
	function merge(ERC20 token_to_merge, uint256 junior_amount) external returns(uint){
		require(token_to_merge == underlying, "Wrong Splitter");
		uint senior_multiplier = (PRICE_PRECISION *(PRICE_PRECISION - junior_weight))/junior_weight; //ex 2.3e6
		uint senior_amount = (senior_multiplier * junior_amount)/PRICE_PRECISION; 
		require(senior.balanceOf(msg.sender) >= senior_amount, "Not enough senior tokens"); 

		junior.burn(msg.sender, junior_amount);
		senior.burn(msg.sender, senior_amount);
		underlying.transfer(msg.sender, junior_amount+senior_amount); 
		return junior_amount + senior_amount; 
	}


	/// @notice only can called after set maturity by tranche token holders
	function redeem_after_maturity(tToken _tToken, uint256 amount) external {
		require(underlying.isMatured(), "Vault not matured");
		require(address(_tToken) == address(senior) || address(_tToken) == address(junior), "Wrong Tranche Token");
		bool isSenior = (address(_tToken) == address(senior)) ? true : false; 
		uint redemption_price = isSenior? s_r: j_r; 
		uint token_redeem_amount = (redemption_price * amount)/PRICE_PRECISION; 

		_tToken.burn(msg.sender, amount); 
		underlying.transfer(msg.sender, token_redeem_amount); 
		
	}


	/// @notice calculate and store redemption Price for post maturity 
	/// @dev should be only called once right after tToken matures, as totalSupply changes when redeeming 
	function calcRedemptionPrice() private {
		uint promised_return = underlying.getPromisedReturn(); //in 1e6 decimals i.e 5000 is 0.05
		uint real_return = underlying.getCurrentRealReturn(); 
		uint _s_r = ((PRICE_PRECISION + promised_return)/(PRICE_PRECISION+real_return)) * PRICE_PRECISION; 
		uint max_s_r = (PRICE_PRECISION/(PRICE_PRECISION - junior_weight)) *PRICE_PRECISION; 
		
		s_r = min(_s_r, max_s_r);
		//total supply right after tVault matures 
		j_r = (underlying.totalSupply() - (senior.totalSupply() * s_r/PRICE_PRECISION))/(junior.totalSupply()); 

	}

	/// @dev need to return in list format to index it easily 
	/// 0 is always senior 
	function getTrancheTokens() public view returns(address[] memory){
 		address[] memory addresses = new address[](2);
 		addresses[0] =  address(senior); 
 		addresses[1] =  address(junior); 
		return addresses; 
	}


	function max(uint256 a, uint256 b) internal pure returns (uint256) {
    	return a >= b ? a : b;
	}

	function min(uint256 a, uint256 b) internal pure returns (uint256) {
    	return a <= b ? a : b;
	}
}","keccak256":"0x30ef6f6a8d99b73632e1d1be9e5a54a205b10ad3e163857a06349da9c4a0f334"},"contracts/vaults/vault_tranche/stableswap.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8;

/*
Invariant - price of trade and amount of liquidity are determined by this equation

An^n sum(x_i) + D = ADn^n + D^(n + 1) / (n^n prod(x_i))

Topics
0. Newton's method x_(n + 1) = x_n - f(x_n) / f'(x_n)
1. Invariant
2. Swap
   - Calculate Y
   - Calculate D
3. Get virtual price
4. Add liquidity
   - Imbalance fee
5. Remove liquidity
6. Remove liquidity one token
   - Calculate withdraw one token
   - getYD
TODO: test?
*/

library Math {
    function abs(uint x, uint y) internal pure returns (uint) {
        return x >= y ? x - y : y - x;
    }
}

contract StableSwap {
  // Number of tokens
  uint private constant N = 2;
  // Amplification coefficient multiplied by N^(N - 1)
  // Higher value makes the curve more flat
  // Lower value makes the curve more like constant product AMM
  uint private constant A = 1000 * (N**(N - 1));
  // 0.03%
  uint private constant SWAP_FEE = 300;
  // Liquidity fee is derived from 2 constraints
  // 1. Fee is 0 for adding / removing liquidity that results in a balanced pool
  // 2. Swapping in a balanced pool is like adding and then removing liquidity
  //    from a balanced pool
  // swap fee = add liquidity fee + remove liquidity fee
  uint private constant LIQUIDITY_FEE = (SWAP_FEE * N) / (4 * (N - 1));
  uint private constant FEE_DENOMINATOR = 1e6;

  address[N] public tokens;
  // Normalize each token to 18 decimals
  // Example - DAI (18 decimals), USDC (6 decimals), USDT (6 decimals)
  //uint[N] private multipliers = [1, 1e12, 1e12];
  uint[N] private multipliers = [1, 1];

  uint[N] public balances;

  // 1 share = 1e18, 18 decimals
  uint private constant DECIMALS = 18;
  uint public totalSupply;

  address factory; 
  mapping(address => uint) public balanceOf;
  constructor(address token1, address token2){
    factory = msg.sender; 
    tokens[0] = token1;
    tokens[1] = token2; 
  }

  function _mint(address _to, uint _amount) private {
      balanceOf[_to] += _amount;
      totalSupply += _amount;
  }

  function _burn(address _from, uint _amount) private {
      balanceOf[_from] -= _amount;
      totalSupply -= _amount;
  }


  // Return precision-adjusted balances, adjusted to 18 decimals
  function _xp() private view returns (uint[N] memory xp) {
      for (uint i; i < N; ++i) {
          xp[i] = balances[i] * multipliers[i];
      }
  }

  /**
   * @notice Calculate D, sum of balances in a perfectly balanced pool
   * If balances of x_0, x_1, ... x_(n-1) then sum(x_i) = D
   * @param xp Precision-adjusted balances
   * @return D
   */
  function _getD(uint[N] memory xp) private pure returns (uint) {
    /*
    Newton's method to compute D
    -----------------------------
    f(D) = ADn^n + D^(n + 1) / (n^n prod(x_i)) - An^n sum(x_i) - D 
    f'(D) = An^n + (n + 1) D^n / (n^n prod(x_i)) - 1

                 (as + np)D_n
    D_(n+1) = -----------------------
              (a - 1)D_n + (n + 1)p

    a = An^n
    s = sum(x_i)
    p = (D_n)^(n + 1) / (n^n prod(x_i))
    */
    uint a = A * N; // An^n

    uint s; // x_0 + x_1 + ... + x_(n-1)
    for (uint i; i < N; ++i) {
        s += xp[i];
    }

    // Newton's method
    // Initial guess, d <= s
    uint d = s;
    uint d_prev;
    for (uint i; i < 255; ++i) {
        // p = D^(n + 1) / (n^n * x_0 * ... * x_(n-1))
        uint p = d;
        for (uint j; j < N; ++j) {
            p = (p * d) / (N * xp[j]);
        }
        d_prev = d;
        d = ((a * s + N * p) * d) / ((a - 1) * d + (N + 1) * p);

        if (Math.abs(d, d_prev) <= 1) {
            return d;
        }
    }
    revert("D didn't converge");
  }


 /**
  * @notice Calculate required dx to get dy
  * @param indexX is the index of the tokenIn
  * @dev only works in binary settins for now, indexX is 0 if senior, 1 if junior 
  * y is tokenOut, so dy should be subtracted, x is tokenIn, so should be added
  */

  function getDx(
      uint dy,
      uint indexX
      ) 
  public view returns(uint){
    uint[N] memory xp = _xp(); 
    uint xamount = xp[indexX]; 
    uint yamount = xp[1-indexX]; 

    uint new_yamount = yamount - dy; 

    uint new_xamount = _getY(1-indexX, indexX, new_yamount, xp); 
    uint dx = new_xamount - xamount; 
    return dx; 
  }

  /**
   * @notice Calculate the new balance of token j given the new balance of token i
   * @param i Index of token in
   * @param j Index of token out
   * @param x New balance of token i
   * @param xp Current precision-adjusted balances
   */
  function _getY(
    uint i,
    uint j,
    uint x,
    uint[N] memory xp
) private pure returns (uint) {
    /*
    Newton's method to compute y
    -----------------------------
    y = x_j

    f(y) = y^2 + y(b - D) - c

                y_n^2 + c
    y_(n+1) = --------------
               2y_n + b - D

    where
    s = sum(x_k), k != j
    p = prod(x_k), k != j
    b = s + D / (An^n)
    c = D^(n + 1) / (n^n * p * An^n)
    */
    uint a = A * N;
    uint d = _getD(xp);
    uint s;
    uint c = d;

    uint _x;
    for (uint k; k < N; ++k) {
        if (k == i) {
            _x = x;
        } else if (k == j) {
            continue;
        } else {
            _x = xp[k];
        }

        s += _x;
        c = (c * d) / (N * _x);
    }
    c = (c * d) / (N * a);
    uint b = s + d / a;

    // Newton's method
    uint y_prev;
    // Initial guess, y <= d
    uint y = d;
    for (uint _i; _i < 255; ++_i) {
        y_prev = y;
        y = (y * y + c) / (2 * y + b - d);
        if (Math.abs(y, y_prev) <= 1) {
            return y;
        }
    }
    revert("y didn't converge");
  }

  /**
   * @notice Calculate the new balance of token i given precision-adjusted
   * balances xp and liquidity d
   * @dev Equation is calculate y is same as _getY
   * @param i Index of token to calculate the new balance
   * @param xp Precision-adjusted balances
   * @param d Liquidity d
   * @return New balance of token i
   */
  function _getYD(
    uint i,
    uint[N] memory xp,
    uint d
) private pure returns (uint) {
    uint a = A * N;
    uint s;
    uint c = d;

    uint _x;
    for (uint k; k < N; ++k) {
        if (k != i) {
            _x = xp[k];
        } else {
            continue;
        }

        s += _x;
        c = (c * d) / (N * _x);
    }
    c = (c * d) / (N * a);
    uint b = s + d / a;

    // Newton's method
    uint y_prev;
    // Initial guess, y <= d
    uint y = d;
    for (uint _i; _i < 255; ++_i) {
        y_prev = y;
        y = (y * y + c) / (2 * y + b - d);
        if (Math.abs(y, y_prev) <= 1) {
            return y;
        }
    }
    revert("y didn't converge");
  }

  // Estimate value of 1 share
  // How many tokens is one share worth?
  function getVirtualPrice() external view returns (uint) {
      uint d = _getD(_xp());
      uint _totalSupply = totalSupply;
      if (_totalSupply > 0) {
          return (d * 10**DECIMALS) / _totalSupply;
      }
      return 0;
  }

  /**
   * @notice Swap dx amount of token i for token j
   * @param i Index of token in
   * @param j Index of token out
   * @param dx Token in amount
   * @param minDy Minimum token out
   */
  function swap(
      uint i,
      uint j,
      uint dx,
      uint minDy
  ) external returns (uint dy) {
    require(i != j, "i = j");

    IERC20(tokens[i]).transferFrom(msg.sender, address(this), dx);

    // Calculate dy
    uint[N] memory xp = _xp();
    uint x = xp[i] + dx * multipliers[i];

    uint y0 = xp[j];
    uint y1 = _getY(i, j, x, xp);
    // y0 must be >= y1, since x has increased
    // -1 to round down
    dy = (y0 - y1 - 1) / multipliers[j];

    // Subtract fee from dy
    uint fee = (dy * SWAP_FEE) / FEE_DENOMINATOR;
    dy -= fee;
    require(dy >= minDy, "dy < min");

    balances[i] += dx;
    balances[j] -= dy;

    IERC20(tokens[j]).transfer(msg.sender, dy);
}

function addLiquidity(uint[N] calldata amounts, uint minShares)
    external
    returns (uint shares)
{
    // calculate current liquidity d0
    uint _totalSupply = totalSupply;
    uint d0;
    uint[N] memory old_xs = _xp();
    if (_totalSupply > 0) {
        d0 = _getD(old_xs);
    }

    // Transfer tokens in
    uint[N] memory new_xs;
    for (uint i; i < N; ++i) {
        uint amount = amounts[i];
        if (amount > 0) {
            IERC20(tokens[i]).transferFrom(msg.sender, address(this), amount);
            new_xs[i] = old_xs[i] + amount * multipliers[i];
        } else {
            new_xs[i] = old_xs[i];
        }
    }

    // Calculate new liquidity d1
    uint d1 = _getD(new_xs);
    require(d1 > d0, "liquidity didn't increase");

    // Reccalcuate D accounting for fee on imbalance
    uint d2;
    if (_totalSupply > 0) {
        for (uint i; i < N; ++i) {
            // TODO: why old_xs[i] * d1 / d0? why not d1 / N?
            uint idealBalance = (old_xs[i] * d1) / d0;
            uint diff = Math.abs(new_xs[i], idealBalance);
            new_xs[i] -= (LIQUIDITY_FEE * diff) / FEE_DENOMINATOR;
        }

        d2 = _getD(new_xs);
    } else {
        d2 = d1;
    }

    // Update balances
    for (uint i; i < N; ++i) {
        balances[i] += amounts[i];
    }

    // Shares to mint = (d2 - d0) / d0 * total supply
    // d1 >= d2 >= d0
    if (_totalSupply > 0) {
        shares = ((d2 - d0) * _totalSupply) / d0;
    } else {
        shares = d2;
    }
    require(shares >= minShares, "shares < min");
    _mint(msg.sender, shares);
}

  function removeLiquidity(uint shares, uint[N] calldata minAmountsOut)
      external
      returns (uint[N] memory amountsOut)
  {
      uint _totalSupply = totalSupply;

      for (uint i; i < N; ++i) {
          uint amountOut = (balances[i] * shares) / _totalSupply;
          require(amountOut >= minAmountsOut[i], "out < min");

          balances[i] -= amountOut;
          amountsOut[i] = amountOut;

          IERC20(tokens[i]).transfer(msg.sender, amountOut);
      }

      _burn(msg.sender, shares);
  }

  /**
   * @notice Calculate amount of token i to receive for shares
   * @param shares Shares to burn
   * @param i Index of token to withdraw
   * @return dy Amount of token i to receive
   *         fee Fee for withdraw. Fee already included in dy
   */
  function _calcWithdrawOneToken(uint shares, uint i)
      private
      view
      returns (uint dy, uint fee)
  {
      uint _totalSupply = totalSupply;
      uint[N] memory xp = _xp();

      // Calculate d0 and d1
      uint d0 = _getD(xp);
      uint d1 = d0 - (d0 * shares) / _totalSupply;

      // Calculate reduction in y if D = d1
      uint y0 = _getYD(i, xp, d1);
      // d1 <= d0 so y must be <= xp[i]
      uint dy0 = (xp[i] - y0) / multipliers[i];

      // Calculate imbalance fee, update xp with fees
      uint dx;
      for (uint j; j < N; ++j) {
          if (j == i) {
              dx = (xp[j] * d1) / d0 - y0;
          } else {
              // d1 / d0 <= 1
              dx = xp[j] - (xp[j] * d1) / d0;
          }
          xp[j] -= (LIQUIDITY_FEE * dx) / FEE_DENOMINATOR;
      }

      // Recalculate y with xp including imbalance fees
      uint y1 = _getYD(i, xp, d1);
      // - 1 to round down
      dy = (xp[i] - y1 - 1) / multipliers[i];
      fee = dy0 - dy;
  }

  function calcWithdrawOneToken(uint shares, uint i)
      external
      view
      returns (uint dy, uint fee)
  {
      return _calcWithdrawOneToken(shares, i);
  }

  /**
   * @notice Withdraw liquidity in token i
   * @param shares Shares to burn
   * @param i Token to withdraw
   * @param minAmountOut Minimum amount of token i that must be withdrawn
   */
  function removeLiquidityOneToken(
      uint shares,
      uint i,
      uint minAmountOut
  ) external returns (uint amountOut) {
      (amountOut, ) = _calcWithdrawOneToken(shares, i);
      require(amountOut >= minAmountOut, "out < min");

      balances[i] -= amountOut;
      _burn(msg.sender, shares);

      IERC20(tokens[i]).transfer(msg.sender, amountOut);
  }





  function xp() public view returns(uint[N] memory){
      return _xp(); 
  }



}

interface IERC20 {
    function totalSupply() external view returns (uint);

    function balanceOf(address account) external view returns (uint);

    function transfer(address recipient, uint amount) external returns (bool);

    function allowance(address owner, address spender) external view returns (uint);

    function approve(address spender, uint amount) external returns (bool);

    function transferFrom(
        address sender,
        address recipient,
        uint amount
    ) external returns (bool);

    event Transfer(address indexed from, address indexed to, uint amount);
    event Approval(address indexed owner, address indexed spender, uint amount);
}","keccak256":"0x3b73a7daf0031bedbb58afe22b22ab9821d0bb2afdada9070f45193bd6c0bcf7","license":"MIT"},"contracts/vaults/vault_tranche/tVault.sol":{"content":"pragma solidity ^0.8.4;


import {Auth} from "../auth/Auth.sol";
import {ERC4626} from "../mixins/ERC4626.sol";

import {SafeCastLib} from "../utils/SafeCastLib.sol";
import {SafeTransferLib} from "../utils/SafeTransferLib.sol";
import {FixedPointMathLib} from "../utils/FixedPointMathLib.sol";

import {ERC20} from "../tokens/ERC20.sol";
import {Instrument} from "../instrument.sol";
import {Vault} from "../vault.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import {TrancheFactory} from "./tranchemaster.sol"; 
import "hardhat/console.sol";

/// @notice super vault that accepts any combinations of ERC4626 instruments at initialization, and will
/// automatically invest/divest when minting/redeeming 
/// @dev instance is generated for every splitter
contract tVault is ERC4626{
	using SafeCastLib for uint256; 
  using SafeTransferLib for ERC20;
  using FixedPointMathLib for uint256;

  uint256 num_instrument; 
  uint256[] ratios; 
  address[] instruments; 
  uint256 init_time; 
  uint256 junior_weight; 
  uint256 promisedReturn; 
  uint256 time_to_maturity; 
  ERC20 want; 

  mapping(address=>uint256) addressToIndex; 

  uint256[] initial_exchange_rates; 

  uint256 public PRICE_PRECISION; 

  /// @notice when intialized, will take in a few ERC4626 instruments (address) as base instruments
  /// param _want is the base assets for all the instruments e.g usdc
  /// param _instruments are ERC4626 addresses that will comprise this super vault
  /// param _ratios are the weight of value invested for each instruments, should sum to 1 
  /// param _junior_weight is the allocation between junior/senior tranche (senior is 1-junior)
  /// param _time_to_maturity is time until the tranche tokens redemption price will be determined
  /// and tranche tokens can be redeemed separately 
  /// param _promisedReturn is the promised senior return gauranteed by junior holders 
  constructor(
    TrancheFactory.InitParams memory param)
    ERC4626(
        ERC20(param._want),
        string(abi.encodePacked("super ", ERC20(param._want).name(), " Vault")),
        string(abi.encodePacked("t", ERC20(param._want).symbol()))
    )  
	//Vault(address(_want))
{
    want = ERC20(param._want); 
    instruments = param._instruments; 
    num_instrument = param._instruments.length; 
    ratios = param._ratios; 
    junior_weight = param._junior_weight; 
    promisedReturn = param._promisedReturn; 
    time_to_maturity = param._time_to_maturity; 
    init_time = block.timestamp; 

    initial_exchange_rates = new uint[](num_instrument); 
    PRICE_PRECISION = 10**want.decimals(); 

    //need to get initial exchange rate between the instruments and want 
    for (uint i=0; i< num_instrument; i++){
        addressToIndex[instruments[i]] = i; 
        initial_exchange_rates[i] = get_exchange_rate(instruments[i]); 
    }
  }


  /// @notice get the amount of shares for the instrument one would obtain
  /// by depositing one want token 
  function get_exchange_rate(address instrument) internal view returns(uint256){
  	return ERC4626(instrument).previewDeposit(PRICE_PRECISION); 
  }

  /// @notice will automatically invest into the ERC4626 instruments and give out 
  /// vault tokens as share
  function mint(uint256 shares, address receiver) public override returns(uint assets)  {
    assets = previewMint(shares); // No need to check for rounding error, previewMint rounds up.
    console.log('here', msg.sender);

    asset.safeTransferFrom(msg.sender, address(this), assets);
    console.log('assets', assets, shares); 
    invest(shares); 

    _mint(receiver, shares);
    emit Deposit(msg.sender, receiver, assets, shares);
    afterDeposit(assets, shares);

  }


  /// @notice will automatically divest from the instruments
  function redeem(
    uint256 shares,
    address receiver,
    address owner
) public override returns(uint assets){
    if (msg.sender != owner) {
        uint256 allowed = allowance[owner][msg.sender]; // Saves gas for limited approvals.

        if (allowed != type(uint256).max) allowance[owner][msg.sender] = allowed - shares;
    }

    // Check for rounding error since we round down in previewRedeem.
    require((assets = previewRedeem(shares)) != 0, "ZERO_ASSETS");
    divest(assets); 

    beforeWithdraw(assets, shares);
    _burn(owner, shares);
    emit Withdraw(msg.sender, receiver, owner, assets, shares);
    asset.safeTransfer(receiver, assets);

  }

  /// @notice will invest into the current instruments, which is equivalent to minting erc4626
  /// @param shares are denominated in vault token, in PRICE_PRECISION
  function invest(uint256 shares) internal {
  	
  	uint num_asset_for_this; 
  	for (uint i=0; i< num_instrument; i++){
  		num_asset_for_this = ERC4626(instruments[i]).convertToAssets((shares*ratios[i])/PRICE_PRECISION);  
      asset.safeApprove(instruments[i], num_asset_for_this); 
      uint a = asset.balanceOf(address(this));
      console.log('balance',a, ratios[0]); 
      console.log('shares', num_asset_for_this, shares); 
  		uint receivedShares = ERC4626(instruments[i]).deposit(num_asset_for_this, address(this)); //will mint the instrument to this contract
  	}
  }

  /// @notice will divest from current instruments, which is equivalent to redeeming erc4626
  /// @param assets are denominated in underlying token
  function divest(uint256 assets) internal{
  	uint num_assets_for_this; 
  	for (uint i=0; i< num_instrument; i++){
  		num_assets_for_this = (assets* ratios[i]/PRICE_PRECISION); 
  		ERC4626(instruments[i]).withdraw(num_assets_for_this, address(this), address(this)); 
  	}

  }


  function isMatured() public view returns(bool){
  	return (block.timestamp - init_time) > time_to_maturity; 
  }

  function getUnderlying() public view returns(address){
  	return address(want); 
  }

  function getJuniorWeight() public view returns(uint256){
  	return junior_weight; 
  }

  function getPromisedReturn() public view returns(uint256){
  	return promisedReturn; 
  }
  function getInitialExchangeRates() public view returns(uint[] memory){
      return initial_exchange_rates; 
  }

  /// @notice get average real returns collected by the vault in this supervault until now  
  /// real return is computed by (final_value_of_vault/initial_value_of_vault) - 1
  function getCurrentRealReturn() public view returns(uint256){
  	uint256[] memory real_returns = new uint256[](num_instrument); 
  	uint256 sum_return; 
  	for (uint i=0; i< num_instrument; i++){
  		real_returns[i] = (get_exchange_rate(instruments[i])/initial_exchange_rates[i])*PRICE_PRECISION;
  		sum_return += real_returns[i] - PRICE_PRECISION; 
	}

	return (sum_return/num_instrument); 
	
  }

  /// @notice sums over all assets in want tokens 
  function totalAssets() public view override returns (uint256){
    uint sumAssets; 
    for (uint i=0; i< num_instrument; i++){
        sumAssets += ERC4626(instruments[i]).totalAssets(); 
    }
    return sumAssets; 

  }


 

}
","keccak256":"0x30f1039d8a7a59e03223cbb9741281df7fa753e90b037611954f48f57486a427"},"contracts/vaults/vault_tranche/tranchemaster.sol":{"content":"pragma solidity ^0.8.4;


import {Auth} from "../auth/Auth.sol";
import {ERC4626} from "../mixins/ERC4626.sol";

import {SafeCastLib} from "../utils/SafeCastLib.sol";
import {SafeTransferLib} from "../utils/SafeTransferLib.sol";
import {FixedPointMathLib} from "../utils/FixedPointMathLib.sol";

import {ERC20} from "../tokens/ERC20.sol";
import {Splitter} from "./splitter.sol";
import {tVault} from "./tVault.sol";
import {StableSwap} from "./stableswap.sol"; 
import "@openzeppelin/contracts/utils/math/Math.sol";

/// @notice have to separate factories because of code size limit 
contract TrancheAMMFactory{

	address base_factory;
	mapping(address=>bool) _isPool; 
	constructor(){
		base_factory = msg.sender; 
	}
	function newPool(address token1, address token2) external returns(StableSwap){
		StableSwap amm = new StableSwap(token1, token2); 
		_isPool[address(amm)] = true; 
		return amm; 
	}
	function isPool(address pooladd) public view returns(bool){
		return _isPool[pooladd]; 
	}
} 


/// @notice contract that stores the contracts and liquidity for each tranches 
contract TrancheFactory{

	uint256 numVaults; 
  address owner; 

  TrancheAMMFactory ammFactory; 

	/// @notice initialization parameters for the vault
	struct InitParams{
		address _want; 
    	address[]  _instruments;
    	uint256[]  _ratios;
    	uint256 _junior_weight; 
    	uint256 _promisedReturn; 
    	uint256 _time_to_maturity;
    	uint256 vaultId; 
	}

	struct Contracts{
		address vault; 
		address splitter; 
		address amm; 
		InitParams param; 
	}

	mapping(uint256=>Contracts) vaultContracts;
	mapping(uint256=>mapping(address=>uint256)) lp_holdings;  //vaultId-> LP holdings for providrers

    constructor(
        address _owner, 
        address ammFactory_address
    ) public {
        owner = _owner;
        ammFactory = TrancheAMMFactory(ammFactory_address); 

    }



	/// @notice adds vaults, spllitters, and amms when tranche bids are filled 
	/// Bidders have to specify the 
	/// param want: underlying token for all the vaults e.g(usdc,eth)
	/// param instruments: addresses of all vaults for the want they want exposure to
	/// param ratios: how much they want to split between the instruments 
	/// param junior weight: how much the juniors are allocated; lower means higher leverage for juniors but lower safety for seniors
	/// param promisedReturn: how much fixed income seniors are getting paid primarily, 
	/// param timetomaturity: when the tVault matures and tranche token holders can redeem their tranche for tVault 
	/// @dev a bid is filled when liquidity provider agrees to provide initial liq for senior/junior or vice versa.  
	/// so initial liq should be provided nonetheless 
	function createVault(InitParams memory param) public {

		tVault newvault = new tVault(param); 
		Splitter splitter = new Splitter(newvault);
		address[] memory tokens = splitter.getTrancheTokens(); 
		StableSwap amm = ammFactory.newPool(tokens[0], tokens[1]); 

		uint vaultId = param.vaultId; 
		Contracts storage contracts = vaultContracts[vaultId]; 
		contracts.vault = address(newvault); 
		contracts.splitter = address(splitter);
		contracts.amm = address(amm); 
		contracts.param = param;

		numVaults++; 
	}	


	/// @notice lp token balance is stored in this contract
	function increaseLPTokenBalance(address to, uint vaultId, uint lpshares) external{
		lp_holdings[vaultId][to] += lpshares; 
	}
	function decreaseLPTokenBalance(address to, uint vaultId, uint lpshares) external{
		lp_holdings[vaultId][to] -= lpshares; 
	}

	function getContracts(uint vaultId) external view returns(Contracts memory){
		return vaultContracts[vaultId]; 
	}

	function getLPTokenBalance(address to, uint vaultId) external view returns(uint256){
		return lp_holdings[vaultId][to]; 
	}

	function getSuperVault(uint vaultId) external view returns(tVault){
		return tVault(vaultContracts[vaultId].vault); 
	}
	function getSplitter(uint vaultId) external view returns(Splitter){
		return Splitter(vaultContracts[vaultId].splitter); 
	}
	function getAmm(uint vaultId) external view returns(StableSwap){
		return StableSwap(vaultContracts[vaultId].amm); 
	}
	//function getNumVaults
	// function getVaultId(InitParams memory param) external view returns(uint){
	// 	return vaultIdMapping[param]; 
	// }


}

/// @notice handles all trading related stuff 
contract TrancheMaster{

	TrancheFactory tFactory;

	constructor(TrancheFactory _tFactory){
		tFactory = _tFactory; 
	}


	/// @notice adds liquidity to pool with vaultId
	/// @dev amount is denominated in want of the tVault, so want-> mint tVault-> split -> provide 
	function addLiquidity(
		address provider,
		 uint amount, 
		 uint vaultId) external 
	{	

		TrancheFactory.Contracts memory contracts = tFactory.getContracts(vaultId); 
		ERC20 want = ERC20(contracts.param._want); 
		tVault vault = tVault(contracts.vault); 
		StableSwap amm = StableSwap(contracts.amm); 
		Splitter splitter = Splitter(contracts.splitter); 

		//Mint tVault
		want.transferFrom(provider, address(this), amount); 
		want.approve(address(vault), amount ); 
		uint shares = vault.convertToShares(amount);
		vault.mint(shares, address(this)); 

		//Split 
		vault.approve(address(splitter), shares);
		(uint ja, uint sa) = splitter.split(vault, shares); 

		//provide(same amount to get a balanced pool)
		uint[2] memory amounts; 
		amounts[0] = ja; 
		amounts[1] = ja; 
		address[] memory tranches = splitter.getTrancheTokens(); 
		ERC20(tranches[0]).approve(address(amm), ja); 
		ERC20(tranches[1]).approve(address(amm), ja); 
		uint lpshares = amm.addLiquidity(amounts, 0); 

		//Transfer
		tFactory.increaseLPTokenBalance(provider, vaultId, lpshares);

	}

	/// @notice remove liquidity from the pool, and gives back merged token
	function removeLiquidity(
		address taker, 
		uint shares, 
		uint vaultId) external 
	{
		TrancheFactory.Contracts memory contracts = tFactory.getContracts(vaultId); 
		ERC20 want = ERC20(contracts.param._want); 
		tVault vault = tVault(contracts.vault); 
		StableSwap amm = StableSwap(contracts.amm); 
		Splitter splitter = Splitter(contracts.splitter); 

		//Transfer
		tFactory.decreaseLPTokenBalance(taker, vaultId, shares); 

		//Remove
		uint[2] memory minAmounts;
		minAmounts[0] =0;
		minAmounts[1] =0;
		uint[2] memory amountsOut = amm.removeLiquidity(shares,minAmounts);
		uint junioramount = amountsOut[1]; 

		//Merge-> junior and senior in, tVault out to this address
		uint merged_token_amount = splitter.merge(vault, junioramount); 

		//Redeem vault 
		vault.redeem(merged_token_amount, taker, address(this)); 


	}



	/// @notice buy tranche token in one tx from underlying tVault collatera; 
	/// @param amount is collateral in 
	/// @dev 1.Mints vault token
	/// 2. Splits Vault token from splitter 
	/// 3. Swap unwanted tToken to wanted tToken
	/// 4. Transfer wanted tToken to user 
	function buy_tranche(
		uint vaultId, 
		uint amount, 
		bool isSenior
		) external 
	{
		TrancheFactory.Contracts memory contracts = tFactory.getContracts(vaultId); 
		ERC20 want = ERC20(contracts.param._want); 
		tVault vault = tVault(contracts.vault); 
		StableSwap amm = StableSwap(contracts.amm); 
		Splitter splitter = Splitter(contracts.splitter); 

		//1.Mint
		want.transferFrom(msg.sender, address(this), amount); 
		want.approve(address(vault), amount); 
		uint shares = vault.convertToShares(amount); 
		vault.mint(shares, address(this));

		//2. Split
		vault.approve(address(splitter), shares); 
		(uint ja, uint sa) = splitter.split(vault, shares); //junior and senior now minted to this address 

		//Senior tokens are indexed at 0 in each amm 
		uint tokenIn = isSenior? 0 : 1;
		uint tokenOut = 1-tokenIn; 
		uint tokenInAmount = isSenior? sa: ja; 
		address[] memory tranches = splitter.getTrancheTokens(); 

		//3. Swap 
		ERC20(tranches[tokenIn]).approve(address(amm), tokenInAmount); 
		uint tokenOutAmount = amm.swap(tokenIn, tokenOut, tokenInAmount, 0); //this will give this contract tokenOut

		//4. Transfer 
		ERC20(tranches[tokenOut]).transfer(msg.sender, tokenOutAmount); 

	}

	/// @notice sell tranche token for collateral in one tx
	/// 1. Transfer tToken 
	/// 2. Swap tTokens to get in correct ratio
	function sell_tranche(
		uint vaultId, 
		uint amount, 
		bool isSenior 
		) external 
	{
		TrancheFactory.Contracts memory contracts = tFactory.getContracts(vaultId); 
		ERC20 want = ERC20(contracts.param._want); 
		tVault vault = tVault(contracts.vault); 
		StableSwap amm = StableSwap(contracts.amm); 
		Splitter splitter = Splitter(contracts.splitter); 

		//1. Transfer tToken to this contract
		address[] memory tranches = splitter.getTrancheTokens(); 
		uint tokenIn = isSenior? 0:1; 
		ERC20(tranches[tokenIn]).transfer(msg.sender, amount); 

		//2. Swap to get correct ratio, if intoken is senior then need junior, 
		(uint pairTokenAmount, uint swappedTokenAmount) = swapToRatio(amount, !isSenior, tranches, vault, amm); 
		uint amountAfterSwap =  amount - swappedTokenAmount; 
		//amountAfterSwap, pairTokenAmount should be the amount of tranche tokens in ratio 

		//3.Merge the tokens (merged tVault token will be directed to this contract)
		uint junior_amount = isSenior? pairTokenAmount: swappedTokenAmount;  
		uint totalAmountMerged = splitter.merge(vault, junior_amount); 

		//4.Redeem merged token in tVault  
		vault.redeem(totalAmountMerged, msg.sender, address(this)); 


	}

	/// @notice swap portion of tToken to another to get the correct ratio
	/// e.x 100 junior-> 30 senior, 70 junior, when ratio is 3:7
	function swapToRatio(
		uint tokenInAmount, 
		bool needSenior,
		address[] memory tranches,
		tVault vault, 
		StableSwap amm) internal returns(uint, uint){
	
		//get swapping Token index; if senior is needed swap junior
		uint tokenInIndex = needSenior? 1:0;
		uint tokenOutIndex = 1- tokenInIndex; 
		address neededToken = tranches[tokenOutIndex]; 
		address swappingToken = tranches[tokenInIndex]; 
		uint junior_weight = vault.getJuniorWeight();
		uint PRICE_PRECISION = vault.PRICE_PRECISION();   
		
		//ex. 100j -> 30j, 70s (determined by ratio)
		// need x amount of juniors for 70s 
		uint neededTokenOutAmount; 
		if (needSenior)  neededTokenOutAmount = (PRICE_PRECISION - junior_weight) * tokenInAmount; 
		else  neededTokenOutAmount = junior_weight * tokenInAmount; 

		//Get how much tokenInAmount I need to get needed tokenoutAmount 
		uint neededTokenInAmount = amm.getDx(neededTokenOutAmount, tokenInIndex); 
		uint TokenOutAmount = amm.swap(tokenInIndex, tokenOutIndex, neededTokenInAmount,0 ); 
		//Now this contract has the neededTokenAmountOut tokens

		return (TokenOutAmount, neededTokenInAmount);
	}



