contracts/turbo/TrustedMarketFactoryV2.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;
import "../libraries/IERC20Full.sol";
import "../balancer/BPool.sol";
import "./AbstractMarketFactoryV2.sol";
import "./FeePot.sol";
import "../libraries/CalculateLinesToBPoolOdds.sol";


contract TrustedMarketFactoryV2 is AbstractMarketFactoryV2, CalculateLinesToBPoolOdds {
    using SafeMath for uint256;

    event MarketCreated(uint256 id, address creator, uint256 _endTime, string description, string[] outcomes);
    event MarketResolved(uint256 id, address winner);

    struct MarketDetails {
        string description;
    }
    MarketDetails[] internal marketDetails;

    constructor(
        address _owner,
        IERC20Full _collateral,
        uint256 _shareFactor,
        FeePot _feePot,
        uint256 _stakerFee,
        uint256 _settlementFee,
        address _protocol,
        uint256 _protocolFee
    )
        AbstractMarketFactoryV2(
            _owner,
            _collateral,
            _shareFactor,
            _feePot,
            _stakerFee,
            _settlementFee,
            _protocol,
            _protocolFee
        )
    {}

    function createMarket(
        address _creator,
        uint256 _endTime,
        string calldata _description,
        string[] calldata _names,
        string[] calldata _symbols,
        uint256[] calldata _odds
    ) public onlyOwner returns (uint256) {
        require(
            _names.length == _symbols.length && _symbols.length == _odds.length,
            "names, symbols, and odds must be the same length"
        );

        uint256 _id = markets.length;
        markets.push(makeMarket(_creator, _names, _symbols, _endTime, _odds));
        marketDetails.push(MarketDetails(_description));

        emit MarketCreated(_id, _creator, _endTime, _description, _symbols);
        return _id;
    }

    function resolveMarket(uint256) public pure override {
        require(false, "Only the TrustedMarketFactory owner can resolve the market, using trustedResolveMarket");
    }

    function trustedResolveMarket(uint256 _id, uint256 _winningOutcome) public onlyOwner {
        OwnedERC20 _winner = markets[_id].shareTokens[_winningOutcome];
        markets[_id].winner = _winner;
        emit MarketResolved(_id, address(_winner));
    }

    function getMarketDetails(uint256 _id) public view returns (MarketDetails memory) {
        return marketDetails[_id];
    }





}

contracts/turbo/AbstractMarketFactoryV2..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "../libraries/IERC20Full.sol";
import "../balancer/BPool.sol";
import "./TurboShareTokenFactory.sol";
import "./FeePot.sol";

abstract contract AbstractMarketFactoryV2 is TurboShareTokenFactoryV1, Ownable {
    using SafeMath for uint256;

    // Should always have ID. Others are optional.
    // event MarketCreated(uint256 id, address settlementAddress, uint256 endTime, ...);

    // Should always have ID. Others are optional.
    // event MarketResolved(uint256 id, ...);

    event SharesMinted(uint256 id, uint256 amount, address receiver);
    event SharesBurned(uint256 id, uint256 amount, address receiver);
    event WinningsClaimed(
        uint256 id,
        address winningOutcome,
        uint256 amount,
        uint256 settlementFee,
        uint256 payout,
        address indexed receiver
    );

    event SettlementFeeClaimed(address settlementAddress, uint256 amount, address indexed receiver);
    event ProtocolFeeClaimed(address protocol, uint256 amount);

    event ProtocolChanged(address protocol);
    event ProtocolFeeChanged(uint256 fee);
    event SettlementFeeChanged(uint256 fee);
    event StakerFeeChanged(uint256 fee);

    IERC20Full public collateral;
    FeePot public feePot;

    // fees are out of 1e18 and only apply to new markets
    uint256 public stakerFee;
    uint256 public settlementFee;
    uint256 public protocolFee;

    address public protocol; // collects protocol fees

    uint256 public accumulatedProtocolFee = 0;
    // settlement address => amount of collateral
    mapping(address => uint256) public accumulatedSettlementFees;

    // How many shares equals one collateral.
    // Necessary to account for math errors from small numbers in balancer.
    // shares = collateral / shareFactor
    // collateral = shares * shareFactor
    uint256 public shareFactor;

    struct Market {
        address settlementAddress;
        OwnedERC20[] shareTokens;
        uint256 endTime;
        OwnedERC20 winner;
        uint256 settlementFee;
        uint256 protocolFee;
        uint256 stakerFee;
        uint256 creationTimestamp;
        uint256[] initialOdds;
    }
    Market[] internal markets;

    uint256 private constant MAX_UINT = 2**256 - 1;

    constructor(
        address _owner,
        IERC20Full _collateral,
        uint256 _shareFactor,
        FeePot _feePot,
        uint256 _stakerFee,
        uint256 _settlementFee,
        address _protocol,
        uint256 _protocolFee
    ) {
        owner = _owner; // controls fees for new markets
        collateral = _collateral;
        shareFactor = _shareFactor;
        feePot = _feePot;
        stakerFee = _stakerFee;
        settlementFee = _settlementFee;
        protocol = _protocol;
        protocolFee = _protocolFee;

        _collateral.approve(address(_feePot), MAX_UINT);

        // First market is always empty so that marketid zero means "no market"
        makeEmptyMarket();
    }

    function makeEmptyMarket() internal {
        string[] memory _noStrings = new string[](0);
        uint256[] memory _noUint256s = new uint256[](0);
        markets.push(makeMarket(address(0), _noStrings, _noStrings, 0, _noUint256s));
    }

    // function createMarket(address _settlementAddress, uint256 _endTime, ...) public returns (uint256);

    function resolveMarket(uint256 _id) public virtual;

    // Returns an empty struct if the market doesn't exist.
    // Can check market existence before calling this by comparing _id against markets.length.
    // Can check market existence of the return struct by checking that shareTokens[0] isn't the null address
    function getMarket(uint256 _id) public view returns (Market memory) {
        if (_id >= markets.length) {
            return Market(address(0), new OwnedERC20[](0), 0, OwnedERC20(address(0)), 0, 0, 0, 0, new uint256[](0));
        } else {
            return markets[_id];
        }
    }

    function marketCount() public view returns (uint256) {
        return markets.length;
    }

    // Returns factory-specific details about a market.
    // function getMarketDetails(uint256 _id) public view returns (MarketDetails memory);

    function mintShares(
        uint256 _id,
        uint256 _shareToMint,
        address _receiver
    ) public {
        require(markets.length > _id, "No such market");
        require(!isMarketResolved(_id), "Cannot mint shares for resolved market");

        uint256 _cost = calcCost(_shareToMint);
        collateral.transferFrom(msg.sender, address(this), _cost);

        Market memory _market = markets[_id];
        for (uint256 _i = 0; _i < _market.shareTokens.length; _i++) {
            _market.shareTokens[_i].trustedMint(_receiver, _shareToMint);
        }

        emit SharesMinted(_id, _shareToMint, _receiver);
    }

    function burnShares(
        uint256 _id,
        uint256 _sharesToBurn,
        address _receiver
    ) public returns (uint256) {
        require(markets.length > _id, "No such market");
        require(!isMarketResolved(_id), "Cannot burn shares for resolved market");

        Market memory _market = markets[_id];
        for (uint256 _i = 0; _i < _market.shareTokens.length; _i++) {
            // errors if sender doesn't have enough shares
            _market.shareTokens[_i].trustedBurn(msg.sender, _sharesToBurn);
        }

        uint256 _payout = calcCost(_sharesToBurn);
        uint256 _protocolFee = _payout.mul(_market.protocolFee).div(10**18);
        uint256 _stakerFee = _payout.mul(_market.stakerFee).div(10**18);
        _payout = _payout.sub(_protocolFee).sub(_stakerFee);

        accumulatedProtocolFee += _protocolFee;
        collateral.transfer(_receiver, _payout);
        feePot.depositFees(_stakerFee);

        emit SharesBurned(_id, _sharesToBurn, msg.sender);
        return _payout;
    }

    function claimWinnings(uint256 _id, address _receiver) public returns (uint256) {
        if (!isMarketResolved(_id)) {
            // errors if market does not exist or is not resolved or resolvable
            resolveMarket(_id);
        }

        Market memory _market = markets[_id];
        uint256 _winningShares = _market.winner.trustedBurnAll(msg.sender);
        _winningShares = (_winningShares / shareFactor) * shareFactor; // remove unusable dust

        uint256 _payout = calcCost(_winningShares); // will fail if there are no winnings to claim
        uint256 _settlementFee = _payout.mul(_market.settlementFee).div(10**18);
        _payout = _payout.sub(_settlementFee);

        accumulatedSettlementFees[_market.settlementAddress] += _settlementFee;
        collateral.transfer(_receiver, _payout);

        emit WinningsClaimed(_id, address(_market.winner), _winningShares, _settlementFee, _payout, _receiver);
        return _payout;
    }

    function claimManyWinnings(uint256[] memory _ids, address _receiver) public returns (uint256) {
        uint256 _totalWinnings = 0;
        for (uint256 i = 0; i < _ids.length; i++) {
            _totalWinnings = _totalWinnings.add(claimWinnings(_ids[i], _receiver));
        }
        return _totalWinnings;
    }

    function claimSettlementFees(address _receiver) public returns (uint256) {
        uint256 _fees = accumulatedSettlementFees[msg.sender];
        if (_fees > 0) {
            accumulatedSettlementFees[msg.sender] = 0;
            collateral.transfer(_receiver, _fees);
            emit SettlementFeeClaimed(msg.sender, _fees, _receiver);
        }
        return _fees;
    }

    function claimProtocolFees() public returns (uint256) {
        require(msg.sender == protocol || msg.sender == address(this), "Only protocol can claim protocol fee");
        uint256 _fees = accumulatedProtocolFee;
        if (_fees > 0) {
            accumulatedProtocolFee = 0;
            collateral.transfer(protocol, _fees);
            emit ProtocolFeeClaimed(protocol, _fees);
        }
        return _fees;
    }

    function setSettlementFee(uint256 _newFee) external onlyOwner {
        settlementFee = _newFee;
        emit SettlementFeeChanged(_newFee);
    }

    function setStakerFee(uint256 _newFee) external onlyOwner {
        stakerFee = _newFee;
        emit StakerFeeChanged(_newFee);
    }

    function setProtocolFee(uint256 _newFee) external onlyOwner {
        protocolFee = _newFee;
        emit ProtocolFeeChanged(_newFee);
    }

    function setProtocol(address _newProtocol, bool _claimFirst) external onlyOwner {
        if (_claimFirst) {
            claimProtocolFees();
        }
        protocol = _newProtocol;
        emit ProtocolChanged(_newProtocol);
    }

    function makeMarket(
        address _settlementAddress,
        string[] memory _names,
        string[] memory _symbols,
        uint256 _endTime,
        uint256[] memory _initialOdds
    ) internal returns (Market memory _market) {
        _market = Market(
            _settlementAddress,
            createShareTokens(_names, _symbols, address(this)),
            _endTime,
            OwnedERC20(address(0)),
            settlementFee,
            protocolFee,
            stakerFee,
            block.timestamp,
            _initialOdds
        );
    }

    function isMarketResolved(uint256 _id) public view returns (bool) {
        Market memory _market = markets[_id];
        return _market.winner != OwnedERC20(address(0));
    }

    // Only usable off-chain. Gas cost can easily eclipse block limit.
    function listUnresolvedMarkets() public view returns (uint256[] memory) {
        uint256 _totalUnresolved = 0;
        for (uint256 i = 0; i < markets.length; i++) {
            if (!isMarketResolved(i)) {
                _totalUnresolved++;
            }
        }

        uint256[] memory _marketIds = new uint256[](_totalUnresolved);

        uint256 n = 0;
        for (uint256 i = 0; i < markets.length; i++) {
            if (n >= _totalUnresolved) break;

            if (!isMarketResolved(i)) {
                _marketIds[n] = i;
                n++;
            }
        }

        return _marketIds;
    }

    // shares => collateral
    function calcCost(uint256 _shares) public view returns (uint256) {
        require(
            _shares >= shareFactor && _shares % shareFactor == 0,
            "Shares must be both greater than (or equal to) and divisible by shareFactor"
        );
        return _shares / shareFactor;
    }

    // collateral => shares
    function calcShares(uint256 _collateralIn) public view returns (uint256) {
        return _collateralIn * shareFactor;
    }

    function onTransferOwnership(address, address) internal override {}
}

contracts/turbo/SportsLinkMarketFactoryV.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "../libraries/IERC20Full.sol";
import "../balancer/BPool.sol";
import "./AbstractMarketFactoryV2.sol";
import "./FeePot.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/math/SignedSafeMath.sol";
import "../libraries/CalculateLinesToBPoolOdds.sol";

contract SportsLinkMarketFactoryV2 is AbstractMarketFactoryV2, CalculateLinesToBPoolOdds {
    using SafeMath for uint256;
    using SignedSafeMath for int256;

    event MarketCreated(
        uint256 id,
        address creator,
        uint256 endTime,
        MarketType marketType,
        uint256 indexed eventId,
        uint256 homeTeamId,
        uint256 awayTeamId,
        uint256 estimatedStartTime,
        int256 score
    );
    event MarketResolved(uint256 id, address winner);
    event LinkNodeChanged(address newLinkNode);

    enum MarketType {HeadToHead, Spread, OverUnder}
    enum HeadToHeadOutcome {
        NoContest, // 0
        Away, // 1
        Home // 2
    }
    enum SpreadOutcome {
        NoContest, // 0
        Away, // 1
        Home // 2
    }
    enum OverUnderOutcome {
        NoContest, // 0
        Over, // 1
        Under // 2
    }
    struct MarketDetails {
        uint256 eventId;
        uint256 homeTeamId;
        uint256 awayTeamId;
        uint256 estimatedStartTime;
        MarketType marketType;
        EventStatus eventStatus;
        // This value depends on the marketType.
        // HeadToHead: ignored
        // Spread: the home team spread
        // OverUnder: total score in game
        int256 value0;
    }
    // MarketId => MarketDetails
    mapping(uint256 => MarketDetails) internal marketDetails;

    enum EventStatus {Unknown, Scheduled, Final, Postponed, Canceled}
    struct EventDetails {
        uint256[3] markets;
        uint256 startTime;
        uint256 homeScore;
        uint256 awayScore;
        EventStatus status;
        // If there is a resolution time then the market is resolved but not necessarily finalized.
        // A market is finalized when its last two score updates were identical.
        // Score updates must occur after a period of time spedcified by resolutionBuffer.
        // This mechanism exists to reduce the risk of bad scores being sent by the API then later corrected.
        // The downside is slower resolution.
        uint256 resolutionTime; // time since last score update
        bool finalized; // true after event resolves and has stable scores
    }
    // EventId => EventDetails
    mapping(uint256 => EventDetails) public events;
    uint256[] public listOfEvents;

    address public linkNode;
    uint256 public sportId;

    constructor(
        address _owner,
        IERC20Full _collateral,
        uint256 _shareFactor,
        FeePot _feePot,
        uint256 _stakerFee,
        uint256 _settlementFee,
        address _protocol,
        uint256 _protocolFee,
        address _linkNode,
        uint256 _sportId
    )
        AbstractMarketFactoryV2(
            _owner,
            _collateral,
            _shareFactor,
            _feePot,
            _stakerFee,
            _settlementFee,
            _protocol,
            _protocolFee
        )
    {
        linkNode = _linkNode;
        sportId = _sportId;
    }

    function createMarket(
        uint256 _eventId,
        uint256 _homeTeamId,
        uint256 _awayTeamId,
        uint256 _startTimestamp,
        int256 _homeSpread,
        uint256 _totalScore,
        bool _makeSpread,
        bool _makeTotalScore,
        int256[2] memory _moneylines // [home,away]
    ) public returns (uint256[3] memory _ids) {
        require(msg.sender == linkNode, "Only link node can create markets");

        address _creator = msg.sender;
        uint256 _endTime = _startTimestamp.add(60 * 8); // 8 hours

        _ids = events[_eventId].markets;

        if (_ids[0] == 0 && _moneylines[0] != 0 && _moneylines[1] != 0) {
            _ids[0] = createHeadToHeadMarket(
                _creator,
                _endTime,
                _eventId,
                _homeTeamId,
                _awayTeamId,
                _startTimestamp,
                _moneylines
            );
        }

        if (_ids[1] == 0 && _makeSpread) {
            // spread market hasn't been created and is ready to be created
            _ids[1] = createSpreadMarket(
                _creator,
                _endTime,
                _eventId,
                _homeTeamId,
                _awayTeamId,
                _startTimestamp,
                _homeSpread
            );
        }

        if (_ids[2] == 0 && _makeTotalScore) {
            // over-under market hasn't been created and is ready to be created
            _ids[2] = createOverUnderMarket(
                _creator,
                _endTime,
                _eventId,
                _homeTeamId,
                _awayTeamId,
                _startTimestamp,
                _totalScore
            );
        }

        events[_eventId].status = EventStatus.Scheduled;
        events[_eventId].startTime = _startTimestamp;
        events[_eventId].markets = _ids;
        listOfEvents.push(_eventId);
    }

    function createHeadToHeadMarket(
        address _creator,
        uint256 _endTime,
        uint256 _eventId,
        uint256 _homeTeamId,
        uint256 _awayTeamId,
        uint256 _startTimestamp,
        int256[2] memory _moneylines // [home,away]
    ) internal returns (uint256) {
        string[] memory _outcomes = new string[](3);
        _outcomes[uint256(HeadToHeadOutcome.NoContest)] = "No Contest";
        _outcomes[uint256(HeadToHeadOutcome.Away)] = "Away";
        _outcomes[uint256(HeadToHeadOutcome.Home)] = "Home";

        uint256 _id = markets.length;
        // moneylines is [home,away] but the outcomes are listed [NC,away,home] so they must be reversed
        markets.push(
            makeMarket(_creator, _outcomes, _outcomes, _endTime, oddsFromLines(_moneylines[1], _moneylines[0]))
        );
        marketDetails[_id] = MarketDetails(
            _eventId,
            _homeTeamId,
            _awayTeamId,
            _startTimestamp,
            MarketType.HeadToHead,
            EventStatus.Scheduled,
            0
        );
        emit MarketCreated(
            _id,
            _creator,
            _endTime,
            MarketType.HeadToHead,
            _eventId,
            _homeTeamId,
            _awayTeamId,
            _startTimestamp,
            0
        );
        return _id;
    }

    function createSpreadMarket(
        address _creator,
        uint256 _endTime,
        uint256 _eventId,
        uint256 _homeTeamId,
        uint256 _awayTeamId,
        uint256 _startTimestamp,
        int256 _homeSpread
    ) internal returns (uint256) {
        string[] memory _outcomes = new string[](3);
        _outcomes[uint256(SpreadOutcome.NoContest)] = "No Contest";
        _outcomes[uint256(SpreadOutcome.Away)] = "Away";
        _outcomes[uint256(SpreadOutcome.Home)] = "Home";

        // The spread is a quantity of tenths. So 55 is 5.5 and -6 is -60.
        // If the spread is a whole number then make it a half point more extreme, to eliminate ties.
        // So 50 becomes 55, -60 becomes -65, and 0 becomes 5.
        if (_homeSpread >= 0 && _homeSpread % 10 == 0) {
            _homeSpread += 5;
        } else if (_homeSpread < 0 && (-_homeSpread) % 10 == 0) {
            _homeSpread -= 5;
        }

        uint256 _id = markets.length;
        markets.push(makeMarket(_creator, _outcomes, _outcomes, _endTime, evenOdds(true, 2)));
        marketDetails[_id] = MarketDetails(
            _eventId,
            _homeTeamId,
            _awayTeamId,
            _startTimestamp,
            MarketType.Spread,
            EventStatus.Scheduled,
            _homeSpread
        );
        emit MarketCreated(
            _id,
            _creator,
            _endTime,
            MarketType.Spread,
            _eventId,
            _homeTeamId,
            _awayTeamId,
            _startTimestamp,
            _homeSpread
        );
        return _id;
    }

    function createOverUnderMarket(
        address _creator,
        uint256 _endTime,
        uint256 _eventId,
        uint256 _homeTeamId,
        uint256 _awayTeamId,
        uint256 _startTimestamp,
        uint256 _overUnderTotal
    ) internal returns (uint256) {
        string[] memory _outcomes = new string[](3);
        _outcomes[uint256(OverUnderOutcome.NoContest)] = "No Contest";
        _outcomes[uint256(OverUnderOutcome.Over)] = "Over";
        _outcomes[uint256(OverUnderOutcome.Under)] = "Under";

        // The total is a quantity of tenths. So 55 is 5.5 and -6 is -60.
        // If the total is a whole number then make it a half point higher, to eliminate ties.
        // So 50 becomes 55 and 0 becomes 5.
        if (_overUnderTotal >= 0 && _overUnderTotal % 10 == 0) {
            _overUnderTotal += 5;
        }

        uint256 _id = markets.length;
        markets.push(makeMarket(_creator, _outcomes, _outcomes, _endTime, evenOdds(true, 2)));
        marketDetails[_id] = MarketDetails(
            _eventId,
            _homeTeamId,
            _awayTeamId,
            _startTimestamp,
            MarketType.OverUnder,
            EventStatus.Scheduled,
            int256(_overUnderTotal)
        );
        emit MarketCreated(
            _id,
            _creator,
            _endTime,
            MarketType.OverUnder,
            _eventId,
            _homeTeamId,
            _awayTeamId,
            _startTimestamp,
            int256(_overUnderTotal)
        );
        return _id;
    }

    function resolveMarket(uint256) public pure override {
        require(false, "Only the link node can resolve the market, using trustedResolveMarkets");
    }

    function trustedResolveMarkets(
        uint256 _eventId,
        uint256 _eventStatus,
        uint256 _homeScore,
        uint256 _awayScore
    ) public {
        require(msg.sender == linkNode, "Only link node can resolve markets");

        EventDetails storage _event = events[_eventId];
        uint256[3] memory _ids = _event.markets;

        require(_ids[0] != 0 || _ids[1] != 0 || _ids[2] != 0, "Cannot resolve markets that weren't created");

        require(EventStatus(_eventStatus) != EventStatus.Scheduled, "cannot resolve SCHEDULED markets");

        // resolve markets as No Contest
        if (EventStatus(_eventStatus) != EventStatus.Final) {
            for (uint256 i = 0; i < _ids.length; i++) {
                uint256 _id = _ids[i];
                if (_id == 0) continue; // skip non-created markets
                OwnedERC20 _winner = markets[_id].shareTokens[0]; // 0th outcome is No Contest for all market types
                markets[_id].winner = _winner;
                emit MarketResolved(_id, address(_winner));
            }
            return;
        }

        // only resolve markets that were created
        if (_ids[0] != 0) {
            resolveHeadToHeadMarket(_ids[0], _homeScore, _awayScore);
        }
        if (_ids[1] != 0) {
            resolveSpreadMarket(_ids[1], _homeScore, _awayScore);
        }
        if (_ids[2] != 0) {
            resolveOverUnderMarket(_ids[2], _homeScore, _awayScore);
        }
    }

    function resolveHeadToHeadMarket(
        uint256 _id,
        uint256 _homeScore,
        uint256 _awayScore
    ) internal {
        OwnedERC20 _winner;
        if (_homeScore > _awayScore) {
            _winner = markets[_id].shareTokens[uint256(HeadToHeadOutcome.Home)]; // home team won
        } else if (_homeScore < _awayScore) {
            _winner = markets[_id].shareTokens[uint256(HeadToHeadOutcome.Away)]; // away team won
        } else {
            _winner = markets[_id].shareTokens[uint256(HeadToHeadOutcome.NoContest)]; // no contest
        }

        markets[_id].winner = _winner;
        emit MarketResolved(_id, address(_winner));
    }

    function resolveSpreadMarket(
        uint256 _id,
        uint256 _homeScore,
        uint256 _awayScore
    ) internal {
        MarketDetails memory _details = marketDetails[_id];
        int256 _targetSpread = _details.value0;

        int256 _actualSpread = int256(_homeScore).sub(int256(_awayScore));

        OwnedERC20 _winner;
        if (_actualSpread > _targetSpread) {
            _winner = markets[_id].shareTokens[uint256(SpreadOutcome.Home)]; // home spread greater
        } else if (_actualSpread < _targetSpread) {
            _winner = markets[_id].shareTokens[uint256(SpreadOutcome.Away)]; // home spread lesser
        } else {
            _winner = markets[_id].shareTokens[uint256(SpreadOutcome.NoContest)]; // no contest
        }

        markets[_id].winner = _winner;
        emit MarketResolved(_id, address(_winner));
    }

    function resolveOverUnderMarket(
        uint256 _id,
        uint256 _homeScore,
        uint256 _awayScore
    ) internal {
        MarketDetails memory _details = marketDetails[_id];
        int256 _targetTotal = _details.value0;

        int256 _actualTotal = int256(_homeScore).add(int256(_awayScore));

        OwnedERC20 _winner;
        if (_actualTotal > _targetTotal) {
            _winner = markets[_id].shareTokens[uint256(OverUnderOutcome.Over)]; // over
        } else if (_actualTotal < _targetTotal) {
            _winner = markets[_id].shareTokens[uint256(OverUnderOutcome.Under)]; // under
        } else {
            _winner = markets[_id].shareTokens[uint256(OverUnderOutcome.NoContest)]; // no contest
        }

        markets[_id].winner = _winner;
        emit MarketResolved(_id, address(_winner));
    }

    function getMarketDetails(uint256 _marketId) public view returns (MarketDetails memory) {
        return marketDetails[_marketId];
    }

    function setLinkNode(address _newLinkNode) external onlyOwner {
        linkNode = _newLinkNode;
        emit LinkNodeChanged(_newLinkNode);
    }

    function getEventMarkets(uint256 _eventId) external view returns (uint256[3] memory) {
        uint256[3] memory _event = events[_eventId].markets;
        return _event;
    }

    // Events can be partially registered, by only creating some markets.
    // This returns true only if an event is fully registered.
    function isEventRegistered(uint256 _eventId) public view returns (bool) {
        uint256[3] memory _event = events[_eventId].markets;
        return _event[0] != 0 && _event[1] != 0 && _event[2] != 0;
    }

    function isEventResolved(uint256 _eventId) public view returns (bool) {
        // check the event's head-to-head market since it will always exist if the event's markets exist
        uint256 _marketId = events[_eventId].markets[0];
        return isMarketResolved(_marketId);
    }

    // Only usable off-chain. Gas cost can easily eclipse block limit.
    // Lists all events that could be resolved with a call to resolveEvent.
    // Not all will be resolvable because this does not ensure the game ended.
    function listResolvableEvents() external view returns (uint256[] memory) {
        uint256 _totalResolvable = countResolvableEvents();
        uint256[] memory _resolvableEvents = new uint256[](_totalResolvable);

        uint256 n = 0;
        for (uint256 i = 0; i < listOfEvents.length; i++) {
            if (n > _totalResolvable) break;
            uint256 _eventId = listOfEvents[i];
            if (isEventResolvable(_eventId)) {
                _resolvableEvents[n] = _eventId;
                n++;
            }
        }

        return _resolvableEvents;
    }

    function countResolvableEvents() internal view returns (uint256) {
        uint256 _totalResolvable = 0;
        for (uint256 i = 0; i < listOfEvents.length; i++) {
            uint256 _eventId = listOfEvents[i];
            if (isEventResolvable(_eventId)) {
                _totalResolvable++;
            }
        }
        return _totalResolvable;
    }

    // Returns true if a call to resolveEvent is potentially useful.
    function isEventResolvable(uint256 _eventId) internal view returns (bool) {
        EventDetails memory _event = events[_eventId];

        bool _unresolved = false; // default because non-existing markets aren't resolvable
        for (uint256 i = 0; i < _event.markets.length; i++) {
            uint256 _marketId = _event.markets[i];
            if (_marketId != 0 && !isMarketResolved(_marketId)) {
                _unresolved = true;
                break;
            }
        }

        return _unresolved;
    }
}

