        emit MarketResolved(_id, address(_winner));
    }

    function getMarketDetails(uint256 _marketId) public view returns (MarketDetails memory) {
        return marketDetails[_marketId];
    }

    function setLinkNode(address _newLinkNode) external onlyOwner {
        linkNode = _newLinkNode;
        emit LinkNodeChanged(_newLinkNode);
    }

    function getEventMarkets(uint256 _eventId) external view returns (uint256[3] memory) {
        uint256[3] memory _event = events[_eventId].markets;
        return _event;
    }

    // Events can be partially registered, by only creating some markets.
    // This returns true only if an event is fully registered.
    function isEventRegistered(uint256 _eventId) public view returns (bool) {
        uint256[3] memory _event = events[_eventId].markets;
        return _event[0] != 0 && _event[1] != 0 && _event[2] != 0;
    }

    function isEventResolved(uint256 _eventId) public view returns (bool) {
        // check the event's head-to-head market since it will always exist if the event's markets exist
        uint256 _marketId = events[_eventId].markets[0];
        return isMarketResolved(_marketId);
    }

    // Only usable off-chain. Gas cost can easily eclipse block limit.
    // Lists all events that could be resolved with a call to resolveEvent.
    // Not all will be resolvable because this does not ensure the game ended.
    function listResolvableEvents() external view returns (uint256[] memory) {
        uint256 _totalResolvable = countResolvableEvents();
        uint256[] memory _resolvableEvents = new uint256[](_totalResolvable);

        uint256 n = 0;
        for (uint256 i = 0; i < listOfEvents.length; i++) {
            if (n > _totalResolvable) break;
            uint256 _eventId = listOfEvents[i];
            if (isEventResolvable(_eventId)) {
                _resolvableEvents[n] = _eventId;
                n++;
            }
        }

        return _resolvableEvents;
    }

    function countResolvableEvents() internal view returns (uint256) {
        uint256 _totalResolvable = 0;
        for (uint256 i = 0; i < listOfEvents.length; i++) {
            uint256 _eventId = listOfEvents[i];
            if (isEventResolvable(_eventId)) {
                _totalResolvable++;
            }
        }
        return _totalResolvable;
    }

    // Returns true if a call to resolveEvent is potentially useful.
    function isEventResolvable(uint256 _eventId) internal view returns (bool) {
        EventDetails memory _event = events[_eventId];

        bool _unresolved = false; // default because non-existing markets aren't resolvable
        for (uint256 i = 0; i < _event.markets.length; i++) {
            uint256 _marketId = _event.markets[i];
            if (_marketId != 0 && !isMarketResolved(_marketId)) {
                _unresolved = true;
                break;
            }
        }

        return _unresolved;
    }
}

contracts/turbo/AbstractMarketFactoryV1..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "../libraries/IERC20Full.sol";
import "../balancer/BPool.sol";
import "./TurboShareTokenFactory.sol";
import "./FeePot.sol";

abstract contract AbstractMarketFactoryV1 is TurboShareTokenFactoryV1, Ownable {
    using SafeMath for uint256;

    // Should always have ID. Others are optional.
    // event MarketCreated(uint256 id, address settlementAddress, uint256 endTime, ...);

    // Should always have ID. Others are optional.
    // event MarketResolved(uint256 id, ...);

    event SharesMinted(uint256 id, uint256 amount, address receiver);
    event SharesBurned(uint256 id, uint256 amount, address receiver);
    event WinningsClaimed(
        uint256 id,
        address winningOutcome,
        uint256 amount,
        uint256 settlementFee,
        uint256 payout,
        address indexed receiver
    );

    event SettlementFeeClaimed(address settlementAddress, uint256 amount, address indexed receiver);
    event ProtocolFeeClaimed(address protocol, uint256 amount);

    event ProtocolChanged(address protocol);
    event ProtocolFeeChanged(uint256 fee);
    event SettlementFeeChanged(uint256 fee);
    event StakerFeeChanged(uint256 fee);

    IERC20Full public collateral;
    FeePot public feePot;

    // fees are out of 1e18 and only apply to new markets
    uint256 public stakerFee;
    uint256 public settlementFee;
    uint256 public protocolFee;

    address public protocol; // collects protocol fees

    uint256 public accumulatedProtocolFee = 0;
    // settlement address => amount of collateral
    mapping(address => uint256) public accumulatedSettlementFees;

    // How many shares equals one collateral.
    // Necessary to account for math errors from small numbers in balancer.
    // shares = collateral / shareFactor
    // collateral = shares * shareFactor
    uint256 public shareFactor;

    struct Market {
        address settlementAddress;
        OwnedERC20[] shareTokens;
        uint256 endTime;
        OwnedERC20 winner;
        uint256 settlementFee;
        uint256 protocolFee;
        uint256 stakerFee;
        uint256 creationTimestamp;
    }
    Market[] internal markets;

    uint256 private constant MAX_UINT = 2**256 - 1;

    constructor(
        address _owner,
        IERC20Full _collateral,
        uint256 _shareFactor,
        FeePot _feePot,
        uint256 _stakerFee,
        uint256 _settlementFee,
        address _protocol,
        uint256 _protocolFee
    ) {
        owner = _owner; // controls fees for new markets
        collateral = _collateral;
        shareFactor = _shareFactor;
        feePot = _feePot;
        stakerFee = _stakerFee;
        settlementFee = _settlementFee;
        protocol = _protocol;
        protocolFee = _protocolFee;

        _collateral.approve(address(_feePot), MAX_UINT);

        // First market is always empty so that marketid zero means "no market"
        string[] memory _nothing = new string[](0);
        markets.push(makeMarket(address(0), _nothing, _nothing, 0));
    }

    // function createMarket(address _settlementAddress, uint256 _endTime, ...) public returns (uint256);

    function resolveMarket(uint256 _id) public virtual;

    // Returns an empty struct if the market doesn't exist.
    // Can check market existence before calling this by comparing _id against markets.length.
    // Can check market existence of the return struct by checking that shareTokens[0] isn't the null address
    function getMarket(uint256 _id) public view returns (Market memory) {
        if (_id >= markets.length) {
            return Market(address(0), new OwnedERC20[](0), 0, OwnedERC20(address(0)), 0, 0, 0, 0);
        } else {
            return markets[_id];
        }
    }

    function marketCount() public view returns (uint256) {
        return markets.length;
    }

    // Returns factory-specific details about a market.
    // function getMarketDetails(uint256 _id) public view returns (MarketDetails memory);

    function mintShares(
        uint256 _id,
        uint256 _shareToMint,
        address _receiver
    ) public {
        require(markets.length > _id, "No such market");
        require(!isMarketResolved(_id), "Cannot mint shares for resolved market");

        uint256 _cost = calcCost(_shareToMint);
        collateral.transferFrom(msg.sender, address(this), _cost);

        Market memory _market = markets[_id];
        for (uint256 _i = 0; _i < _market.shareTokens.length; _i++) {
            _market.shareTokens[_i].trustedMint(_receiver, _shareToMint);
        }

        emit SharesMinted(_id, _shareToMint, _receiver);
    }

    function burnShares(
        uint256 _id,
        uint256 _sharesToBurn,
        address _receiver
    ) public returns (uint256) {
        require(markets.length > _id, "No such market");
        require(!isMarketResolved(_id), "Cannot burn shares for resolved market");

        Market memory _market = markets[_id];
        for (uint256 _i = 0; _i < _market.shareTokens.length; _i++) {
            // errors if sender doesn't have enough shares
            _market.shareTokens[_i].trustedBurn(msg.sender, _sharesToBurn);
        }

        uint256 _payout = calcCost(_sharesToBurn);
        uint256 _protocolFee = _payout.mul(_market.protocolFee).div(10**18);
        uint256 _stakerFee = _payout.mul(_market.stakerFee).div(10**18);
        _payout = _payout.sub(_protocolFee).sub(_stakerFee);

        accumulatedProtocolFee += _protocolFee;
        collateral.transfer(_receiver, _payout);
        feePot.depositFees(_stakerFee);

        emit SharesBurned(_id, _sharesToBurn, msg.sender);
        return _payout;
    }

    function claimWinnings(uint256 _id, address _receiver) public returns (uint256) {
        if (!isMarketResolved(_id)) {
            // errors if market does not exist or is not resolved or resolvable
            resolveMarket(_id);
        }

        Market memory _market = markets[_id];
        uint256 _winningShares = _market.winner.trustedBurnAll(msg.sender);
        _winningShares = (_winningShares / shareFactor) * shareFactor; // remove unusable dust

        uint256 _payout = calcCost(_winningShares); // will fail if there are no winnings to claim
        uint256 _settlementFee = _payout.mul(_market.settlementFee).div(10**18);
        _payout = _payout.sub(_settlementFee);

        accumulatedSettlementFees[_market.settlementAddress] += _settlementFee;
        collateral.transfer(_receiver, _payout);

        emit WinningsClaimed(_id, address(_market.winner), _winningShares, _settlementFee, _payout, _receiver);
        return _payout;
    }

    function claimManyWinnings(uint256[] memory _ids, address _receiver) public returns (uint256) {
        uint256 _totalWinnings = 0;
        for (uint256 i = 0; i < _ids.length; i++) {
            _totalWinnings = _totalWinnings.add(claimWinnings(_ids[i], _receiver));
        }
        return _totalWinnings;
    }

    function claimSettlementFees(address _receiver) public returns (uint256) {
        uint256 _fees = accumulatedSettlementFees[msg.sender];
        if (_fees > 0) {
            accumulatedSettlementFees[msg.sender] = 0;
            collateral.transfer(_receiver, _fees);
            emit SettlementFeeClaimed(msg.sender, _fees, _receiver);
        }
        return _fees;
    }

    function claimProtocolFees() public returns (uint256) {
        require(msg.sender == protocol || msg.sender == address(this), "Only protocol can claim protocol fee");
        uint256 _fees = accumulatedProtocolFee;
        if (_fees > 0) {
            accumulatedProtocolFee = 0;
            collateral.transfer(protocol, _fees);
            emit ProtocolFeeClaimed(protocol, _fees);
        }
        return _fees;
    }

    function setSettlementFee(uint256 _newFee) external onlyOwner {
        settlementFee = _newFee;
        emit SettlementFeeChanged(_newFee);
    }

    function setStakerFee(uint256 _newFee) external onlyOwner {
        stakerFee = _newFee;
        emit StakerFeeChanged(_newFee);
    }

    function setProtocolFee(uint256 _newFee) external onlyOwner {
        protocolFee = _newFee;
        emit ProtocolFeeChanged(_newFee);
    }

    function setProtocol(address _newProtocol, bool _claimFirst) external onlyOwner {
        if (_claimFirst) {
            claimProtocolFees();
        }
        protocol = _newProtocol;
        emit ProtocolChanged(_newProtocol);
    }

    function makeMarket(
        address _settlementAddress,
        string[] memory _names,
        string[] memory _symbols,
        uint256 _endTime
    ) internal returns (Market memory _market) {
        _market = Market(
            _settlementAddress,
            createShareTokens(_names, _symbols, address(this)),
            _endTime,
            OwnedERC20(address(0)),
            settlementFee,
            protocolFee,
            stakerFee,
            block.timestamp
        );
    }

    function isMarketResolved(uint256 _id) public view returns (bool) {
        Market memory _market = markets[_id];
        return _market.winner != OwnedERC20(address(0));
    }

    // Only usable off-chain. Gas cost can easily eclipse block limit.
    function listUnresolvedMarkets() public view returns (uint256[] memory) {
        uint256 _totalUnresolved = 0;
        for (uint256 i = 0; i < markets.length; i++) {
            if (!isMarketResolved(i)) {
                _totalUnresolved++;
            }
        }

        uint256[] memory _marketIds = new uint256[](_totalUnresolved);

        uint256 n = 0;
        for (uint256 i = 0; i < markets.length; i++) {
            if (n >= _totalUnresolved) break;

            if (!isMarketResolved(i)) {
                _marketIds[n] = i;
                n++;
            }
        }

        return _marketIds;
    }

    // shares => collateral
    function calcCost(uint256 _shares) public view returns (uint256) {
        require(
            _shares >= shareFactor && _shares % shareFactor == 0,
            "Shares must be both greater than (or equal to) and divisible by shareFactor"
        );
        return _shares / shareFactor;
    }

    // collateral => shares
    function calcShares(uint256 _collateralIn) public view returns (uint256) {
        return _collateralIn * shareFactor;
    }

    function onTransferOwnership(address, address) internal override {}
}

contracts/turbo/NFLMarketFactoryV2.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "../libraries/IERC20Full.sol";
import "../balancer/BPool.sol";
import "./AbstractMarketFactoryV2.sol";
import "./FeePot.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/math/SignedSafeMath.sol";
import "../libraries/CalculateLinesToBPoolOdds.sol";

contract NFLMarketFactoryV2 is AbstractMarketFactoryV2, CalculateLinesToBPoolOdds {
    using SafeMath for uint256;
    using SignedSafeMath for int256;

    event MarketCreated(
        uint256 id,
        address creator,
        uint256 endTime,
        MarketType marketType,
        uint256 indexed eventId,
        string homeTeamName,
        uint256 homeTeamId,
        string awayTeamName,
        uint256 awayTeamId,
        uint256 estimatedStartTime,
        int256 score
    );
    event MarketResolved(uint256 id, address winner);
    event LinkNodeChanged(address newLinkNode);

    enum MarketType {HeadToHead, Spread, OverUnder}
    enum HeadToHeadOutcome {
        NoContest, // 0
        Away, // 1
        Home // 2
    }
    enum SpreadOutcome {
        NoContest, // 0
        Away, // 1
        Home // 2
    }
    enum OverUnderOutcome {
        NoContest, // 0
        Over, // 1
        Under // 2
    }
    struct MarketDetails {
        uint256 eventId;
        string homeTeamName;
        uint256 homeTeamId;
        string awayTeamName;
        uint256 awayTeamId;
        uint256 estimatedStartTime;
        MarketType marketType;
        EventStatus eventStatus;
        // This value depends on the marketType.
        // HeadToHead: ignored
        // Spread: the home team spread
        // OverUnder: total score in game
        int256 value0;
    }
    // MarketId => MarketDetails
    mapping(uint256 => MarketDetails) internal marketDetails;

    enum EventStatus {Unknown, Scheduled, Final, Postponed, Canceled}
    struct EventDetails {
        uint256[3] markets;
        uint256 startTime;
        uint256 homeScore;
        uint256 awayScore;
        EventStatus status;
        // If there is a resolution time then the market is resolved but not necessarily finalized.
        // A market is finalized when its last two score updates were identical.
        // Score updates must occur after a period of time specified by resolutionBuffer.
        // This mechanism exists to reduce the risk of bad scores being sent by the API then later corrected.
        // The downside is slower resolution.
        uint256 resolutionTime; // time since last score update
        bool finalized; // true after event resolves and has stable scores
    }
    // EventId => EventDetails
    mapping(uint256 => EventDetails) public events;
    uint256[] public listOfEvents;

    address public linkNode;
    uint256 public sportId;

    constructor(
        address _owner,
        IERC20Full _collateral,
        uint256 _shareFactor,
        FeePot _feePot,
        uint256 _stakerFee,
        uint256 _settlementFee,
        address _protocol,
        uint256 _protocolFee,
        address _linkNode,
        uint256 _sportId
    )
        AbstractMarketFactoryV2(
            _owner,
            _collateral,
            _shareFactor,
            _feePot,
            _stakerFee,
            _settlementFee,
            _protocol,
            _protocolFee
        )
    {
        linkNode = _linkNode;
        sportId = _sportId;
    }

    function createMarket(
        uint256 _eventId,
        string memory _homeTeamName,
        uint256 _homeTeamId,
        string memory _awayTeamName,
        uint256 _awayTeamId,
        uint256 _startTimestamp,
        int256 _homeSpread,
        uint256 _totalScore,
        bool _makeSpread,
        bool _makeTotalScore,
        int256[2] memory _moneylines // [home,away]
    ) public returns (uint256[3] memory _ids) {
        require(msg.sender == linkNode, "Only link node can create markets");

        uint256 _endTime = _startTimestamp.add(60 * 8); // 8 hours

        _ids = events[_eventId].markets;

        if (_ids[0] == 0 && _moneylines[0] != 0 && _moneylines[1] != 0) {
            _ids[0] = createHeadToHeadMarket(
                msg.sender,
                _endTime,
                _eventId,
                _homeTeamName,
                _homeTeamId,
                _awayTeamName,
                _awayTeamId,
                _startTimestamp,
                _moneylines
            );
        }

        if (_ids[1] == 0 && _makeSpread) {
            // spread market hasn't been created and is ready to be created
            _ids[1] = createSpreadMarket(
                msg.sender,
                _endTime,
                _eventId,
                _homeTeamName,
                _homeTeamId,
                _awayTeamName,
                _awayTeamId,
                _startTimestamp,
                _homeSpread
            );
        }

        if (_ids[2] == 0 && _makeTotalScore) {
            // over-under market hasn't been created and is ready to be created
            _ids[2] = createOverUnderMarket(
                msg.sender,
                _endTime,
                _eventId,
                _homeTeamName,
                _homeTeamId,
                _awayTeamName,
                _awayTeamId,
                _startTimestamp,
                _totalScore
            );
        }

        events[_eventId].status = EventStatus.Scheduled;
        events[_eventId].startTime = _startTimestamp;
        events[_eventId].markets = _ids;
        listOfEvents.push(_eventId);
    }

    function createHeadToHeadMarket(
        address _creator,
        uint256 _endTime,
        uint256 _eventId,
        string memory _homeTeamName,
        uint256 _homeTeamId,
        string memory _awayTeamName,
        uint256 _awayTeamId,
        uint256 _startTimestamp,
        int256[2] memory _moneylines // [home,away]
    ) internal returns (uint256) {
        string[] memory _outcomes = new string[](3);
        _outcomes[uint256(HeadToHeadOutcome.NoContest)] = "No Contest / Draw";
        _outcomes[uint256(HeadToHeadOutcome.Away)] = _awayTeamName;
        _outcomes[uint256(HeadToHeadOutcome.Home)] = _homeTeamName;

        uint256 _id = markets.length;
        // moneylines is [home,away] but the outcomes are listed [NC,away,home] so they must be reversed
        markets.push(
            makeMarket(_creator, _outcomes, _outcomes, _endTime, oddsFromLines(_moneylines[1], _moneylines[0]))
        );
        marketDetails[_id] = MarketDetails(
            _eventId,
            _homeTeamName,
            _homeTeamId,
            _awayTeamName,
            _awayTeamId,
            _startTimestamp,
            MarketType.HeadToHead,
            EventStatus.Scheduled,
            0
        );
        emit MarketCreated(
            _id,
            _creator,
            _endTime,
            MarketType.HeadToHead,
            _eventId,
            _homeTeamName,
            _homeTeamId,
            _awayTeamName,
            _awayTeamId,
            _startTimestamp,
            0
        );
        return _id;
    }

    function createSpreadMarket(
        address _creator,
        uint256 _endTime,
        uint256 _eventId,
        string memory _homeTeamName,
        uint256 _homeTeamId,
        string memory _awayTeamName,
        uint256 _awayTeamId,
        uint256 _startTimestamp,
        int256 _homeSpread
    ) internal returns (uint256) {
        string[] memory _outcomes = new string[](3);
        _outcomes[uint256(SpreadOutcome.NoContest)] = "No Contest";
        _outcomes[uint256(SpreadOutcome.Away)] = _awayTeamName;
        _outcomes[uint256(SpreadOutcome.Home)] = _homeTeamName;

        // The spread is a quantity of tenths. So 55 is 5.5 and -6 is -60.
        // If the spread is a whole number then make it a half point more extreme, to eliminate ties.
        // So 50 becomes 55, -60 becomes -65, and 0 becomes 5.
        if (_homeSpread >= 0 && _homeSpread % 10 == 0) {
            _homeSpread += 5;
        } else if (_homeSpread < 0 && (-_homeSpread) % 10 == 0) {
            _homeSpread -= 5;
        }

        uint256 _id = markets.length;
        markets.push(makeMarket(_creator, _outcomes, _outcomes, _endTime, evenOdds(true, 2)));
        marketDetails[_id] = MarketDetails(
            _eventId,
            _homeTeamName,
            _homeTeamId,
            _awayTeamName,
            _awayTeamId,
            _startTimestamp,
            MarketType.Spread,
            EventStatus.Scheduled,
            _homeSpread
        );
        emit MarketCreated(
            _id,
            _creator,
            _endTime,
            MarketType.Spread,
            _eventId,
            _homeTeamName,
            _homeTeamId,
            _awayTeamName,
            _awayTeamId,
            _startTimestamp,
            _homeSpread
        );
        return _id;
    }

    function createOverUnderMarket(
        address _creator,
        uint256 _endTime,
        uint256 _eventId,
        string memory _homeTeamName,
        uint256 _homeTeamId,
        string memory _awayTeamName,
        uint256 _awayTeamId,
        uint256 _startTimestamp,
        uint256 _overUnderTotal
    ) internal returns (uint256) {
        string[] memory _outcomes = new string[](3);
        _outcomes[uint256(OverUnderOutcome.NoContest)] = "No Contest";
        _outcomes[uint256(OverUnderOutcome.Over)] = "Over";
        _outcomes[uint256(OverUnderOutcome.Under)] = "Under";

        // The total is a quantity of tenths. So 55 is 5.5 and -6 is -60.
        // If the total is a whole number then make it a half point higher, to eliminate ties.
        // So 50 becomes 55 and 0 becomes 5.
        if (_overUnderTotal >= 0 && _overUnderTotal % 10 == 0) {
            _overUnderTotal += 5;
        }

        uint256 _id = markets.length;
        markets.push(makeMarket(_creator, _outcomes, _outcomes, _endTime, evenOdds(true, 2)));
        marketDetails[_id] = MarketDetails(
            _eventId,
            _homeTeamName,
            _homeTeamId,
            _awayTeamName,
            _awayTeamId,
            _startTimestamp,
            MarketType.OverUnder,
            EventStatus.Scheduled,
            int256(_overUnderTotal)
        );
        emit MarketCreated(
            _id,
            _creator,
            _endTime,
            MarketType.OverUnder,
            _eventId,
            _homeTeamName,
            _homeTeamId,
            _awayTeamName,
            _awayTeamId,
            _startTimestamp,
            int256(_overUnderTotal)
        );
        return _id;
    }

    function resolveMarket(uint256) public pure override {
        require(false, "Only the link node can resolve the market, using trustedResolveMarkets");
    }

    function trustedResolveMarkets(
        uint256 _eventId,
        uint256 _eventStatus,
        uint256 _homeScore,
        uint256 _awayScore
    ) public {
        require(msg.sender == linkNode, "Only link node can resolve markets");

        EventDetails storage _event = events[_eventId];
        uint256[3] memory _ids = _event.markets;

        require(_ids[0] != 0 || _ids[1] != 0 || _ids[2] != 0, "Cannot resolve markets that weren't created");

        require(EventStatus(_eventStatus) != EventStatus.Scheduled, "cannot resolve SCHEDULED markets");

        // resolve markets as No Contest
        if (EventStatus(_eventStatus) != EventStatus.Final) {
            for (uint256 i = 0; i < _ids.length; i++) {
                uint256 _id = _ids[i];
                if (_id == 0) continue; // skip non-created markets
                OwnedERC20 _winner = markets[_id].shareTokens[0]; // 0th outcome is No Contest for all market types
                markets[_id].winner = _winner;
                emit MarketResolved(_id, address(_winner));
            }
            return;
        }

        // only resolve markets that were created
        if (_ids[0] != 0) {
            resolveHeadToHeadMarket(_ids[0], _homeScore, _awayScore);
        }
        if (_ids[1] != 0) {
            resolveSpreadMarket(_ids[1], _homeScore, _awayScore);
        }
        if (_ids[2] != 0) {
            resolveOverUnderMarket(_ids[2], _homeScore, _awayScore);
        }
    }

    function resolveHeadToHeadMarket(
        uint256 _id,
        uint256 _homeScore,
        uint256 _awayScore
    ) internal {
        OwnedERC20 _winner;
        if (_homeScore > _awayScore) {
            _winner = markets[_id].shareTokens[uint256(HeadToHeadOutcome.Home)]; // home team won
        } else if (_homeScore < _awayScore) {
            _winner = markets[_id].shareTokens[uint256(HeadToHeadOutcome.Away)]; // away team won
        } else {
            _winner = markets[_id].shareTokens[uint256(HeadToHeadOutcome.NoContest)]; // no contest
        }

        markets[_id].winner = _winner;
        emit MarketResolved(_id, address(_winner));
    }

    function resolveSpreadMarket(
        uint256 _id,
        uint256 _homeScore,
        uint256 _awayScore
    ) internal {
        MarketDetails memory _details = marketDetails[_id];
        int256 _targetSpread = _details.value0;

        int256 _actualSpread = int256(_homeScore).sub(int256(_awayScore));

        OwnedERC20 _winner;
        if (_actualSpread > _targetSpread) {
            _winner = markets[_id].shareTokens[uint256(SpreadOutcome.Home)]; // home spread greater
        } else if (_actualSpread < _targetSpread) {
            _winner = markets[_id].shareTokens[uint256(SpreadOutcome.Away)]; // home spread lesser
        } else {
            _winner = markets[_id].shareTokens[uint256(SpreadOutcome.NoContest)]; // no contest
        }

        markets[_id].winner = _winner;
        emit MarketResolved(_id, address(_winner));
    }

    function resolveOverUnderMarket(
        uint256 _id,
        uint256 _homeScore,
        uint256 _awayScore
    ) internal {
        MarketDetails memory _details = marketDetails[_id];
        int256 _targetTotal = _details.value0;

        int256 _actualTotal = int256(_homeScore).add(int256(_awayScore));

        OwnedERC20 _winner;
        if (_actualTotal > _targetTotal) {
            _winner = markets[_id].shareTokens[uint256(OverUnderOutcome.Over)]; // over
        } else if (_actualTotal < _targetTotal) {
            _winner = markets[_id].shareTokens[uint256(OverUnderOutcome.Under)]; // under
        } else {
            _winner = markets[_id].shareTokens[uint256(OverUnderOutcome.NoContest)]; // no contest
        }

        markets[_id].winner = _winner;
        emit MarketResolved(_id, address(_winner));
    }

    function getMarketDetails(uint256 _marketId) public view returns (MarketDetails memory) {
        return marketDetails[_marketId];
    }

    function setLinkNode(address _newLinkNode) external onlyOwner {
        linkNode = _newLinkNode;
        emit LinkNodeChanged(_newLinkNode);
    }

    function getEventMarkets(uint256 _eventId) external view returns (uint256[3] memory) {
        uint256[3] memory _event = events[_eventId].markets;
        return _event;
    }

    // Events can be partially registered, by only creating some markets.
    // This returns true only if an event is fully registered.
    function isEventRegistered(uint256 _eventId) public view returns (bool) {
        uint256[3] memory _event = events[_eventId].markets;
        return _event[0] != 0 && _event[1] != 0 && _event[2] != 0;
    }

    function isEventResolved(uint256 _eventId) public view returns (bool) {
        // check the event's head-to-head market since it will always exist if the event's markets exist
        uint256 _marketId = events[_eventId].markets[0];
        return isMarketResolved(_marketId);
    }

    // Only usable off-chain. Gas cost can easily eclipse block limit.
    // Lists all events that could be resolved with a call to resolveEvent.
    // Not all will be resolvable because this does not ensure the game ended.
    function listResolvableEvents() external view returns (uint256[] memory) {
        uint256 _totalResolvable = countResolvableEvents();
        uint256[] memory _resolvableEvents = new uint256[](_totalResolvable);

        uint256 n = 0;
        for (uint256 i = 0; i < listOfEvents.length; i++) {
            if (n > _totalResolvable) break;
            uint256 _eventId = listOfEvents[i];
            if (isEventResolvable(_eventId)) {
                _resolvableEvents[n] = _eventId;
                n++;
            }
        }

        return _resolvableEvents;
    }

    function countResolvableEvents() internal view returns (uint256) {
        uint256 _totalResolvable = 0;
        for (uint256 i = 0; i < listOfEvents.length; i++) {
            uint256 _eventId = listOfEvents[i];
            if (isEventResolvable(_eventId)) {
                _totalResolvable++;
            }
        }
        return _totalResolvable;
    }

    // Returns true if a call to resolveEvent is potentially useful.
    function isEventResolvable(uint256 _eventId) internal view returns (bool) {
        EventDetails memory _event = events[_eventId];

        bool _unresolved = false; // default because non-existing markets aren't resolvable
        for (uint256 i = 0; i < _event.markets.length; i++) {
            uint256 _marketId = _event.markets[i];
            if (_marketId != 0 && !isMarketResolved(_marketId)) {
                _unresolved = true;
                break;
            }
        }

        return _unresolved;
    }
}

contracts/turbo/MMALinkMarketFactoryV2.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "../libraries/IERC20Full.sol";
import "../balancer/BPool.sol";
import "./AbstractMarketFactoryV2.sol";
import "./FeePot.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/math/SignedSafeMath.sol";
import "../libraries/CalculateLinesToBPoolOdds.sol";

contract MMALinkMarketFactoryV2 is AbstractMarketFactoryV2, CalculateLinesToBPoolOdds {
    using SafeMath for uint256;
    using SignedSafeMath for int256;

    event MarketCreated(
        uint256 id,
        address creator,
        uint256 endTime,
        MarketType marketType,
        uint256 indexed eventId,
        string homeFighterName,
        uint256 homeFighterId,
        string awayFighterName,
        uint256 awayFighterId,
        uint256 estimatedStartTime
    );
    event MarketResolved(uint256 id, address winner);
    event LinkNodeChanged(address newLinkNode);

    enum MarketType {HeadToHead}
    enum HeadToHeadOutcome {
        NoContest, // 0
        Away, // 1
        Home // 2
    }

    struct MarketDetails {
        uint256 eventId;
        string homeFighterName;
        uint256 homeFighterId;
        string awayFighterName;
        uint256 awayFighterId;
        uint256 estimatedStartTime;
        MarketType marketType;
        EventStatus eventStatus;
    }
    // MarketId => MarketDetails
    mapping(uint256 => MarketDetails) internal marketDetails;

    enum EventStatus {Unknown, Scheduled, Final, Postponed, Canceled}
    struct EventDetails {
        uint256[1] markets;
        uint256 homeFighterId;
        uint256 awayFighterId;
        uint256 startTime;
        EventStatus eventStatus;
    }

    // EventId => EventDetails
    mapping(uint256 => EventDetails) public events;
    uint256[] public listOfEvents;

    address public linkNode;
    uint256 public sportId;

    constructor(
        address _owner,
        IERC20Full _collateral,
        uint256 _shareFactor,
        FeePot _feePot,
        uint256 _stakerFee,
        uint256 _settlementFee,
        address _protocol,
        uint256 _protocolFee,
        address _linkNode,
        uint256 _sportId
    )
        AbstractMarketFactoryV2(
            _owner,
            _collateral,
            _shareFactor,
            _feePot,
            _stakerFee,
            _settlementFee,
            _protocol,
            _protocolFee
        )
    {
        linkNode = _linkNode;
        sportId = _sportId;
    }

    function createMarket(
        uint256 _eventId,
        string memory _homeFighterName,
        uint256 _homeFighterId,
        string memory _awayFighterName,
        uint256 _awayFighterId,
        uint256 _startTimestamp,
        int256[2] memory _moneylines // [home,away]
    ) public {
        require(msg.sender == linkNode, "Only link node can create markets");

        address _creator = msg.sender;
        uint256 _endTime = _startTimestamp.add(60 * 8); // 8 hours

        uint256[1] memory _ids = events[_eventId].markets;
        // require(_ids[0] == 0, "This event was already used to create markets");

        _ids[0] = createHeadToHeadMarket(
            _creator,
            _endTime,
            _eventId,
            _homeFighterName,
            _homeFighterId,
            _awayFighterName,
            _awayFighterId,
            _startTimestamp,
            _moneylines
        );

        events[_eventId].markets = _ids;
        events[_eventId].homeFighterId = _homeFighterId;
        events[_eventId].awayFighterId = _awayFighterId;
        events[_eventId].startTime = _startTimestamp;
        events[_eventId].eventStatus = EventStatus.Scheduled;
        listOfEvents.push(_eventId);
    }

    function createHeadToHeadMarket(
        address _creator,
        uint256 _endTime,
        uint256 _eventId,
        string memory _homeTeamName,
        uint256 _homeFighterId,
        string memory _awayTeamName,
        uint256 _awayFighterId,
        uint256 _startTimestamp,
        int256[2] memory _moneylines // [home,away]
    ) internal returns (uint256) {
        string[] memory _outcomes = new string[](3);
        _outcomes[uint256(HeadToHeadOutcome.NoContest)] = "No Contest";
        _outcomes[uint256(HeadToHeadOutcome.Away)] = _awayTeamName;
        _outcomes[uint256(HeadToHeadOutcome.Home)] = _homeTeamName;

        uint256 _id = markets.length;
        // moneylines is [home,away] but the outcomes are listed [NC,away,home] so they must be reversed
        markets.push(
            makeMarket(_creator, _outcomes, _outcomes, _endTime, oddsFromLines(_moneylines[1], _moneylines[0]))
        );
        marketDetails[_id] = MarketDetails(
            _eventId,
            _homeTeamName,
            _homeFighterId,
            _awayTeamName,
            _awayFighterId,
            _startTimestamp,
            MarketType.HeadToHead,
            EventStatus.Scheduled
        );
        emit MarketCreated(
            _id,
            _creator,
            _endTime,
            MarketType.HeadToHead,
            _eventId,
            _homeTeamName,
            _homeFighterId,
            _awayTeamName,
            _awayFighterId,
            _startTimestamp
        );
        return _id;
    }

    enum WhoWon {Unknown, Home, Away, Draw}

    function trustedResolveMarkets(
        uint256 _eventId,
        EventStatus _eventStatus,
        uint256 _homeFighterId,
        uint256 _awayFighterId,
        WhoWon _whoWon
    ) public {
        require(msg.sender == linkNode, "Only link node can resolve markets");

        EventDetails memory _event = events[_eventId];
        require(_event.markets[0] != 0, "Cannot resolve markets that weren't created");
        require(EventStatus(_eventStatus) != EventStatus.Scheduled, "Cannot resolve SCHEDULED markets");

        if (eventIsNoContest(_event, _eventStatus, _homeFighterId, _awayFighterId, _whoWon)) {
            resolveMarketsAsNoContest(_eventId);
        } else {
            resolveHeadToHeadMarket(_event.markets[0], _whoWon);
        }

        events[_eventId].eventStatus = _eventStatus;
    }

    function eventIsNoContest(
        EventDetails memory _event,
        EventStatus _eventStatus,
        uint256 _homeFighterId,
        uint256 _awayFighterId,
        WhoWon _whoWon
    ) internal pure returns (bool) {
        bool _draw = _whoWon == WhoWon.Draw;
        bool _notFinal = _eventStatus != EventStatus.Final;
        bool _unstableHomeFighterId = _event.homeFighterId != _homeFighterId;
        bool _unstableAwayFighterId = _event.awayFighterId != _awayFighterId;
        return _draw || _notFinal || _unstableHomeFighterId || _unstableAwayFighterId;
    }

    function resolveMarketsAsNoContest(uint256 _eventId) internal {
        uint256[1] memory _marketIds = events[_eventId].markets;
        for (uint256 i = 0; i < _marketIds.length; i++) {
            uint256 _marketId = _marketIds[i];
            if (_marketId == 0) continue; // skip non-created markets
            OwnedERC20 _winner = markets[_marketId].shareTokens[0]; // 0th outcome is No Contest for all market types
            markets[_marketId].winner = _winner;
            emit MarketResolved(_marketId, address(_winner));
        }
    }

    function resolveHeadToHeadMarket(uint256 _marketId, WhoWon _whoWon) internal {
        OwnedERC20 _winner;
        if (WhoWon.Home == _whoWon) {
            _winner = markets[_marketId].shareTokens[uint256(HeadToHeadOutcome.Home)];
        } else if (WhoWon.Away == _whoWon) {
            _winner = markets[_marketId].shareTokens[uint256(HeadToHeadOutcome.Away)];
        } else {
            require(false, "Bad market resolution choice");
        }

        markets[_marketId].winner = _winner;
        emit MarketResolved(_marketId, address(_winner));
    }

    function getMarketDetails(uint256 _marketId) public view returns (MarketDetails memory) {
        return marketDetails[_marketId];
    }

    // Only usable off-chain. Gas cost can easily eclipse block limit.
    // Lists all events that could be resolved with a call to resolveEvent.
    // Not all will be resolvable because this does not ensure the game ended.
    function listResolvableEvents() external view returns (uint256[] memory) {
        uint256 _totalResolvable = countResolvableEvents();
        uint256[] memory _resolvableEvents = new uint256[](_totalResolvable);

        uint256 n = 0;
        for (uint256 i = 0; i < listOfEvents.length; i++) {
            if (n > _totalResolvable) break;
            uint256 _eventId = listOfEvents[i];
            if (isEventResolvable(_eventId)) {
                _resolvableEvents[n] = _eventId;
                n++;
            }
        }

        return _resolvableEvents;
    }

    function countResolvableEvents() internal view returns (uint256) {
        uint256 _totalResolvable = 0;
        for (uint256 i = 0; i < listOfEvents.length; i++) {
            uint256 _eventId = listOfEvents[i];
            if (isEventResolvable(_eventId)) {
                _totalResolvable++;
            }
        }
        return _totalResolvable;
    }

    // Returns true if a call to resolveEvent is potentially useful.
    function isEventResolvable(uint256 _eventId) internal view returns (bool) {
        EventDetails memory _event = events[_eventId];

        bool _unresolved = false; // default because non-existing markets aren't resolvable
        for (uint256 i = 0; i < _event.markets.length; i++) {
            uint256 _marketId = _event.markets[i];
            if (_marketId != 0 && !isMarketResolved(_marketId)) {
                _unresolved = true;
                break;
            }
        }

        return _unresolved;
    }

    function getEvent(uint256 _eventId) external view returns (EventDetails memory _event) {
        _event = events[_eventId];
    }

    function setLinkNode(address _newLinkNode) external onlyOwner {
        linkNode = _newLinkNode;
        emit LinkNodeChanged(_newLinkNode);
    }

    function resolveMarket(uint256) public pure override {
        require(false, "Only the link node can resolve the market, using trustedResolveMarkets");
    }
}

contracts/turbo/MLBMarketFactoryV3.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "../libraries/IERC20Full.sol";
import "../balancer/BPool.sol";
import "./AbstractMarketFactoryV3.sol";
import "./FeePot.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/math/SignedSafeMath.sol";
import "../libraries/Sport.sol";
import "../libraries/HasHeadToHeadMarket.sol";
import "../libraries/ResolveByScore.sol";
import "../libraries/Versioned.sol";

contract MLBMarketFactoryV3 is AbstractMarketFactoryV3, SportView, HasHeadToHeadMarket, ResolvesByScore, Versioned {
    using SafeMath for uint256;
    using SignedSafeMath for int256;

    uint256 constant HeadToHead = 0;
    string constant InvalidName = "No Contest";

    constructor(
        address _owner,
        IERC20Full _collateral,
        uint256 _shareFactor,
        FeePot _feePot,
        uint256[3] memory _fees,
        address _protocol,
        address _linkNode
    )
        AbstractMarketFactoryV3(_owner, _collateral, _shareFactor, _feePot, _fees, _protocol)
        Versioned("v1.4.0")
        ManagedByLink(_linkNode)
        HasHeadToHeadMarket(HeadToHead, InvalidName)
    {}

    function createEvent(
        uint256 _eventId,
        string memory _homeTeamName,
        uint256 _homeTeamId,
        string memory _awayTeamName,
        uint256 _awayTeamId,
        uint256 _startTimestamp,
        int256[2] memory _moneylines // [home,away]
    ) public onlyLinkNode returns (uint256[] memory _marketIds) {
        _marketIds = makeMarkets(_moneylines, _homeTeamName, _awayTeamName);
        makeSportsEvent(
            _eventId,
            _marketIds,
            build1Line(),
            _startTimestamp,
            _homeTeamId,
            _awayTeamId,
            _homeTeamName,
            _awayTeamName
        );
    }

    function makeMarkets(
        int256[2] memory _moneylines,
        string memory _homeTeamName,
        string memory _awayTeamName
    ) internal returns (uint256[] memory _marketIds) {
        _marketIds = new uint256[](1);
        _marketIds[HeadToHead] = makeHeadToHeadMarket(_moneylines, _homeTeamName, _awayTeamName);
    }

    function resolveValidEvent(
        SportsEvent memory _event,
        uint256 _homeScore,
        uint256 _awayScore
    ) internal override {
        resolveHeadToHeadMarket(_event.markets[HeadToHead], _homeScore, _awayScore);
    }
}

contracts/turbo/CryptoMarketFactoryV2.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "../libraries/IERC20Full.sol";
import "../balancer/BPool.sol";
import "./AbstractMarketFactoryV2.sol";
import "./FeePot.sol";
import "@openzeppelin/contracts/utils/math/SignedSafeMath.sol";

import "../utils/AggregatorV3Interface.sol";
import "../libraries/CalculateLinesToBPoolOdds.sol";

contract CryptoMarketFactoryV2 is AbstractMarketFactoryV2, CalculateLinesToBPoolOdds {
    using SafeMath for uint256;
    using SignedSafeMath for int256;

    event MarketCreated(
        uint256 id,
        address creator,
        uint256 indexed endTime,
        MarketType marketType,
        uint256 indexed coinIndex,
        uint256 price
    );
    event MarketResolved(uint256 id, address winner);

    struct Coin {
        string name;
        AggregatorV3Interface priceFeed;
        uint256 price;
        uint8 imprecision; // how many decimals to truncate
        uint256[1] currentMarkets;
    }
    Coin[] public coins;

    enum MarketType {
        PriceUpDown // 0
    }
    enum PriceUpDownOutcome {
        Above, // 0
        NotAbove // 1
    }
    struct MarketDetails {
        MarketType marketType;
        uint256 coinIndex;
        uint256 creationPrice;
        uint256 resolutionPrice;
    }
    // MarketId => MarketDetails
    mapping(uint256 => MarketDetails) internal marketDetails;

    address public linkNode; // market creator and resolver

    uint256 public nextResolutionTime;

    constructor(
        address _owner,
        IERC20Full _collateral,
        uint256 _shareFactor,
        FeePot _feePot,
        uint256 _stakerFee,
        uint256 _settlementFee,
        address _protocol,
        uint256 _protocolFee,
        address _linkNode
    )
        AbstractMarketFactoryV2(
            _owner,
            _collateral,
            _shareFactor,
            _feePot,
            _stakerFee,
            _settlementFee,
            _protocol,
            _protocolFee
        )
    {
        linkNode = _linkNode;

        string memory _name = "";
        coins.push(makeCoin(_name, AggregatorV3Interface(address(0)), 0));
    }

    function getMarketDetails(uint256 _marketId) public view returns (MarketDetails memory) {
        return marketDetails[_marketId];
    }

    // NOTE: Trusts the owner not to add a coin twice.
    // Returns the coin index.
    function addCoin(
        string calldata _name,
        AggregatorV3Interface _priceFeed,
        uint8 _imprecision
    ) external onlyOwner returns (uint256 _coinIndex) {
        Coin memory _coin = makeCoin(_name, _priceFeed, _imprecision);
        _coinIndex = coins.length;
        coins.push(_coin);
    }

    function getCoin(uint256 _coinIndex) public view returns (Coin memory _coin) {
        _coin = coins[_coinIndex];
    }

    function getCoins() public view returns (Coin[] memory _coins) {
        _coins = new Coin[](coins.length);
        // Skip first coin because it's always the zeroed-out fake coin.
        for (uint256 i = 1; i < coins.length; i++) {
            _coins[i] = coins[i];
        }
    }

    // Iterates over all coins.
    // If markets do not exist for coin, create them.
    // Unless _nextResolutionTime is zero; then do not create new markets.
    // If markets for coin exist and are ready to resolve, resolve them and create new markets.
    // Else, error.
    //
    // Assume that _roundIds has a dummy value at index 0, and is 1 indexed like the
    // coins array.
    function createAndResolveMarkets(uint80[] calldata _roundIds, uint256 _nextResolutionTime) public {
        require(msg.sender == linkNode, "Only link node can create markets");
        // If market creation was stopped then it can be started again.
        // If market creation wasn't stopped then you must wait for market end time to resolve.
        require(block.timestamp >= nextResolutionTime, "Must wait for market resolution");
        require(_roundIds.length == coins.length, "Must specify one roundId for each coin");

        uint256 _resolutionTime = nextResolutionTime;
        nextResolutionTime = _nextResolutionTime;

        // Start at 1 to skip the fake Coin in the 0 index
        for (uint256 i = 1; i < coins.length; i++) {
            createAndResolveMarketsForCoin(i, _resolutionTime, _roundIds[i]);
        }
    }

    function createAndResolveMarketsForCoin(
        uint256 _coinIndex,
        uint256 _resolutionTime,
        uint80 _roundId
    ) internal {
        Coin memory _coin = coins[_coinIndex];
        (uint256 _fullPrice, uint256 _newPrice) = getPrice(_coin, _roundId, _resolutionTime);

        // resolve markets
        if (_coin.currentMarkets[uint256(MarketType.PriceUpDown)] != 0) {
            resolvePriceUpDownMarket(_coin, _newPrice, _fullPrice);
        }

        // update price only AFTER resolution
        coins[_coinIndex].price = _newPrice;

        // link node sets nextResolutionTime to zero to signify "do not create markets after resolution"
        if (nextResolutionTime == 0) {
            return;
        }

        // create markets
        coins[_coinIndex].currentMarkets[uint256(MarketType.PriceUpDown)] = createPriceUpDownMarket(
            _coinIndex,
            linkNode,
            _newPrice
        );
    }

    function resolvePriceUpDownMarket(
        Coin memory _coin,
        uint256 _newPrice,
        uint256 _fullPrice
    ) internal {
        uint256 _marketId = _coin.currentMarkets[uint256(MarketType.PriceUpDown)];

        OwnedERC20 _winner;
        if (_newPrice > _coin.price) {
            _winner = markets[_marketId].shareTokens[uint256(PriceUpDownOutcome.Above)];
        } else {
            _winner = markets[_marketId].shareTokens[uint256(PriceUpDownOutcome.NotAbove)];
        }

        markets[_marketId].winner = _winner;
        marketDetails[_marketId].resolutionPrice = _fullPrice;
        emit MarketResolved(_marketId, address(_winner));
    }

    function createPriceUpDownMarket(
        uint256 _coinIndex,
        address _creator,
        uint256 _newPrice
    ) internal returns (uint256 _id) {
        string[] memory _outcomes = new string[](2);
        _outcomes[uint256(PriceUpDownOutcome.Above)] = "Above";
        _outcomes[uint256(PriceUpDownOutcome.NotAbove)] = "Not Above";

        uint256 _nextResolutionTime = nextResolutionTime;
        _id = markets.length;
        markets.push(makeMarket(_creator, _outcomes, _outcomes, _nextResolutionTime, evenOdds(false, 2)));
        marketDetails[_id] = MarketDetails(MarketType.PriceUpDown, _coinIndex, _newPrice, 0);
        emit MarketCreated(_id, _creator, _nextResolutionTime, MarketType.PriceUpDown, _coinIndex, _newPrice);
    }

    // Returns the price based on a few factors.
    // If _roundId is zero then it returns the latest price.
    // Else, it returns the price for that round,
    //       but errors if that isn't the first round after the resolution time.
    // The price is then altered to match the desired precision.
    function getPrice(
        Coin memory _coin,
        uint80 _roundId,
        uint256 _resolutionTime
    ) internal view returns (uint256 _fullPrice, uint256 _truncatedPrice) {
        if (_roundId == 0) {
            (, int256 _rawPrice, , , ) = _coin.priceFeed.latestRoundData();
            require(_rawPrice >= 0, "Price from feed is negative");
            _fullPrice = uint256(_rawPrice);
        } else {
            (, int256 _rawPrice, , uint256 updatedAt, ) = _coin.priceFeed.getRoundData(_roundId);
            require(_rawPrice >= 0, "Price from feed is negative");
            require(updatedAt >= _resolutionTime, "Price hasn't been updated yet");

            // if resolution time is zero then market creation was stopped, so the previous round doesn't matter
            if (_resolutionTime != 0) {
                (, , , uint256 _previousRoundTime, ) = _coin.priceFeed.getRoundData(previousRound(_roundId));
                require(_previousRoundTime < _resolutionTime, "Must use first round after resolution time");
            }

            _fullPrice = uint256(_rawPrice);
        }

        // The precision is how many decimals the price has. Zero is dollars, 2 includes cents, 3 is tenths of a cent, etc.
        // Our resolution rules want a certain precision. Like BTC is to the dollar and MATIC is to the cent.
        // If somehow the decimals are larger than the desired precision then add zeroes to the end to meet the precision.
        // This does not change the resolution outcome but does guard against decimals() changing and therefore altering the basis.

        uint8 _precision = _coin.priceFeed.decimals(); // probably constant but that isn't guaranteed, so query each time
        if (_precision > _coin.imprecision) {
            uint8 _truncate = _precision - _coin.imprecision;
            _truncatedPrice = _fullPrice / (10**_truncate);
        } else if (_precision < _coin.imprecision) {
            uint8 _greaten = _coin.imprecision - _precision;
            _truncatedPrice = _fullPrice * (10**_greaten);
        } else {
            _truncatedPrice = _fullPrice;
        }

        // Round up because that cleanly fits Above/Not-Above.
        if (_truncatedPrice != _fullPrice) {
            _truncatedPrice += 1;
        }
    }

    function makeCoin(
        string memory _name,
        AggregatorV3Interface _priceFeed,
        uint8 _imprecision
    ) internal pure returns (Coin memory _coin) {
        uint256[1] memory _currentMarkets = [uint256(0)];
        _coin = Coin(_name, _priceFeed, 0, _imprecision, _currentMarkets);
    }

    function resolveMarket(uint256) public pure override {
        require(false, "Use createAndResolveMarkets");
    }

    // The roundId is the encoding of two parts: the phase and the phase-specific round id.
    // To find the previous roundId:
    // 1. extract the phase and phase-specific round (I call these _phaseId and _roundId)
    // 2. decrement the phase-specific round
    // 3. re-encode the phase and phase-specific round.
    uint256 private constant PHASE_OFFSET = 64;

    function previousRound(uint80 _fullRoundId) internal pure returns (uint80) {
        uint256 _phaseId = uint256(uint16(_fullRoundId >> PHASE_OFFSET));
        uint64 _roundId = uint64(_fullRoundId) - 1;
        return uint80((_phaseId << PHASE_OFFSET) | _roundId);
    }
}

contracts/libraries/FakePriceFeed.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "../utils/AggregatorV3Interface.sol";

contract FakePriceFeed is AggregatorV3Interface {
    uint8 decimals_;
    string description_;
    uint256 version_;

    struct Round {
        uint80 roundId;
        int256 answer;
        uint256 startedAt;
        uint256 updatedAt;
        uint80 answeredInRound;
    }
    mapping(uint80 => Round) rounds;
    uint80 latestRoundId;

    constructor(
        uint8 _decimals,
        string memory _description,
        uint256 _version
    ) {
        decimals_ = _decimals;
        description_ = _description;
        version_ = _version;
    }

    function decimals() external view override returns (uint8) {
        return decimals_;
    }

    function description() external view override returns (string memory) {
        return description_;
    }

    function version() external view override returns (uint256) {
        return version_;
    }

    // getRoundData and latestRoundData should both raise "No data present"
    // if they do not have data to report, instead of returning unset values
    // which could be misinterpreted as actual reported values.
    function getRoundData(uint80 roundId_)
        public
        view
        override
        returns (
            uint80 _roundId,
            int256 _answer,
            uint256 _startedAt,
            uint256 _updatedAt,
            uint80 _answeredInRound
        )
    {
        Round memory _round = rounds[roundId_];
        _roundId = _round.roundId;
        _answer = _round.answer;
        _startedAt = _round.startedAt;
        _updatedAt = _round.updatedAt;
        _answeredInRound = _round.answeredInRound;
    }

    function latestRoundData()
        public
        view
        override
        returns (
            uint80 _roundId,
            int256 _answer,
            uint256 _startedAt,
            uint256 _updatedAt,
            uint80 _answeredInRound
        )
    {
        return getRoundData(latestRoundId);
    }

    function addRound(
        uint80 _roundId,
        int256 _answer,
        uint256 _startedAt,
        uint256 _updatedAt,
        uint80 _answeredInRound
    ) external {
        rounds[_roundId] = Round(_roundId, _answer, _startedAt, _updatedAt, _answeredInRound);
        latestRoundId = _roundId;
    }
}

contracts/stablecoin/indexCDS.sol.content:
pragma solidity ^0.8.4; 

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

//import "../ERC20/IERC20.sol";

import "../turbo/AMMFactory.sol"; 
import "../turbo/OwnedShareToken.sol"; 
import "hardhat/console.sol";

contract IndexCDS is ERC721, ReentrancyGuard{

	//For each lockID
 	struct Index{
 		uint256 value;
 		uint256[] amounts; 
 		address[] tokens; 
 		
 	}

 	struct Index_info{
 		uint256[] token_amounts;
 		uint256[] marketIds; 
 		uint256[] outcomes; 
 		uint256 lockId; 
 	}

 	struct Variable{
 		uint256 amount;
 		uint256 marketId;
 		uint256 outcome;
 	}

 	uint256 public totalNumMints;
 	uint256 num_outcomes = 2; //TODO this is only for binary outcomes 
 	mapping(uint256=> Index_info) index_infos; //lockid to indiexinfo
 	mapping(uint256 => Index) indexes; 
 	mapping(address=>uint256[]) address_to_id;

 	constructor()
        ERC721("IndexCDS", "iCDS"){

        }
  
 	//TODO Call to amm factory
 	function getPrice(address token) internal view returns(uint256){
 		return 1; 
 	}

 	//TODO Returns the maximum return of this nft including and excluding longCDS tokens
 	function getAPR(address holder) public view returns(uint256){
 		return 1; 
 	}

 	// function curValue(address holder) public view returns(uint256){
 	// 	uint256 lockID = address_to_id[holder]; 
 	// 	address[] memory tokens_ = indexes[lockID].tokens; 
 	// 	uint256[] memory amounts = indexes[lockID].amounts; 

 	// 	uint256 num_tokens = tokens_.length;
 	// 	uint256 price; 
 	// 	uint256 value;
 	// //	uint256[] memory prices; 
 	// 	for (uint256 i=0; i< num_tokens; i++){ 
 	// 		price = getPrice(tokens_[i]); 
 	// 		value = value + (price * amounts[i]); 
 	// 	}


 	// }

 	//Mints nft for current price of each cds 
 	//Token addresses are for CDS(sharetokens in marketfactory) tokens 
 	function mintIndex(address recipient,  bool from_recipient, 
 					   uint256[] memory prices,
 					   uint256[] memory amounts, //this is token amount, not collateral
 					   address[] memory tokens_addresses 
 					   ) internal returns(uint256){
 		uint256 lockID = ++totalNumMints; 

 		uint256 num_tokens = prices.length;
 		uint256 value; 
 		for (uint256 i=0; i< num_tokens; i++){ 
 			value = value + (prices[i] * amounts[i]);

 			if (from_recipient){
 				OwnedERC20(tokens_addresses[i]).transferFrom(recipient, address(this), amounts[i]);
 			}//else each sharetokens are already owned by this contract 

 		}

 		Index memory index = Index(value,amounts, tokens_addresses); 
 		indexes[lockID] = index; 
 		address_to_id[recipient].push(lockID); 

		_safeMint(recipient, lockID); 

		return lockID; 


 	}

 	
 	function redeemIndex(address recipient, uint256 lockID, bool from_recipient) internal {
 		
 		Index memory index = indexes[lockID]; 
 		uint256 num_tokens = index.amounts.length;

 		if (from_recipient){
	 		for (uint256 i=0; i< num_tokens; i++){
	 			SafeERC20.safeTransfer(IERC20(index.tokens[i]), recipient, index.amounts[i]); 
	 		}
 		}

 		_burn(lockID); 
 		delete indexes[lockID]; 


 } 	



 	function getVariable(
 		uint256[] memory marketIds, 
 		uint256[] memory outcomes, 
 		uint256[] memory amounts) internal returns(Variable[] memory){

 		Variable[] memory variables = new Variable[](marketIds.length);
 		for (uint256 i=0; i< marketIds.length; i++){
 			variables[i] = Variable(amounts[i], marketIds[i], outcomes[i]); 			
 		}
 		return variables; 
 	}

 	//Public View Functions
 	function getUserLockId(address user) public view returns(uint256[] memory){
 		return address_to_id[user];
 	}

 	// function getUserIndexInfo(address user) public view returns(Index_info memory){
 	// 	uint256[] memory lockId = address_to_id[user]; 
 	// 	return index_infos[lockId];
 	// }

 	// function getUserCDSBalance() public view returns(uint256[] memory){
 	// 	uint256[] memory lockIds = address_to_id[user]; //nfts that this user has 


 	// 	Index memory index = indexes[lockId]; 
 	// 	address[] memory tokens = index.tokens; 
 	// 	uint256[] memory balance; 
 	// 	for (i=0; i< tokens.length; i++){
 	// 		balance[i] = tokens[i].balanceOf(address(this)); 

 	// 	}
 	// 	return balance; 
 	// }

 	function getUserTotalBalance(address user) public view returns(Index_info[] memory){
 		uint256[] memory lockIds = address_to_id[user];
 		Index_info[] memory user_infos = new Index_info[](lockIds.length);
 		for (uint256 i=0; i<lockIds.length; i++){
 			user_infos[i] = index_infos[lockIds[i]]; 
 		}

 		return user_infos; 

 	}



 	//State Changing Functions


 	/* This is the function called by contract calls, it first transfers the DS
 	from the msg.sender to this contract, and this contract will buy all the sharetokens
 	in behalf. It will then mint the nft and give it back to msg.sender
 	does the minting nft as well.
 	User gives collateral to this contract, this contract buys sharetokens + mints nft and gives back
 	*/
 	function buyBulk(address recipient,  
 		address marketFactoryAddress, 
 		address ammFactoryAddress, 
 		uint256[] memory marketIds,
 		uint256[] memory outcomes, 
 		uint256[] memory amounts) external returns(uint256){

 		uint256 num_buys = marketIds.length; 
 		AMMFactory amm = AMMFactory(ammFactoryAddress); 
 		AbstractMarketFactoryV3 marketFactory = AbstractMarketFactoryV3(marketFactoryAddress);
 		IERC20 collateral = marketFactory.collateral(); //DS

 		uint256[] memory prices = new uint256[](num_buys); 
 		uint256[] memory token_amounts = new uint256[](num_buys); 
 		address[] memory outcometokens = new address[](num_buys); 

 		Variable[] memory variable = getVariable(marketIds, outcomes, amounts);
 		//Iterate over all chosen markets 
 		for (uint256 i=0; i<num_buys; i++){
 			//Should transfer collateral all at once to save gas 
 			//This allows this contract to now have the sharetokens for each outcome 

 			token_amounts[i] = executeBuy(marketFactory,amm,
 			 collateral, msg.sender, variable[i]); 

 			outcometokens[i] = getOutcomeToken(marketFactory,variable[i]);
 			prices[i] = getPrice(outcometokens[i]); //TODO find how to calculate prices 

 		}

 		uint256 lockId = mintIndex(msg.sender,false, prices, token_amounts, outcometokens  );
 		index_infos[lockId] = Index_info(token_amounts, marketIds, outcomes, lockId);
 		
 		return lockId;
 	}

 	function getOutcomeToken(AbstractMarketFactoryV3 marketFactory,
 		Variable memory variable ) internal returns(address){
 		AbstractMarketFactoryV3.Market memory _market =  marketFactory.getMarket(variable.marketId); 

 		return address(_market.shareTokens[variable.outcome]); 
 	}

 	function executeBuy(AbstractMarketFactoryV3 marketFactory, 
 		AMMFactory amm, 
 		IERC20 collateral, 
 		address recipient, 
 		Variable memory variable) internal returns(uint256){


 		collateral.transferFrom(recipient, address(this), variable.amount); 
 		collateral.approve(address(amm), variable.amount); 

 		return amm.buy(marketFactory, variable.marketId,
 			variable.outcome,variable.amount, 0);
 	}


 	//User gives id of its NFT to this contract, it then burns the NFT and sell the underlying tokens, 
 	//and gives the collateral back to the user. For now it will sell all tokens and give the collateral
 	//for all tokens back. Users will have to rebuy if they want to just get rid of one token 
 	function sellBulk(address recipient, 
 		uint256 lockID, 
 		address marketFactoryAddress, 
 		address ammFactoryAddress) external {

 		AMMFactory amm = AMMFactory(ammFactoryAddress); 
 		AbstractMarketFactoryV3 marketFactory = AbstractMarketFactoryV3(marketFactoryAddress);
 		IERC20 collateral = marketFactory.collateral(); 

 		Index_info memory info = index_infos[lockID]; 

 		uint256[] memory shareTokensIn = new uint256[](num_outcomes); 
 		uint256 total_collateral_out = 0; 
 		uint256 num_sells = info.marketIds.length; 


 		for (uint256 i=0; i< num_sells; i++){

 			for(uint256 j=0; j<num_outcomes; j++){
 				//outcome[i] is 0 or 1 (if it is binary)
 				//0 if outcome[i] != j, info.token_amounts[i] otherwise
 				//bool isZero = (info.outcomes[i] != j);
 				//shareTokensIn[j] = isZero ? 0: info.token_amounts[i];
 				shareTokensIn[j] = info.token_amounts[i];
 			}
 			//need to approve amm for the sharetokens; 
 			sell_approve(marketFactory, amm, info.marketIds[i], info.outcomes[i], shareTokensIn); 

 			total_collateral_out = total_collateral_out + amm.sellForCollateral(
 				marketFactory, 
 				info.marketIds[i],
 				info.outcomes[i], 
 				shareTokensIn, 
 				0);
 			//Now this contract holds the collateral, so should give it back to msg.sender
 		}

 		redeemIndex( recipient,  lockID,  false); 
 		collateral.transfer(msg.sender, total_collateral_out ); 



 	}

 	function sell_approve(AbstractMarketFactoryV3 marketFactory, 
 		AMMFactory amm ,
 		uint256 marketId, 
 		uint256 outcome, 
 		uint256[] memory shareTokensIn ) private {
 		require(shareTokensIn[outcome] > 0); 
 		 AbstractMarketFactoryV3.Market memory _market = marketFactory.getMarket(marketId);
 		 _market.shareTokens[outcome].approve(address(amm), shareTokensIn[outcome]); 
 	}


}

