solmate/src/tokens/ERC721.sol.content:
// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity >=0.8.0;

/// @notice Modern, minimalist, and gas efficient ERC-721 implementation.
/// @author Solmate (https://github.com/transmissions11/solmate/blob/main/src/tokens/ERC721.sol)
abstract contract ERC721 {
    /*//////////////////////////////////////////////////////////////
                                 EVENTS
    //////////////////////////////////////////////////////////////*/

    event Transfer(address indexed from, address indexed to, uint256 indexed id);

    event Approval(address indexed owner, address indexed spender, uint256 indexed id);

    event ApprovalForAll(address indexed owner, address indexed operator, bool approved);

    /*//////////////////////////////////////////////////////////////
                         METADATA STORAGE/LOGIC
    //////////////////////////////////////////////////////////////*/

    string public name;

    string public symbol;

    function tokenURI(uint256 id) public view virtual returns (string memory);

    /*//////////////////////////////////////////////////////////////
                      ERC721 BALANCE/OWNER STORAGE
    //////////////////////////////////////////////////////////////*/

    mapping(uint256 => address) internal _ownerOf;

    mapping(address => uint256) internal _balanceOf;

    function ownerOf(uint256 id) public view virtual returns (address owner) {
        require((owner = _ownerOf[id]) != address(0), "NOT_MINTED");
    }

    function balanceOf(address owner) public view virtual returns (uint256) {
        require(owner != address(0), "ZERO_ADDRESS");

        return _balanceOf[owner];
    }

    /*//////////////////////////////////////////////////////////////
                         ERC721 APPROVAL STORAGE
    //////////////////////////////////////////////////////////////*/

    mapping(uint256 => address) public getApproved;

    mapping(address => mapping(address => bool)) public isApprovedForAll;

    /*//////////////////////////////////////////////////////////////
                               CONSTRUCTOR
    //////////////////////////////////////////////////////////////*/

    constructor(string memory _name, string memory _symbol) {
        name = _name;
        symbol = _symbol;
    }

    /*//////////////////////////////////////////////////////////////
                              ERC721 LOGIC
    //////////////////////////////////////////////////////////////*/

    function approve(address spender, uint256 id) public virtual {
        address owner = _ownerOf[id];

        require(msg.sender == owner || isApprovedForAll[owner][msg.sender], "NOT_AUTHORIZED");

        getApproved[id] = spender;

        emit Approval(owner, spender, id);
    }

    function setApprovalForAll(address operator, bool approved) public virtual {
        isApprovedForAll[msg.sender][operator] = approved;

        emit ApprovalForAll(msg.sender, operator, approved);
    }

    function transferFrom(
        address from,
        address to,
        uint256 id
    ) public virtual {
        require(from == _ownerOf[id], "WRONG_FROM");

        require(to != address(0), "INVALID_RECIPIENT");

        require(
            msg.sender == from || isApprovedForAll[from][msg.sender] || msg.sender == getApproved[id],
            "NOT_AUTHORIZED"
        );

        // Underflow of the sender's balance is impossible because we check for
        // ownership above and the recipient's balance can't realistically overflow.
        unchecked {
            _balanceOf[from]--;

            _balanceOf[to]++;
        }

        _ownerOf[id] = to;

        delete getApproved[id];

        emit Transfer(from, to, id);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 id
    ) public virtual {
        transferFrom(from, to, id);

        require(
            to.code.length == 0 ||
                ERC721TokenReceiver(to).onERC721Received(msg.sender, from, id, "") ==
                ERC721TokenReceiver.onERC721Received.selector,
            "UNSAFE_RECIPIENT"
        );
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 id,
        bytes calldata data
    ) public virtual {
        transferFrom(from, to, id);

        require(
            to.code.length == 0 ||
                ERC721TokenReceiver(to).onERC721Received(msg.sender, from, id, data) ==
                ERC721TokenReceiver.onERC721Received.selector,
            "UNSAFE_RECIPIENT"
        );
    }

    /*//////////////////////////////////////////////////////////////
                              ERC165 LOGIC
    //////////////////////////////////////////////////////////////*/

    function supportsInterface(bytes4 interfaceId) public view virtual returns (bool) {
        return
            interfaceId == 0x01ffc9a7 || // ERC165 Interface ID for ERC165
            interfaceId == 0x80ac58cd || // ERC165 Interface ID for ERC721
            interfaceId == 0x5b5e139f; // ERC165 Interface ID for ERC721Metadata
    }

    /*//////////////////////////////////////////////////////////////
                        INTERNAL MINT/BURN LOGIC
    //////////////////////////////////////////////////////////////*/

    function _mint(address to, uint256 id) internal virtual {
        require(to != address(0), "INVALID_RECIPIENT");

        require(_ownerOf[id] == address(0), "ALREADY_MINTED");

        // Counter overflow is incredibly unrealistic.
        unchecked {
            _balanceOf[to]++;
        }

        _ownerOf[id] = to;

        emit Transfer(address(0), to, id);
    }

    function _burn(uint256 id) internal virtual {
        address owner = _ownerOf[id];

        require(owner != address(0), "NOT_MINTED");

        // Ownership check above ensures no underflow.
        unchecked {
            _balanceOf[owner]--;
        }

        delete _ownerOf[id];

        delete getApproved[id];

        emit Transfer(owner, address(0), id);
    }

    /*//////////////////////////////////////////////////////////////
                        INTERNAL SAFE MINT LOGIC
    //////////////////////////////////////////////////////////////*/

    function _safeMint(address to, uint256 id) internal virtual {
        _mint(to, id);

        require(
            to.code.length == 0 ||
                ERC721TokenReceiver(to).onERC721Received(msg.sender, address(0), id, "") ==
                ERC721TokenReceiver.onERC721Received.selector,
            "UNSAFE_RECIPIENT"
        );
    }

    function _safeMint(
        address to,
        uint256 id,
        bytes memory data
    ) internal virtual {
        _mint(to, id);

        require(
            to.code.length == 0 ||
                ERC721TokenReceiver(to).onERC721Received(msg.sender, address(0), id, data) ==
                ERC721TokenReceiver.onERC721Received.selector,
            "UNSAFE_RECIPIENT"
        );
    }
}

/// @notice A generic interface for a contract which properly accepts ERC721 tokens.
/// @author Solmate (https://github.com/transmissions11/solmate/blob/main/src/tokens/ERC721.sol)
abstract contract ERC721TokenReceiver {
    function onERC721Received(
        address,
        address,
        uint256,
        bytes calldata
    ) external virtual returns (bytes4) {
        return ERC721TokenReceiver.onERC721Received.selector;
    }
}

contracts/vaults/vault_tranche/splitter..content:
pragma solidity ^0.8.4;


import {Auth} from "../auth/Auth.sol";
import {ERC4626} from "../mixins/ERC4626.sol";

import {SafeCastLib} from "../utils/SafeCastLib.sol";
import {SafeTransferLib} from "../utils/SafeTransferLib.sol";
import {FixedPointMathLib} from "../utils/FixedPointMathLib.sol";

import {ERC20} from "../tokens/ERC20.sol";
import {Instrument} from "../instrument.sol";
import {tVault} from "./tVault.sol"; 
import "@openzeppelin/contracts/utils/math/Math.sol";




/// @notice tokens for junior/senior tranches 
contract tToken is ERC20{

	modifier onlySplitter() {
    require(msg.sender == splitter, "!Splitter");
     _;
  }

  address splitter; 
  ERC20 asset; 

  /// @notice asset is the tVault  
  constructor(
      ERC20 _asset, 
      string memory _name,
      string memory _symbol, 
      address _splitter
  ) ERC20(_name, _symbol, _asset.decimals()) {
      asset = _asset;
      splitter = _splitter; 
  }

  function mint(address to, uint256 amount) external onlySplitter{
  	_mint(to, amount); 
  }

  function burn(address from, uint256 amount) external onlySplitter{
  	_burn(from, amount);
  }


}

/// @notice Accepts ERC20 and splits them into senior/junior tokens
/// Will hold the ERC20 token in this contract
/// Before maturity, redemption only allowed for a pair, 
/// After maturity, redemption allowed for individual tranche tokens, with the determined conversion rate
/// @dev new instance is generated for each vault
contract Splitter{
	using SafeCastLib for uint256; 
  using SafeTransferLib for ERC20;
  using FixedPointMathLib for uint256;

  tVault underlying; 
  tToken senior;
  tToken junior;  

  //weight is in PRICE_PRECISION 6, i.e 5e5 = 0.5
  uint256 junior_weight; 
  uint256 PRICE_PRECISION; 
  uint promised_return; 


  //Redemption Prices 
  uint256 s_r; 
  uint256 j_r; 

  constructor(
  	tVault _underlying //underlying vault token to split 
  	){
  	underlying = _underlying; 
  	senior = new tToken(_underlying, "senior", string(abi.encodePacked("se_", _underlying.symbol())), address(this));
  	junior = new tToken(_underlying, "junior", string(abi.encodePacked("ju_", _underlying.symbol())), address(this));

  	junior_weight = underlying.getJuniorWeight(); 
  	promised_return = underlying.getPromisedReturn(); 
  	PRICE_PRECISION = underlying.PRICE_PRECISION(); 
  }

	/// @notice accepts token_to_split and mints s,j tokens
	/// ex. 1 vault token-> 0.3 junior and 0.7 senior for weight of 0.3, 0.7
	function split(ERC20 token_to_split, uint256 amount) external returns(uint, uint) {
		require(token_to_split == underlying, "Wrong Splitter");

		token_to_split.safeTransferFrom(msg.sender, address(this), amount); 

		uint junior_token_mint_amount = (amount * junior_weight)/PRICE_PRECISION;  
		uint senior_token_mint_amount = amount - junior_token_mint_amount; 

		junior.mint(msg.sender, junior_token_mint_amount); 
		senior.mint(msg.sender, senior_token_mint_amount);

		return (junior_token_mint_amount, senior_token_mint_amount); 

	}

	/// @notice aceepts junior and senior token and gives back token_to_merge(tVault tokens)
	/// Function to call when redeeming before maturity
	/// @param token_to_merge is the valut token
	/// @param junior_amount is amount of junior tokens user want to redeem
	/// @dev senior amount is automiatically computed when given junior amount 
	function merge(ERC20 token_to_merge, uint256 junior_amount) external returns(uint){
		require(token_to_merge == underlying, "Wrong Splitter");
		uint senior_multiplier = (PRICE_PRECISION *(PRICE_PRECISION - junior_weight))/junior_weight; //ex 2.3e6
		uint senior_amount = (senior_multiplier * junior_amount)/PRICE_PRECISION; 
		require(senior.balanceOf(msg.sender) >= senior_amount, "Not enough senior tokens"); 

		junior.burn(msg.sender, junior_amount);
		senior.burn(msg.sender, senior_amount);
		underlying.transfer(msg.sender, junior_amount+senior_amount); 
		return junior_amount + senior_amount; 
	}


	/// @notice only can called after set maturity by tranche token holders
	function redeem_after_maturity(tToken _tToken, uint256 amount) external {
		require(underlying.isMatured(), "Vault not matured");
		require(address(_tToken) == address(senior) || address(_tToken) == address(junior), "Wrong Tranche Token");
		bool isSenior = (address(_tToken) == address(senior)) ? true : false; 
		uint redemption_price = isSenior? s_r: j_r; 
		uint token_redeem_amount = (redemption_price * amount)/PRICE_PRECISION; 

		_tToken.burn(msg.sender, amount); 
		underlying.transfer(msg.sender, token_redeem_amount); 
		
	}


	/// @notice calculate and store redemption Price for post maturity 
	/// @dev should be only called once right after tToken matures, as totalSupply changes when redeeming 
	function calcRedemptionPrice() private {
		uint promised_return = underlying.getPromisedReturn(); //in 1e6 decimals i.e 5000 is 0.05
		uint real_return = underlying.getCurrentRealReturn(); 
		uint _s_r = ((PRICE_PRECISION + promised_return)/(PRICE_PRECISION+real_return)) * PRICE_PRECISION; 
		uint max_s_r = (PRICE_PRECISION/(PRICE_PRECISION - junior_weight)) *PRICE_PRECISION; 
		
		s_r = min(_s_r, max_s_r);
		//total supply right after tVault matures 
		j_r = (underlying.totalSupply() - (senior.totalSupply() * s_r/PRICE_PRECISION))/(junior.totalSupply()); 

	}

	/// @dev need to return in list format to index it easily 
	/// 0 is always senior 
	function getTrancheTokens() public view returns(address[] memory){
 		address[] memory addresses = new address[](2);
 		addresses[0] =  address(senior); 
 		addresses[1] =  address(junior); 
		return addresses; 
	}


	function max(uint256 a, uint256 b) internal pure returns (uint256) {
    	return a >= b ? a : b;
	}

	function min(uint256 a, uint256 b) internal pure returns (uint256) {
    	return a <= b ? a : b;
	}
}

contracts/vaults/vault_tranche/stableswa.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8;

/*
Invariant - price of trade and amount of liquidity are determined by this equation

An^n sum(x_i) + D = ADn^n + D^(n + 1) / (n^n prod(x_i))

Topics
0. Newton's method x_(n + 1) = x_n - f(x_n) / f'(x_n)
1. Invariant
2. Swap
   - Calculate Y
   - Calculate D
3. Get virtual price
4. Add liquidity
   - Imbalance fee
5. Remove liquidity
6. Remove liquidity one token
   - Calculate withdraw one token
   - getYD
TODO: test?
*/

library Math {
    function abs(uint x, uint y) internal pure returns (uint) {
        return x >= y ? x - y : y - x;
    }
}

contract StableSwap {
  // Number of tokens
  uint private constant N = 2;
  // Amplification coefficient multiplied by N^(N - 1)
  // Higher value makes the curve more flat
  // Lower value makes the curve more like constant product AMM
  uint private constant A = 1000 * (N**(N - 1));
  // 0.03%
  uint private constant SWAP_FEE = 300;
  // Liquidity fee is derived from 2 constraints
  // 1. Fee is 0 for adding / removing liquidity that results in a balanced pool
  // 2. Swapping in a balanced pool is like adding and then removing liquidity
  //    from a balanced pool
  // swap fee = add liquidity fee + remove liquidity fee
  uint private constant LIQUIDITY_FEE = (SWAP_FEE * N) / (4 * (N - 1));
  uint private constant FEE_DENOMINATOR = 1e6;

  address[N] public tokens;
  // Normalize each token to 18 decimals
  // Example - DAI (18 decimals), USDC (6 decimals), USDT (6 decimals)
  //uint[N] private multipliers = [1, 1e12, 1e12];
  uint[N] private multipliers = [1, 1];

  uint[N] public balances;

  // 1 share = 1e18, 18 decimals
  uint private constant DECIMALS = 18;
  uint public totalSupply;

  address factory; 
  mapping(address => uint) public balanceOf;
  constructor(address token1, address token2){
    factory = msg.sender; 
    tokens[0] = token1;
    tokens[1] = token2; 
  }

  function _mint(address _to, uint _amount) private {
      balanceOf[_to] += _amount;
      totalSupply += _amount;
  }

  function _burn(address _from, uint _amount) private {
      balanceOf[_from] -= _amount;
      totalSupply -= _amount;
  }


  // Return precision-adjusted balances, adjusted to 18 decimals
  function _xp() private view returns (uint[N] memory xp) {
      for (uint i; i < N; ++i) {
          xp[i] = balances[i] * multipliers[i];
      }
  }

  /**
   * @notice Calculate D, sum of balances in a perfectly balanced pool
   * If balances of x_0, x_1, ... x_(n-1) then sum(x_i) = D
   * @param xp Precision-adjusted balances
   * @return D
   */
  function _getD(uint[N] memory xp) private pure returns (uint) {
    /*
    Newton's method to compute D
    -----------------------------
    f(D) = ADn^n + D^(n + 1) / (n^n prod(x_i)) - An^n sum(x_i) - D 
    f'(D) = An^n + (n + 1) D^n / (n^n prod(x_i)) - 1

                 (as + np)D_n
    D_(n+1) = -----------------------
              (a - 1)D_n + (n + 1)p

    a = An^n
    s = sum(x_i)
    p = (D_n)^(n + 1) / (n^n prod(x_i))
    */
    uint a = A * N; // An^n

    uint s; // x_0 + x_1 + ... + x_(n-1)
    for (uint i; i < N; ++i) {
        s += xp[i];
    }

    // Newton's method
    // Initial guess, d <= s
    uint d = s;
    uint d_prev;
    for (uint i; i < 255; ++i) {
        // p = D^(n + 1) / (n^n * x_0 * ... * x_(n-1))
        uint p = d;
        for (uint j; j < N; ++j) {
            p = (p * d) / (N * xp[j]);
        }
        d_prev = d;
        d = ((a * s + N * p) * d) / ((a - 1) * d + (N + 1) * p);

        if (Math.abs(d, d_prev) <= 1) {
            return d;
        }
    }
    revert("D didn't converge");
  }


 /**
  * @notice Calculate required dx to get dy
  * @param indexX is the index of the tokenIn
  * @dev only works in binary settins for now, indexX is 0 if senior, 1 if junior 
  * y is tokenOut, so dy should be subtracted, x is tokenIn, so should be added
  */

  function getDx(
      uint dy,
      uint indexX
      ) 
  public view returns(uint){
    uint[N] memory xp = _xp(); 
    uint xamount = xp[indexX]; 
    uint yamount = xp[1-indexX]; 

    uint new_yamount = yamount - dy; 

    uint new_xamount = _getY(1-indexX, indexX, new_yamount, xp); 
    uint dx = new_xamount - xamount; 
    return dx; 
  }

  /**
   * @notice Calculate the new balance of token j given the new balance of token i
   * @param i Index of token in
   * @param j Index of token out
   * @param x New balance of token i
   * @param xp Current precision-adjusted balances
   */
  function _getY(
    uint i,
    uint j,
    uint x,
    uint[N] memory xp
) private pure returns (uint) {
    /*
    Newton's method to compute y
    -----------------------------
    y = x_j

    f(y) = y^2 + y(b - D) - c

                y_n^2 + c
    y_(n+1) = --------------
               2y_n + b - D

    where
    s = sum(x_k), k != j
    p = prod(x_k), k != j
    b = s + D / (An^n)
    c = D^(n + 1) / (n^n * p * An^n)
    */
    uint a = A * N;
    uint d = _getD(xp);
    uint s;
    uint c = d;

    uint _x;
    for (uint k; k < N; ++k) {
        if (k == i) {
            _x = x;
        } else if (k == j) {
            continue;
        } else {
            _x = xp[k];
        }

        s += _x;
        c = (c * d) / (N * _x);
    }
    c = (c * d) / (N * a);
    uint b = s + d / a;

    // Newton's method
    uint y_prev;
    // Initial guess, y <= d
    uint y = d;
    for (uint _i; _i < 255; ++_i) {
        y_prev = y;
        y = (y * y + c) / (2 * y + b - d);
        if (Math.abs(y, y_prev) <= 1) {
            return y;
        }
    }
    revert("y didn't converge");
  }

  /**
   * @notice Calculate the new balance of token i given precision-adjusted
   * balances xp and liquidity d
   * @dev Equation is calculate y is same as _getY
   * @param i Index of token to calculate the new balance
   * @param xp Precision-adjusted balances
   * @param d Liquidity d
   * @return New balance of token i
   */
  function _getYD(
    uint i,
    uint[N] memory xp,
    uint d
) private pure returns (uint) {
    uint a = A * N;
    uint s;
    uint c = d;

    uint _x;
    for (uint k; k < N; ++k) {
        if (k != i) {
            _x = xp[k];
        } else {
            continue;
        }

        s += _x;
        c = (c * d) / (N * _x);
    }
    c = (c * d) / (N * a);
    uint b = s + d / a;

    // Newton's method
    uint y_prev;
    // Initial guess, y <= d
    uint y = d;
    for (uint _i; _i < 255; ++_i) {
        y_prev = y;
        y = (y * y + c) / (2 * y + b - d);
        if (Math.abs(y, y_prev) <= 1) {
            return y;
        }
    }
    revert("y didn't converge");
  }

  // Estimate value of 1 share
  // How many tokens is one share worth?
  function getVirtualPrice() external view returns (uint) {
      uint d = _getD(_xp());
      uint _totalSupply = totalSupply;
      if (_totalSupply > 0) {
          return (d * 10**DECIMALS) / _totalSupply;
      }
      return 0;
  }

  /**
   * @notice Swap dx amount of token i for token j
   * @param i Index of token in
   * @param j Index of token out
   * @param dx Token in amount
   * @param minDy Minimum token out
   */
  function swap(
      uint i,
      uint j,
      uint dx,
      uint minDy
  ) external returns (uint dy) {
    require(i != j, "i = j");

    IERC20(tokens[i]).transferFrom(msg.sender, address(this), dx);

    // Calculate dy
    uint[N] memory xp = _xp();
    uint x = xp[i] + dx * multipliers[i];

    uint y0 = xp[j];
    uint y1 = _getY(i, j, x, xp);
    // y0 must be >= y1, since x has increased
    // -1 to round down
    dy = (y0 - y1 - 1) / multipliers[j];

    // Subtract fee from dy
    uint fee = (dy * SWAP_FEE) / FEE_DENOMINATOR;
    dy -= fee;
    require(dy >= minDy, "dy < min");

    balances[i] += dx;
    balances[j] -= dy;

    IERC20(tokens[j]).transfer(msg.sender, dy);
}

function addLiquidity(uint[N] calldata amounts, uint minShares)
    external
    returns (uint shares)
{
    // calculate current liquidity d0
    uint _totalSupply = totalSupply;
    uint d0;
    uint[N] memory old_xs = _xp();
    if (_totalSupply > 0) {
        d0 = _getD(old_xs);
    }

    // Transfer tokens in
    uint[N] memory new_xs;
    for (uint i; i < N; ++i) {
        uint amount = amounts[i];
        if (amount > 0) {
            IERC20(tokens[i]).transferFrom(msg.sender, address(this), amount);
            new_xs[i] = old_xs[i] + amount * multipliers[i];
        } else {
            new_xs[i] = old_xs[i];
        }
    }

    // Calculate new liquidity d1
    uint d1 = _getD(new_xs);
    require(d1 > d0, "liquidity didn't increase");

    // Reccalcuate D accounting for fee on imbalance
    uint d2;
    if (_totalSupply > 0) {
        for (uint i; i < N; ++i) {
            // TODO: why old_xs[i] * d1 / d0? why not d1 / N?
            uint idealBalance = (old_xs[i] * d1) / d0;
            uint diff = Math.abs(new_xs[i], idealBalance);
            new_xs[i] -= (LIQUIDITY_FEE * diff) / FEE_DENOMINATOR;
        }

        d2 = _getD(new_xs);
    } else {
        d2 = d1;
    }

    // Update balances
    for (uint i; i < N; ++i) {
        balances[i] += amounts[i];
    }

    // Shares to mint = (d2 - d0) / d0 * total supply
    // d1 >= d2 >= d0
    if (_totalSupply > 0) {
        shares = ((d2 - d0) * _totalSupply) / d0;
    } else {
        shares = d2;
    }
    require(shares >= minShares, "shares < min");
    _mint(msg.sender, shares);
}

  function removeLiquidity(uint shares, uint[N] calldata minAmountsOut)
      external
      returns (uint[N] memory amountsOut)
  {
      uint _totalSupply = totalSupply;

      for (uint i; i < N; ++i) {
          uint amountOut = (balances[i] * shares) / _totalSupply;
          require(amountOut >= minAmountsOut[i], "out < min");

          balances[i] -= amountOut;
          amountsOut[i] = amountOut;

          IERC20(tokens[i]).transfer(msg.sender, amountOut);
      }

      _burn(msg.sender, shares);
  }

  /**
   * @notice Calculate amount of token i to receive for shares
   * @param shares Shares to burn
   * @param i Index of token to withdraw
   * @return dy Amount of token i to receive
   *         fee Fee for withdraw. Fee already included in dy
   */
  function _calcWithdrawOneToken(uint shares, uint i)
      private
      view
      returns (uint dy, uint fee)
  {
      uint _totalSupply = totalSupply;
      uint[N] memory xp = _xp();

      // Calculate d0 and d1
      uint d0 = _getD(xp);
      uint d1 = d0 - (d0 * shares) / _totalSupply;

      // Calculate reduction in y if D = d1
      uint y0 = _getYD(i, xp, d1);
      // d1 <= d0 so y must be <= xp[i]
      uint dy0 = (xp[i] - y0) / multipliers[i];

      // Calculate imbalance fee, update xp with fees
      uint dx;
      for (uint j; j < N; ++j) {
          if (j == i) {
              dx = (xp[j] * d1) / d0 - y0;
          } else {
              // d1 / d0 <= 1
              dx = xp[j] - (xp[j] * d1) / d0;
          }
          xp[j] -= (LIQUIDITY_FEE * dx) / FEE_DENOMINATOR;
      }

      // Recalculate y with xp including imbalance fees
      uint y1 = _getYD(i, xp, d1);
      // - 1 to round down
      dy = (xp[i] - y1 - 1) / multipliers[i];
      fee = dy0 - dy;
  }

  function calcWithdrawOneToken(uint shares, uint i)
      external
      view
      returns (uint dy, uint fee)
  {
      return _calcWithdrawOneToken(shares, i);
  }

  /**
   * @notice Withdraw liquidity in token i
   * @param shares Shares to burn
   * @param i Token to withdraw
   * @param minAmountOut Minimum amount of token i that must be withdrawn
   */
  function removeLiquidityOneToken(
      uint shares,
      uint i,
      uint minAmountOut
  ) external returns (uint amountOut) {
      (amountOut, ) = _calcWithdrawOneToken(shares, i);
      require(amountOut >= minAmountOut, "out < min");

      balances[i] -= amountOut;
      _burn(msg.sender, shares);

      IERC20(tokens[i]).transfer(msg.sender, amountOut);
  }





  function xp() public view returns(uint[N] memory){
      return _xp(); 
  }



}

interface IERC20 {
    function totalSupply() external view returns (uint);

    function balanceOf(address account) external view returns (uint);

    function transfer(address recipient, uint amount) external returns (bool);

    function allowance(address owner, address spender) external view returns (uint);

    function approve(address spender, uint amount) external returns (bool);

    function transferFrom(
        address sender,
        address recipient,
        uint amount
    ) external returns (bool);

    event Transfer(address indexed from, address indexed to, uint amount);
    event Approval(address indexed owner, address indexed spender, uint amount);
}

contracts/vaults/vault_tranche/testVault.content:
pragma solidity ^0.8.4;


import {Auth} from "../auth/Auth.sol";
import {ERC4626} from "../mixins/ERC4626.sol";

import {SafeCastLib} from "../utils/SafeCastLib.sol";
import {SafeTransferLib} from "../utils/SafeTransferLib.sol";
import {FixedPointMathLib} from "../utils/FixedPointMathLib.sol";

import {ERC20} from "../tokens/ERC20.sol";
import {Instrument} from "../instrument.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";


contract testVault is ERC4626, Auth{
	using SafeCastLib for uint256; 
    using SafeTransferLib for ERC20;
    using FixedPointMathLib for uint256;


    event InstrumentDeposit(address indexed user, Instrument indexed instrument, uint256 underlyingAmount);
    event InstrumentWithdrawal(address indexed user, Instrument indexed instrument, uint256 underlyingAmount);
    event InstrumentTrusted(address indexed user, Instrument indexed instrument);
    event InstrumentDistrusted(address indexed user, Instrument indexed instrument);

    /*///////////////////////////////////////////////////////////////
                                 CONSTANTS
    //////////////////////////////////////////////////////////////*/

    uint256 internal BASE_UNIT;
    uint256 totalInstrumentHoldings; //total holdings deposited into all Instruments 
    ERC20 public immutable UNDERLYING;

    mapping(Instrument => InstrumentData) public getInstrumentData;
    mapping(address => uint256) public  num_proposals;
    mapping(uint256=> Instrument) Instruments; //marketID-> Instrument 

    /// @param trusted Whether the Instrument is trusted.
    /// @param balance The amount of underlying tokens held in the Instrument.
    struct InstrumentData {
        // Used to determine if the Vault will operate on a Instrument.
        bool trusted;
        // Balance of the contract denominated in Underlying, 
        // used to determine profit and loss during harvests of the Instrument.  
        // represents the amount of debt the Instrument has incurred from this vault   
        uint248 balance;
        uint256 faceValue;
        uint256 marketId;
    	uint256 principal; //this is total available allowance
        uint256 expectedYield; // total interest paid over duration
        uint256 duration;
        string description;
        address Instrument_address;    
    }

    constructor(address _UNDERLYING)
        ERC4626(
            ERC20(_UNDERLYING),
            string(abi.encodePacked("debita ", ERC20(_UNDERLYING).name(), " Vault")),
            string(abi.encodePacked("db", ERC20(_UNDERLYING).symbol()))
        )  Auth(msg.sender)

    {
        UNDERLYING = ERC20(_UNDERLYING);
        BASE_UNIT = 10**ERC20(_UNDERLYING).decimals();

        //totalSupply = type(uint256).max;
    }


    /// @notice Harvest a trusted Instrument, records profit/loss 
    function harvest(Instrument instrument) external requiresAuth{
        require(getInstrumentData[instrument].trusted, "UNTRUSTED_Instrument");
    	uint256 oldTotalInstrumentHoldings = totalInstrumentHoldings; 
        uint256 balanceLastHarvest = getInstrumentData[instrument].balance;
        uint256 balanceThisHarvest = instrument.balanceOfUnderlying(address(instrument));
        
        getInstrumentData[instrument].balance = balanceThisHarvest.safeCastTo248();
        uint256 profit = balanceThisHarvest - balanceLastHarvest; 

        totalInstrumentHoldings = oldTotalInstrumentHoldings + profit; 

    }

    /// @notice Deposit a specific amount of float into a trusted Instrument.
   	/// Called when market is approved. 
   	/// Also has the role of granting a credit line to a credit-based Instrument like uncol.loans 
    function depositIntoInstrument(Instrument instrument, uint256 underlyingAmount) external requiresAuth{
    	require(getInstrumentData[instrument].trusted, "UNTRUSTED Instrument");
    	totalInstrumentHoldings += underlyingAmount; 

        getInstrumentData[instrument].balance += underlyingAmount.safeCastTo248();

        UNDERLYING.transfer(address(instrument), underlyingAmount);
        emit InstrumentDeposit(msg.sender, instrument, underlyingAmount);

    }

    /// @notice Withdraw a specific amount of underlying tokens from a Instrument.
    function withdrawFromInstrument(Instrument instrument, uint256 underlyingAmount) external requiresAuth{
    	require(getInstrumentData[instrument].trusted, "UNTRUSTED Instrument");
        getInstrumentData[instrument].balance -= underlyingAmount.safeCastTo248();
        totalInstrumentHoldings -= underlyingAmount;
        require(instrument.redeemUnderlying(underlyingAmount), "REDEEM_FAILED");
        emit InstrumentWithdrawal(msg.sender, instrument, underlyingAmount);

    }


    /// @notice Withdraws all underyling balance from the Instrument to the vault 
    function withdrawAllFromInstrument(Instrument instrument) external requiresAuth{
    	uint248 total_Instrument_balance = instrument.balanceOfUnderlying(address(instrument)).safeCastTo248();
    	uint248 current_balance =  getInstrumentData[instrument].balance;
    	getInstrumentData[instrument].balance -= Math.min(total_Instrument_balance, current_balance).safeCastTo248();
    	instrument.redeemUnderlying(total_Instrument_balance);

    }



    /// @notice Stores a Instrument as trusted when its approved
    function trustInstrument(Instrument instrument) external requiresAuth{
    	getInstrumentData[instrument].trusted = true;

    }

    /// @notice Stores a Instrument as untrusted
    function distrustInstrument(Instrument instrument) external requiresAuth{
    	getInstrumentData[instrument].trusted = false; 
    }


    /// @notice returns true if Instrument is approved
    function isTrusted(Instrument instrument) public view returns(bool){
    	return getInstrumentData[instrument].trusted; 
    }

    /// @notice Calculates the total amount of underlying tokens the Vault holds, excluding profit 
    function totalAssets() public view override returns(uint256){
    	return totalInstrumentHoldings + totalFloat();
    }

   	
    function totalFloat() public view returns (uint256) {
        return UNDERLYING.balanceOf(address(this));
    }

    function fetchInstrumentData(uint256 marketId) public view returns(InstrumentData memory){
        return getInstrumentData[Instruments[marketId]];
    }

    /// @notice add instrument proposal created by the Utilizer 
    /// @dev Instrument instance should be created before this is called 
    function addProposal(
        InstrumentData memory data
    ) external {
        require(data.principal > 0, "principal must be greater than 0");
        require(data.duration > 0, "duration must be greater than 0");
        require(data.faceValue > 0, "faceValue must be greater than 0");
        require(data.principal >= BASE_UNIT, "Needs to be in decimal format"); // should be collateral address, not DS. Can't be less than 1.0 X?
   

        num_proposals[msg.sender] ++; 
        getInstrumentData[Instrument(data.Instrument_address)] = (
        	InstrumentData(
        		false, 
                0, 
                data.faceValue, 
                data.marketId, 
                data.principal, 
                data.expectedYield, 
                data.duration, 
                data.description, 
                data.Instrument_address)
        	); 

        Instruments[data.marketId] = Instrument(data.Instrument_address);
    }
}

